insert into contabilidade.planocontadetalhe values (302, 2, '1.1.3.8.1.14.00.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE ALIENAÇÃO DE BENS MÓVEIS E IMÓVEIS', 'Registra os valores dos créditos a receber decorrentes da alienação de bens móveis ou imóveis.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (303, 2, '1.1.3.8.1.15.00.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE PAGAMENTO DE DESPESA DE TERCEIROS', 'Registra os recursos a receber provenientes de pagamentos de despesas de terceiros decorrentes de cláusulas contratuais, tais como: água, luz, telefone, instalações, transporte e aluguéis.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (304, 2, '1.1.3.8.1.16.00.00.00.00', 'CRÉDITOS A RECEBER POR ACERTO FINANCEIRO COM SERVIDORES E EX-SERVIDORES', 'Registra os valores dos créditos a receber decorrentes de acertos financeiros com servidores e ex-servidores do órgão, conforme disposto na lei 8.112/90.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (305, 2, '1.1.3.8.1.17.00.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE FOLHA DE PAGAMENTO', 'Registra os valores dos créditos a receber decorrentes de folha de pagamento.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (306, 2, '1.1.3.8.1.18.00.00.00.00', 'DIVIDENDOS PROPOSTOS A RECEBER', 'Registra os valores dos dividendos a que a empresa tenha direito, em função de participações em outras empresas, quando tais empresas já tenham registrado na demonstração de lucros ou prejuízos acumulados a parcela de Dividendos propostos a distribuir.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (307, 2, '1.1.3.8.1.20.00.00.00.00', 'REMUNERAÇÃO DE DEBÊNTURES A RECEBER', 'Registra os valores das remunerações de debêntures a receber.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (308, 2, '1.1.3.8.1.27.01.00.00.00', 'INSCRIÇÃO DE CERTIDÃO DE DECISÃO - TÍTULO EXECUTIVO DO TCE-RS', 'Registra os créditos de natureza não-tributária exigíveis a partir do recebimento, pela entidade, de certidão de decisão - título executivo, emitida pelo Tribunal de Contas do Estado com característica de liquidez e certeza, podendo ser, facultativamente inscrita como divida ativa não tributaria', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (309, 2, '1.1.3.8.1.28.00.00.00.00', 'CRÉDITOS A RECEBER DE ENTIDADES FEDERAIS', 'Registra os valores a receber a curto prazo, decorrentes de direitos junto a entidades federais.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (310, 2, '1.1.3.8.1.29.00.00.00.00', 'CRÉDITOS A RECEBER DE ENTIDADES ESTADUAIS, DISTRITAIS E MUNICIPAIS', 'Registra os valores a receber a curto prazo, decorrentes de direitos junto a estados, DF e municÍpios.', 1, true, 2, 'P');
