insert into contabilidade.planocontadetalhe values (312, 2, '1.1.3.8.1.99.00.00.00.00', 'OUTROS CRÉDITOS A RECEBER E VALORES DE  CURTO PRAZO', 'Registra os valores de outros créditos a receber, para os quais não tenham contas especificas neste subgrupo de contas.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (313, 2, '1.1.4.1.1.01.00.00.00.00', 'TÍTULOS PÚBLICOS', 'Registra os valores relativos aos titulos públicos, emitidos e em circulacao, de posse da entidade pública.', 1, true, 2, 'P');
