insert into contabilidade.planocontadetalhe values (316, 2, '1.1.4.1.1.09.01.00.00.00', 'TÍTULOS DE RESPONSABILIDADE DO TESOURO', 'Registra os investimentos realizados pelo RPPS em títulos de emissão do Tesouro Nacional, efetuados em conformidade com as Resoluções CMN nº 3.922/2010 e nº 4.392/2014.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (317, 2, '1.1.4.1.1.09.02.00.00.00', 'TÍTULOS DE RESPONSABILIDADE DO BANCO CENTRAL', 'Registra saldos remanescentes dos investimentos realizados pelo RPPS, em títulos de emissão do Banco Central do Brasil, considerando que este não emite mais títulos da Dívida Pública, conforme art. 34 da Lei Complementar nº 101/2000.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (318, 2, '1.1.4.1.1.09.03.00.00.00', 'DEPÓSITOS DE POUPANÇA', 'Registra os investimentos realizados pelo RPPS em poupança e seus rendimentos (juros e atualização monetária) posteriores, em conformidade com as Resoluções CMN nº 3.922/2010 e nº 4.392/2014.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (319, 2, '1.1.4.1.1.09.04.00.00.00', 'FUNDOS DE INVESTIMENTO EM RENDA FIXA OU REFERENCIADOS', 'Registra os investimentos realizados pelos RPPS em fundos de investimentos em renda fixa, efetuados em conformidade com a Resolução CMN nº 3.922/2010 (alterada pela Resolução CMN nº 4.392/2014), art. 7º, inciso IV.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (320, 2, '1.1.4.1.1.09.05.00.00.00', 'FUNDOS DE INVESTIMENTO EM RENDA FIXA - CRÉDITO PRIVADO', 'Registra os investimentos realizados pelos RPPS em fundos de investimentos em renda fixa - crédito privado, efetuados em conformidade com as Resoluções CMN nº 3.922/2010 e nº 4.392/2014.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (321, 2, '1.1.4.1.1.09.06.00.00.00', 'FUNDOS DE INVESTIMENTOS EM RENDA FIXA OU REFERENCIADOS - IMA/IDkA', 'Registra os investimentos realizados pelo RPPS em fundos de investimentos ou como referenciados em indicadores de desempenho de renda fixa, constituídos sob a forma de condomínio aberto e cuja política de investimento assuma o compromisso de buscar o retorno de um dos subíndices do Índice de Mercado Anbima (IMA) ou do Índice de Duração Constante Anbima (IDkA), efetuados em conformidade com as Resoluções CMN nº 3.922/2010 e nº 4.392/2014.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (322, 2, '1.1.4.1.1.09.07.00.00.00', 'FUNDOS DE INVESTIMENTOS EM DIREITOS CREDITÓRIOS ', 'Registra os investimentos realizados pelo RPPS em fundos de investimentos em direito creditório, efetuados em conformidade com as Resoluções CMN nº 3.922/2010 e nº 4.392/2014.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (323, 2, '1.1.4.1.1.09.08.00.00.00', 'FUNDOS DE INVESTIMENTOS EM TÍTULOS DO TESOURO', 'Registra os investimentos realizados pelo RPPS, em fundos de investimentos em títulos do tesouro, efetuados em conformidade com as Resoluções CMN nº 3.922/2010 e nº 4.392/2014.', 1, true, 2, 'F');
