insert into contabilidade.planocontadetalhe values (436, 2, '1.2.1.2.1.04.10.00.00.00', 'CRÉDITOS A RECEBER POR PAGAMENTOS INDEVIDOS DE BENEFÍCIOS PREVIDENCIÁRIOS  ', 'Registra as responsabilidades imputadas a órgãos pagadores por pagamentos indevidos de benefícios previdenciários, pós-morte do segurado (créditos reconhecidos sem necessidade de instauração de tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (437, 2, '1.2.1.2.1.04.99.00.00.00', 'OUTROS CRÉDITOS ADMINISTRATIVOS', 'Registra o crédito do ente correspondente a outros créditos administrativos que não se enquadram em rubricas específicas existentes.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (438, 2, '1.2.1.2.1.05.01.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE PAGAMENTOS INDEVIDOS -TOMADA DE CONTAS ESPECIAL', 'Registra as responsabilidades imputadas a gestores e outros responsáveis e co-responsáveis por pagamentos efetuados indevidamente, inclusive em casos de super faturamento. (quando for instaurada tomada de conta especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (439, 2, '1.2.1.2.1.05.02.00.00.00', 'CRÉDITOS A RECEBER DE SERVIDORES NÃO RECOLHIDOS NO PRAZO LEGAL  - TOMADA DE CONTAS ESPECIAL', 'Registra os saldos dos valores entregues a funcionários ou servidores, não devolvidos dentro dos prazos estabelecidos em normas legais ou determimanação administrativa, ou seja, quando o produto a arrecadar não tenha sido recolhido no prazo legal (quando for instaurada tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (440, 2, '1.2.1.2.1.05.03.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE DESFALQUES OU DESVIOS  - TOMADA DE CONTAS ESPECIAL', 'Registra os desfalques, desvios de bens e valores do ente público ou de entidades públicas, apuradas em processos por ações administrativas e policial ou julgamento judicial (quando for instaurada tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (441, 2, '1.2.1.2.1.05.04.00.00.00', 'CRÉDITOS A RECEBER POR DÉBITOS DE TERCEIROS EM PRESTAÇÃO DE SERVICOS - TOMADA DE CONTAS ESPECIAL', 'Registra os valores relativos às responsabilidades por irregularidades em prestação de serviços por terceiros, cujos processos foram apurados, com imputação da responsabilidade. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (465, 2, '1.2.1.2.1.98.05.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE ALIENAÇÃO DE BENS MÓVEIS E IMÓVEIS', 'Registra os valores, realizáveis após 12 (doze) meses da data das demonstrações, dos créditos a receber decorrentes da alienação de bens móveis e imóveis.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (442, 2, '1.2.1.2.1.05.05.00.00.00', 'CRÉDITOS A RECEBER DE RESPONSÁVEIS POR DANOS OU PERDAS - TOMADA DE CONTAS ESPECIAL', 'Registra as responsabilidades apuradas em inquéritos administrativo ou policial contra servidores ou terceiros, por danos e perdas causados à fazenda nacional sendo apurados com imputação da responsabilidade (quando for instaurada tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (443, 2, '1.2.1.2.1.05.06.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE PAGAMENTOS SEM RESPALDO ORCAMENTÁRIO  - TOMADA DE CONTAS ESPECIAL', 'Registra as despesas realizadas sem existência de crédito que as comporte, resultando em pagamento sem prévio empenho ou ainda com classificação imprópria (diferente daquela prevista no orçamento), apuradas, com imputação da responsabilidade (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (444, 2, '1.2.1.2.1.05.07.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE CRÉDITOS ANTECIPADOS  - TOMADA DE CONTAS ESPECIAL', 'Registra os valores pagos sobre dotações decorrentes de créditos antecipados não realizados até o fechamento do exercício, apurados, com imputação da responsabilidade (quando for instaurada tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (445, 2, '1.2.1.2.1.05.08.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE FALTA OU IRREGULARIDADE DE COMPROVAÇÃO  - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores correspondentes à falta de documentação comprobatória da execução da despesa, inclusive as apresentações fora do prazo legal, bem como a inadimplência na comprovação de instrumentos de transferência ou ainda irregularidade na documentação apurados, com imputação da responsabilidade (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (446, 2, '1.2.1.2.1.05.09.00.00.00', 'CRÉDITOS  A RECEBER DECORRENTES DE FALTA DE LICITAÇÃO  - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores relativos a bens e serviços adquiridos sem a necessária licitação específica, em processos motivados, por determinações administrativas e ou inquéritos, apurados, com imputação da responsabilidade. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (447, 2, '1.2.1.2.1.05.10.00.00.00', 'MULTAS E JUROS A RECEBER DE SERVIDORES RESPONSABILIZADOS - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores referentes a multas e juros pagos pelo órgão, porém de responsabilidade de servidores, que foram apurados, com imputação da responsabilidade (quando for instaurada tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (448, 2, '1.2.1.2.1.05.11.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE FALTA DE RECOLHIMENTO DE OBRIGAÇÕES PECUNIARIAS  - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores correspondentes à falta de recolhimento ou pagamento de obrigações pecuniárias, tais como: parte de obrigações patronais do empregador, valores descontados de terceiros e não recolhidos, não pagamento de obrigações para com o ente (quando for instaurada tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (449, 2, '1.2.1.2.1.05.12.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE FRAUDES EM BENEFÍCIOS  - TOMADA DE CONTAS ESPECIAL ', 'Registra os débitos relativos a fraudes em benefícios da previdência social, com a responsabilidade devidamente apurada e imputada a servidores ou terceiros (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (450, 2, '1.2.1.2.1.05.13.00.00.00', 'CRÉDITOS  A RECEBER DECORRENTES DE ADIANTAMENTOS CONCEDIDOS IRREGULARMENTE - TOMADA DE CONTAS ESPECIAL ', 'Registra as responsabilidades imputadas a gestores e outros responsáveis e co-responsáveis por adiantamentos concedidos em desacordo com a legislação pertinente, tais como a fornecedores e empregados (quando for instaurada tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (451, 2, '1.2.1.2.1.05.14.00.00.00', 'CRÉDITOS A RECEBER POR USO INDEVIDO DE COTA - TOMADA DE CONTAS ESPECIAL ', 'Registra os debitos (apurados e em apuração) de parlamentares em razão de recebimento indevido de valor relacionado a cota destinada a custear gastos vinculados ao exercício de atividade parlamentar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (452, 2, '1.2.1.3.1.03.00.00.00.00', 'APLICAÇÕES EM SEGMENTO DE IMÓVEIS', 'Registra os investimentos de longo prazo realizados em segmento de imóveis, realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (453, 2, '1.2.1.2.1.05.15.00.00.00', 'CRÉDITOS A RECEBER ORIUNDOS DE USO OU ALUGUEL - TOMADA DE CONTAS ESPECIAL ', 'Registra os débitos (apurados e em apuração) referentes a débitos de parlamentares conhecidos após seu afastamento do mandado e que não puderam ser descontados em folha de pagamento. exemplos: remanescente de conta de energia elétrica, gás, taxa de limpeza pública e aluguéis de outros bens imóveis pertencente ao órgão.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (485, 2, '1.2.1.9.1.01.00.00.00.00', 'PRÊMIOS DE SEGUROS A APROPRIAR', 'Registra os valores pagos a titulo de prêmios de seguro a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (454, 2, '1.2.1.2.1.05.99.00.00.00', 'OUTRAS CRÉDITOS A RECEBER APURADOS EM TOMADA DE CONTAS ESPECIAL ', 'Registra as responsabilidades diversas cujas classificações não estejam definidas em contas próprias ou para as quais não hajam contas específicas. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (455, 2, '1.2.1.2.1.06.01.00.00.00', 'DEPÓSITOS E CAUÇÕES RELATIVOS A CONTRATOS OU CONVENÇÕES', 'Registra os valores relativos a depósitos e cauções, efetuados por vinculações a contratos ou convenções, para garantias de operações específicas, realizáveis no longo prazo.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (456, 2, '1.2.1.2.1.06.02.00.00.00', 'DEPÓSITOS JUDICIAIS', 'Registra os valores a receber decorrentes de depósitos efetuados por determinação judicial, realizáveis no longo prazo.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (457, 2, '1.2.1.2.1.06.03.00.00.00', 'DEPÓSITOS PARA INTERPOSIÇÃO DE RECURSOS', 'Registra os valores relativos aos depósitos vinculados decorrentes da interposição de recursos na esfera judicial ou administrativa.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (458, 2, '1.2.1.2.1.06.04.00.00.00', 'DEPÓSITOS ESPECIAIS', 'Registra os valores relativos a outros depósitos em contas bancárias, vinculados a obrigações contratuais, convenções, acordos e ajustes.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (459, 2, '1.2.1.2.1.06.05.00.00.00', 'VALORES APREENDIDOS POR DECISÃO JUDICIAL', 'Registra os valores apreendidos por decisão judicial através de penhora ou arresto, realizáveis no longo prazo.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (460, 2, '1.2.1.2.1.06.07.00.00.00', 'DEPÓSITOS COMPULSÓRIOS', 'Registra os valores relativos a depósitos compulsórios, de acordo com a legislação em vigor.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (461, 2, '1.2.1.2.1.06.99.00.00.00', 'OUTROS DEPÓSITOS RESTITUÍVEIS E VALORES VINCULADOS', 'Registra os valores relativos a outros depósitos restituíveis e valores vinculados.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (462, 2, '1.2.1.2.1.98.01.00.00.00', 'CRÉDITOS A RECEBER DE ENTIDADES FEDERAIS', 'Registra os valores realizáveis após 12 (doze) meses da data das demonstrações decorrentes de débito de entidades federais com o estado/município.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (463, 2, '1.2.1.2.1.98.03.00.00.00', 'CRÉDITOS ORIUNDOS DE CONCESSÕES DE DIREITOS DE USO E DE EXPLORAÇÃO DE BENS', 'Registra o valor dos créditos oriundos das concessões de direitos de uso e de exploração de bens do estado/município, para terceiros ou para outras entidades governamentais realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (464, 2, '1.2.1.2.1.98.04.00.00.00', 'CRÉDITOS A RECEBER DE DEVEDORES DE CONVÊNIOS', 'Registra os valores, realizáveis após 12 (doze) meses da data das demonstrações, oriundos de créditos de convênios.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (466, 2, '1.2.1.2.1.98.06.00.00.00', 'CRÉDITOS A RECEBER POR ACERTO FINANCEIRO COM SERVIDOR E EX-SERVIDOR', 'Registra os valores, realizáveis após 12 (doze) meses da data das demonstrações, dos créditos a receber decorrentes de acertos financeiros com servidores e ex-servidores do órgão.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (467, 2, '1.2.1.2.1.98.07.00.00.00', 'CRÉDITOS ADMINISTRATIVOS DECORRENTES DE AUTOS DE INFRAÇÃO', 'Registra o valor dos créditos administrativos oriundos de autos de infração e outros.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (468, 2, '1.2.1.2.1.98.08.00.00.00', 'INDENIZAÇÕES A RECEBER', 'Registra o valor de indenizações a receber realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (469, 2, '1.2.1.2.1.98.99.01.00.00', 'INSCRIÇÃO DE CERTIDÃO DE DECISÃO - TÍTULO EXECUTIVO DO TCE/RS', 'Registra os créditos exigíveis a partir do recebimento, pela entidade, de certidão de decisão - título executivo, emitida pelo Tribunal de Contas do Estado com característica de liquidez e certeza, podendo ser, facultativamente inscrita como divida ativa não tributaria', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (470, 2, '1.2.1.3.1.01.01.00.00.00', 'TÍTULOS PÚBLICOS - MERCADO ABERTO', 'Registra os valores, realizáveis após os 12 (doze) meses seguintes a data da publicação das demonstrações contábeis, de títulos públicos - mercado aberto.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (471, 2, '1.2.1.3.1.01.02.00.00.00', 'AÇÕES', 'Registra os valores, realizáveis após os 12 (doze) meses seguintes a data da publicação das demonstrações contábeis, de ações representativas das participações dos órgãos e entidades no capital das sociedades por ações havidas por aquisições, subscrições, bonificações e doações.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (472, 2, '1.2.1.3.1.01.03.00.00.00', 'FUNDO DE APLICAÇÕES EM COTAS - RENDA VARIÁVEL', 'Registra os valores, realizáveis  após os 12 (doze) meses seguintes a data da publicação das demonstrações contábeis, aplicados em fundos de aplicações em cotas - renda variável.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (473, 2, '1.2.1.3.1.01.99.00.00.00', 'OUTROS TÍTULOS E VALORES MOBILIÁRIOS', 'Registra os valores, realizáveis após os 12 (doze) meses seguintes a data da publicação das demonstrações contábeis, de outros títulos e valores mobiliários.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (474, 2, '1.2.1.3.1.02.01.00.00.00', 'APLICAÇÃO TEMPORÁRIA EM OURO', 'Registra as aplicações temporárias em ouro, realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (475, 2, '1.2.1.3.1.02.02.00.00.00', 'APLICAÇÃO TEMPORÁRIA EM OUTROS METAIS PRECIOSOS', 'Registra as aplicações temporárias em outros metais preciosos, realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (476, 2, '1.2.1.3.1.04.00.00.00.00', 'FUNDOS AVALIADOS A VALOR DE MERCADO', 'Compreende os investimentos de longo prazo realizados em fundos  que, devido à sua natureza, devem ser avaliados a valor de mercado.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (477, 2, '1.2.1.4.1.01.00.00.00.00', 'MERCADORIAS PARA REVENDA', 'Registra todos os produtos adquiridos de terceiros para revenda, que não sofrem nenhum processo de transformação na entidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (478, 2, '1.2.1.4.1.02.00.00.00.00', 'PRODUTOS E SERVIÇOS ACABADOS', 'Registra os produtos já terminados e oriundos da própria produção da entidade e disponíveis para venda, estocados na fabrica, ou em depósitos, ou em filiais, ou ainda com terceiros em consignação e os serviços acabados.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (479, 2, '1.2.1.4.1.03.00.00.00.00', 'PRODUTOS E SERVIÇOS EM ELABORAÇÃO', 'Registra os produtos e serviços em andamento que não serão concluídos até 12 (doze) meses para a data da publicação das demonstrações contábeis.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (480, 2, '1.2.1.4.1.04.00.00.00.00', 'MATÉRIAS-PRIMAS', 'Registra o estoque de matérias-primas que será utilizado no processo produtivo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (481, 2, '1.2.1.4.1.05.00.00.00.00', 'MATÉRIAIS EM TRÂNSITO', 'Registra o valor dos materiais em processo de transferência para outras unidades da entidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (482, 2, '1.2.1.4.1.06.00.00.00.00', 'ALMOXARIFADO', 'Registra o valor dos materiais destinados ao consumo interno da unidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (483, 2, '1.2.1.4.1.07.00.00.00.00', 'ADIANTAMENTOS A FORNECEDORES', 'Registra os adiantamentos efetuados pela entidade a fornecedores, vinculados a compras especificas de materiais que serão incorporados aos estoques quando de seu efetivo recebimento.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (487, 2, '1.2.1.9.1.03.00.00.00.00', 'ASSINATURAS E ANUIDADES A APROPRIAR', 'Registra os valores pagos a titulo de assinaturas e anuidades a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (488, 2, '1.2.1.9.1.04.00.00.00.00', 'ALUGUEIS PAGOS A APROPRIAR', 'Registra os valores pagos a titulo de aluguel a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (489, 2, '1.2.1.9.1.05.00.00.00.00', 'TRIBUTOS PAGOS A APROPRIAR', 'Registra os valores pagos a titulo de tributos a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (490, 2, '1.2.1.9.1.06.00.00.00.00', 'CONTRIBUIÇÕES CONFEDERATIVAS A APROPRIAR', 'Registra os valores pagos a titulo de contribuições confederativas a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (491, 2, '1.2.1.9.1.07.00.00.00.00', 'BENEFÍCIOS A APROPRIAR', 'Registra os valores pagos a titulo de beneficio a pessoal a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (492, 2, '1.2.1.9.1.99.00.00.00.00', 'DEMAIS VPD A APROPRIAR', 'Registra os demais valores pagos a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (493, 2, '1.2.2.1.1.01.01.00.00.00', 'PARTICIPAÇÕES EM SOCIEDADES CONTROLADAS', 'Registra as participações permanentes do ente em sociedades controladas, avaliadas por equivalência patrimonial.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (494, 2, '1.2.2.1.1.01.02.00.00.00', 'PARTICIPAÇÕES EM SOCIEDADES CONTROLADAS EM CONJUNTO', 'Registra as participações permanentes do ente em sociedades controladas em conjunto, avaliadas por equivalência patrimonial.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (495, 2, '1.2.2.1.1.01.03.00.00.00', 'PARTICIPAÇÕES EM SOCIEDADES COLIGADAS', 'Registra as participações permanentes do ente em sociedades coligadas, avaliadas por equivalência patrimonial.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (496, 2, '1.2.2.1.1.01.04.00.00.00', 'MAIS-VALIA SOBRE OS ATIVOS DAS INVESTIDAS', 'Registra a diferença entre o valor do percentual adquirido do valor justo dos ativos líquidos da investida e o valor do percentual adquirido do valor do patrimônio líquido da investida.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (497, 2, '1.2.2.1.1.01.05.00.00.00', 'ÁGIO SOBRE OS INVESTIMENTOS', 'Registra a diferença entre o valor pago pela participação adquirida e o valor do percentual adquirido do valor justo dos ativos líquidos da investida.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (498, 2, '1.2.2.1.1.01.06.00.00.00', 'ADIANTAMENTO PARA FUTURO AUMENTO DE CAPITAL', 'Registra os valores repassados pelo ente, destinados a serem utilizados para aumento de capital.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (500, 2, '1.2.2.1.1.01.08.00.00.00', 'PARTICIPAÇÃO EM FUNDOS', 'Registra as participações permanentes do ente em fundos, avaliadas por equivalência patrimonial.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (501, 2, '1.2.2.1.1.01.09.00.00.00', 'PARTICIPAÇÃO EM OUTRAS SOCIEDADES AVALIDAS POR EQUIVALÊNCIA PATRIMONIAL', 'Registra as participações permanentes do ente em outras sociedades, avaliadas por equivalência patrimonial.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (502, 2, '1.2.2.1.1.01.95.00.00.00', 'OUTRAS PARTICIPAÇÕES - MEP', 'Registra as participações permanentes da unidade em outras entidades em forma de ações ou cotas, avaliadas pelo método de equivalência patrimonial - Outras Participações.', 1, true, 2, 'P');
