insert into contabilidade.planocontadetalhe values (594, 2, '1.2.3.2.1.07.00.00.00.00', 'INSTALAÇÕES', 'Registra equipamentos, materiais e custo de implantação de instalações que, não obstante integradas aos edifícios, devem ser segregadas das obras civis, como, por exemplo, as instalações elétricas, hidráulicas, sanitárias, de vapor, de ar comprimido, frigoríficas, contra incêndio, de comunicações, de climatização, para combustíveis, gases, de antipoluição, para cozinha, dentre outros. E também aplicável a construção de trechos ferroviários.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (595, 2, '1.2.3.2.1.08.00.00.00.00', 'BENFEITORIAS EM PROPRIEDADE DE TERCEIROS', 'Registra construções em terrenos arrendados de terceiros e instalações e outras benfeitorias em prédios alugados. São considerados somente os gastos com construção e instalação que se incorporam ao imóvel e revertem ao proprietário do imóvel ao final da locação.', 1, true, 2, 'P');
