insert into contabilidade.planocontadetalhe values (932, 2, '3.3.2.3.1.03.00.00.00.00', 'AUDITORIA EXTERNA', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de auditoria externa.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (933, 2, '3.3.2.3.1.04.00.00.00.00', 'COMUNICAÇÃO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de comunicação, prestados por pessoa jurídica.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (934, 2, '3.3.2.3.1.05.00.00.00.00', 'PUBLICIDADE', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de publicidade.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (935, 2, '3.3.2.3.1.06.00.00.00.00', 'MANUTENÇÃO E CONSERVAÇÃO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de manutenção de bens móveis e imóveis.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (936, 2, '3.3.2.3.1.07.00.00.00.00', 'SERVIÇOS DE APOIO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços prestados por pessoa jurídica a título de apoio as atividades administrativas, técnicas e operacionais dos órgãos públicos, nos casos em que o contrato não especifique o quantitativo físico do pessoal a ser utilizado.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (937, 2, '3.3.2.3.1.08.00.00.00.00', 'SERVIÇOS DE ÁGUA E ESGOTO, ENERGIA ELÉTRICA, GÁS E OUTROS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de água e esgoto, energia elétrica, gás e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (939, 2, '3.3.2.3.1.11.00.00.00.00', 'SERVIÇOS RELACIONADOS À TECNOLOGIA DA INFORMAÇÃO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de processamento de dados prestados por empresas especializadas na área de informática; atualização e adaptação de programas de processamento de dados; suporte técnico, revisão, correção de problemas operacionais, análise p/acrescentar novas funções, hospedagem de sistemas e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (940, 2, '3.3.2.3.1.12.00.00.00.00', 'SERVIÇOS DE TRANSPORTE', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de transporte de servidores, trabalhadores de uma entidade ou de mercadorias e produtos diversos.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (941, 2, '3.3.2.3.1.13.00.00.00.00', 'ARMAZENAGEM', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de aluguel de galpões, silos e outros locais destinados a armazenagem de mercadorias e produtos. Inclui, ainda, as variações provenientes de garantia dos estoques armazenados e a utilização de serviço especializado na solução de gestão de estoque, incluindo gestão de materiais e transporte de materiais e equipamentos.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (942, 2, '3.3.2.3.1.14.00.00.00.00', 'ASSINATURAS DE PERIODICOS E ANUIDADES', 'Registra as variações patrimoniais diminutivas provenientes da utilização de assinaturas de TV (TV a cabo), jornais, inclusive diário oficial, revistas, recortes de publicações, software, podendo estar na forma de disquete, CD-ROM, boletins e outros que não se destinem a coleções ou bibliotecas.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (943, 2, '3.3.2.3.1.15.00.00.00.00', 'CAPATAZIA, ESTIVA E PESAGEM', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de movimentação e pesagem de cargas (mercadorias e produtos).', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (944, 2, '3.3.2.3.1.17.00.00.00.00', 'COMISSÕES E CORRETAGENS', 'Registra as variações patrimoniais diminutivas provenientes da apropriação de valor referente a comissões e corretagens decorrentes de serviços prestados por empresas de intermediação e representação comercial.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (945, 2, '3.3.2.3.1.18.00.00.00.00', 'CONDOMÍNIOS', 'Registra as variações patrimoniais diminutivas provenientes da apropriação das taxas condominiais a conta do proprietário, ou do locatário, conforme previsto no contrato de locação.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (946, 2, '3.3.2.3.1.19.00.00.00.00', 'CONFECÇÃO DE MATERIAL DE ACONDIC. E EMBALAGEM', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de confecção de materiais destinados a preservação, acomodação ou embalagem de produtos diversos, tais como: - bolsas - caixas - mochilas - sacolas e outros. Somente serão classificados como serviços, caso a unidade forneça a matéria-prima.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (947, 2, '3.3.2.3.1.20.00.00.00.00', 'CONFECÇÃO DE UNIFORMES, BANDEIRAS E FLÂMULAS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de confecção de: - bandeiras - brasões - estandartes - flâmulas - uniformes (inclusive as despesas relacionadas com auxílio fardamento descritos na lei nº  8.237/91) e outros. Somente serão classificados como serviços, caso a unidade forneça a matéria-prima.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (948, 2, '3.3.2.3.1.21.00.00.00.00', 'DIREITOS AUTORAIS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de direitos autorais sobre obras científicas, literárias ou em que a divulgação seja de interesse do governo.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (949, 2, '3.3.2.3.1.22.00.00.00.00', 'EXPOSIÇÕES, CONGRESSOS, CONFERÊNCIAS E OUTROS', 'Registra as variações patrimoniais diminutivas provenientes de serviços utilizados na instalação e manutenção de: conferencias - reuniões técnicas - congressos - exposições - feiras - festejos populares - festivais - e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (950, 2, '3.3.2.3.1.23.00.00.00.00', 'FESTIVIDADES E HOMENAGENS', 'Registra as variações patrimoniais diminutivas provenientes de serviços utilizados na organização de eventos, tais como: coquetéis - festas de congraçamento - recepções - e outras.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (951, 2, '3.3.2.3.1.45.00.00.00.00', 'SERVIÇOS FUNERÁRIOS', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de remoções, sepultamentos e transladações.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (952, 2, '3.3.2.3.1.24.00.00.00.00', 'HONORÁRIOS ADVOCATÍCIOS - ÔNUS DE SUCUMBÊNCIA', 'Registra as variações patrimoniais diminutivas provenientes da apropriação dos honorários advocatícios decorrentes do ônus da sucumbência, entendido este como o ônus que recai sobre a parte vencida numa ação, para pagamento dos honorários advocatícios a vencedora.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (953, 2, '3.3.2.3.1.25.00.00.00.00', 'HOSPEDAGENS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de hospedagens e alimentação de servidores e convidados do governo em viagens oficiais pagos diretamente a estabelecimentos hoteleiros (quando não houver pagamento de diárias).', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (954, 2, '3.3.2.3.1.26.00.00.00.00', 'PATROCÍNIO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de patrocínios a atividades ou eventos, de natureza cultural, esportiva, educacional, ambiental, cientifica, comunitária, promocional etc. Para influenciar favoravelmente o público e/ou atingir outros objetivos relacionados com a promoção de atos, obras, planos, ações, bens ou serviços. Não inclui o patrocínio de mídia, assim considerados aqueles em que o proponente e um veículo de comunicação ou um gestor de meios ou instalações que funcionem como veículo de mídia alternativa e aqueles em que um veículo de comunicação e o único ou o principal realizador da ação patrocinada, ainda que o proponente do projeto e provedor de conteúdo para a ação patrocinada não se enquadre na definição de veículo de comunicação ou meio de mídia alternativa.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (955, 2, '3.3.2.3.1.27.00.00.00.00', 'PRODUÇÕES JORNALÍSTICAS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de edição de jornais, revistas, noticiários e materiais jornalísticos para vídeos.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (956, 2, '3.3.2.3.1.28.00.00.00.00', 'PROMOÇÃO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços realizados por integrantes do SICOM ou por terceiros, que empreguem recursos de não-mídia, realizadas com objetivo de incentivar públicos de interesse a conhecerem ou comprarem produtos, serviços, marcas, conceitos ou políticas públicas.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (957, 2, '3.3.2.3.1.29.00.00.00.00', 'SEGUROS EM GERAL', 'Registra as variações patrimoniais diminutivas provenientes da apropriação de prêmios pagos por seguros de qualquer natureza, inclusive cobertura de danos causados a pessoas ou bens de terceiros, prêmios de seguros de bens do estado ou de terceiros, seguro obrigatório de veículos.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1042, 2, '3.5.1.1.2.13.00.00.00.00', 'CORRESPONDÊNCIA DE CRÉDITOS - SALDOS FINANCEIROS', 'Registra a apropriação da transferência de créditos internos entre UG de mesma gestão referentes a contas com saldos financeiras em rotinas contábeis orçamentárias.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (958, 2, '3.3.2.3.1.30.00.00.00.00', 'SELEÇÃO E TREINAMENTO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços prestados nas áreas de instrução e orientação profissional, recrutamento e seleção de pessoal (concurso público) e treinamento.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (959, 2, '3.3.2.3.1.99.00.00.00.00', 'OUTROS SERVIÇOS TERCEIROS - PJ', 'Registra as variações patrimoniais diminutivas provenientes das demais prestação de serviços por pessoa jurídica fornecida a entidade governamental.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (960, 2, '1.2.3.2.1.99.00.00.00.00', 'DEMAIS BENS IMÓVEIS', 'Compreende os demais bens imóveis não classificados anteriormente nesse plano de contas.', 1, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (961, 2, '3.3.2.3.1.31.00.00.00.00', 'SERV. MÉDICO-HOSPITALAR, ODONTOL. E LABORATORIAIS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços médico-hospitalares, odontológicos e laboratoriais, prestados por pessoas jurídicas sem vínculo empregatício, tais como: análises clínicas - cirurgias - consultas - ecografias - endoscopias - enfermagem - esterilização - exames de laboratório - raio x - tomografias - tratamento odontológico - ultrassonografias - e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (962, 2, '3.3.2.3.1.32.00.00.00.00', 'SERVIÇOS BANCÁRIOS', 'Registra as variações patrimoniais diminutivas provenientes da apropriação de comissões, tarifas e remunerações decorrentes de serviços prestados por bancos e outras instituições financeiras, bem como despesas com a guarda/custódia de valores (cofres de aluguel).', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (963, 2, '3.3.2.3.1.33.00.00.00.00', 'SERVIÇOS DE ANÁLISES E PESQUISA CIENTÍFICAS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de análises físico-químicas e pesquisas científicas, não relacionadas com apoio ao ensino, tais como: análises minerais - análises de solo - análises químicas - coleta de dados em experimentos - tratamento e destinação de resíduos e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (964, 2, '3.3.2.3.1.34.00.00.00.00', 'SERVIÇOS DE APOIO DO ENSINO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços com o objetivo de proporcionar o desenvolvimento e o aperfeiçoamento do ensino, em todos os níveis, inclusive pesquisas experiências e assemelhados.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (965, 2, '3.3.2.3.1.35.00.00.00.00', 'SERVIÇOS DE ASSISTÊNCIA SOCIAL', 'Registra as variações patrimoniais diminutivas provenientes de serviços de assistência social prestada a servidores, segurados carentes, abrigados, internados e a seus dependentes, tais como: - ajuda de custo supletiva - gêneros alimentícios - documentação - transporte e sepultamento.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (966, 2, '3.3.2.3.1.36.00.00.00.00', 'SERVIÇOS DE ÁUDIO VÍDEO E FOTO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de filmagens, gravações, revelações, ampliações e reproduções de sons e imagens, tais como: confecção de álbuns - confecção de crachás funcionais por firmas especializadas - emolduramento de fotografias - imagens de satélites - revelação de filmes - microfilmagem e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (967, 2, '3.3.2.3.1.38.00.00.00.00', 'SERVIÇOS DE CONSERV. E REBENEF. DE MERCADORIAS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços utilizados na conservação e rebeneficiamento de mercadorias.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (968, 2, '3.3.2.3.1.39.00.00.00.00', 'SERVIÇOS DE CONTROLE AMBIENTAL', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de controle ambiental.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (969, 2, '3.3.2.3.1.40.00.00.00.00', 'SERVIÇOS DE CÓPIAS E REPRODUÇÃO DE DOCUMENTOS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de copias xerográficas, inclusive a locação e a manutenção de equipamentos reprográficos, cópias e reproduções de documentos e publicações em geral, inclusive por meio magnético e digitalização.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (970, 2, '3.3.2.3.1.41.00.00.00.00', 'SERVIÇOS DE CRECHE E ASSISTÊNCIA PRÉ ESCOLAR', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços prestados por entidades de assistência social para atender os dependentes de servidores do órgão, habilitados a usufruírem desse benefício.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (971, 2, '3.3.2.3.1.42.00.00.00.00', 'SERVIÇOS DE INCINERAÇÃO/DESTRUIÇÃO DE MATERIAL', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviço de processo de incineração e destruição de materiais, bem como resíduos industriais.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (972, 2, '3.3.2.3.1.43.00.00.00.00', 'SERVIÇOS DE PRODUÇÃO INDUSTRIAL', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de transformação, beneficiamento e industrialização de matérias-primas que resultarão em um produto final. Somente serão classificados como serviços, caso a unidade forneça a matéria-prima.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (973, 2, '3.3.2.3.1.44.00.00.00.00', 'SERVIÇOS DE REABILITAÇÃO PROFISSIONAL', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de reabilitação profissional prestados por pessoa jurídica, com o objetivo de proporcionar aos incapacitados (parcial ou totalmente), os meios indicados para a reeducação e readaptação profissional e social, de modo que possam voltar a participar do mercado de trabalho. Ex.: contratação de empresa para ministrar curso profissionalizante.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (974, 2, '3.3.2.3.1.46.00.00.00.00', 'SERVIÇOS GRÁFICOS E EDITORIAIS', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de artes gráficas prestados por pessoa jurídica, bem como com serviços editoriais diversos. - Confecção de impressos em geral - encadernação de livros jornais e revistas - impressão de jornais - boletins - encartes - folders e assemelhados - plastificação de impressos - serviços de identificação de livros editados usando código de barras - serviços editoriais diversos.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (975, 2, '3.3.2.3.1.47.00.00.00.00', 'SERVIÇOS JUDICIÁRIOS', 'Registra as variações patrimoniais diminutivas proveniente da apropriação de custas processuais decorrentes de ações judiciais, diligencias (inclusive condução) salários e honorários dos avaliadores, peritos judiciais e oficiais de justiça e serviços de cartório.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (976, 2, '3.3.2.3.1.51.00.00.00.00', 'SERVIÇOS TÉCNICOS PROFISSIONAIS', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços prestados por empresas especializadas nas seguintes áreas: advocacia - arquitetura - contabilidade - economia - engenharia - estatística e outras. Exceto os serviços de consultoria que devem ser classificados em conta específica.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (998, 2, '3.4.1.2.1.01.00.00.00.00', 'JUROS DA DÍVIDA CONTRATADA NO EXTERIOR', 'Registra a variação patrimonial diminutiva com juros da dívida pública contratada no exterior com vistas a execução de obras, fornecimento de bens ou prestação de serviços, ou da celebração de contratos de financiamento.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (977, 2, '3.3.2.3.1.52.00.00.00.00', 'TAXA DE ADMINISTRAÇÃO', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de organização e administração prestados por pessoa jurídica, tais como: - taxa de administração paga ao instituto Euvaldo Lodi IEl e ao centro de integração empresa-escola - CIEE, decorrente dos serviços de seleção e recrutamento de estudantes, tendo como objetivo o ingresso ao mercado de trabalho e programas de estágio; - taxa de administração patrimonial das contribuições do Banco Central do Brasil, na qualidade de patrocinador, destinado ao custeio das aposentadorias e pensões concedidas com base na Lei 8.112/1990, e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (978, 2, '3.3.2.3.1.54.00.00.00.00', 'LOCAÇÃO DE MÃO-DE-OBRA', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços prestados por pessoas jurídicas para órgãos públicos, tais como limpeza e higiene, vigilância ostensiva e outros, nos casos em que o contrato especifique o quantitativo físico do pessoal a ser utilizado.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (979, 2, '3.3.2.3.1.55.00.00.00.00', 'ARRENDAMENTO MERCANTIL OPERACIONAL', 'REGISTRA AS VARIAÇÕES PATRIMONIAIS DIMINUTIVAS PROVENIENTES DE ARRENDAMENTO MERCANTIL OPERACIONAL', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (980, 2, '3.4.2.1.3.02.00.00.00.00', 'ENCARGOS DE MORA', 'Registra a variação patrimonial diminutiva decorrente de encargos a título de penalidade em virtude de atrasos e/ou não cumprimento dos prazos contratuais dos empréstimos e financiamentos internos contraídos com pessoas jurídicas de direito público ou privado.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (981, 2, '3.3.2.3.1.56.00.00.00.00', 'PASSAGENS E DESPESAS COM LOCOMOÇÃO', 'Registra as variações patrimoniais diminutivas proveniente da utilização de passagens (aéreas, terrestres, fluviais ou marítimas), taxas de embarque, seguros, fretamento, pedágios, locação ou uso de veículos para transporte de pessoas e suas respectivas bagagens, inclusive quando ocorrer em decorrência de mudanças de domicilio no interesse da administração.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (982, 2, '3.3.3.1.1.01.02.00.00.00', 'DEPRECIAÇÃO DE BENS IMÓVEIS', 'Registra a redução do valor dos bens imóveis pelo desgaste ou perda de utilidade por uso, ação da natureza ou obsolescência.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (983, 2, '3.3.3.2.1.01.00.00.00.00', 'AMORTIZAÇÃO DE IMOBILIZADO', 'Registra a redução do valor aplicado na aquisição de direitos, do ativo imobilizado, com existência ou exercício de duração limitada, ou cujo objeto seja bem de utilização por prazo legal ou contratualmente limitado.', 1, true, 2, 'N');
