insert into contabilidade.planocontadetalhe values (1045, 2, '3.5.1.2.2.02.99.00.00.00', 'OUTRAS TRANSFERÊNCIAS  NÃO FINANCEIRAS CONCEDIDAS - INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA', 'Registra o valor das demais transferências concedidas de bens ou valores independentes da execução orçamentária.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1046, 2, '3.5.1.3.2.01.01.00.00.00', 'RECURSOS PARA COBERTURA DE INSUFICIÊNCIAS FINANCEIRAS', 'Registra os recursos orçamentários efetivamente utilizados para a cobertura de insuficiências financeiras entre as receitas e despesas previdenciárias no exercício de referência.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1047, 2, '3.5.1.3.2.01.02.00.00.00', 'RECURSOS PARA FORMAÇÃO DE RESERVA', 'Registra os recursos transferidos pelo ente federativo ao RPPS para formação de reserva espontânea, para futuros pagamentos de benefícios do plano financeiro.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1048, 2, '3.5.1.3.2.01.99.00.00.00', 'OUTROS APORTES PARA O RPPS ', 'Registra o somatório de eventuais aportes de recursos no RPPS para outras necessidades financeiras que não a cobertura de insuficiências financeiras e formação de reservas.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1049, 2, '3.5.1.3.2.02.01.00.00.00', 'RECURSOS PARA COBERTURA DE DÉFICIT FINANCEIRO', 'Registra o somatório dos recursos necessários ao equilíbrio financeiro do plano previdenciário, ou seja, à cobertura de déficit entre as receitas e despesas previdenciárias, referentes ao exercício atual.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1050, 2, '3.5.1.3.2.02.02.00.00.00', 'RECURSOS PARA COBERTURA DE DÉFICIT ATUARIAL', 'Registra os recursos orçamentários alocados para cobertura de déficit atuarial no exercício de referência, desde que não definidos por lei como contribuições suplementares.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1051, 2, '3.5.1.3.2.02.99.00.00.00', 'OUTROS APORTES PARA O RPPS', 'Registra outros aportes financeiros para o RPPS com outras finalidades previdenciárias.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1052, 2, '3.5.2.1.4.02.00.00.00.00', 'PROGRAMA TROCA-TROCA', 'Registra o valor das variações patrimoniais diminutivas que o município tem que repassar para o Estado referente ao programa troca-troca.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1053, 2, '3.5.2.1.4.03.00.00.00.00', 'TRANSFERÊNCIAS PARA GRUPAMENTOS DE INCÊNDIO', 'Registra o valor das variações patrimoniais diminutivas que o município tem que repassar para os grupamentos de incêndio.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1054, 2, '3.6.2.3.1.01.00.00.00.00', 'PERDAS COM ALIENAÇÃO DE SOFTWARES', 'Registra a variação patrimonial diminutiva de perdas com alienação de softwares.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1055, 2, '3.5.2.1.4.04.00.00.00.00', 'RESTITUIÇÃO DE TRANSFERÊNCIAS E CONVÊNIOS RECEBIDOS DO ESTADO', 'Registra o valor das variações patrimoniais diminutivas com restituições de convênios e transferências recebidas do Estado, como, por exemplo: saldo de juros de aplicações financeiras; saldos de valores não utilizados.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1056, 2, '3.5.3.1.1.01.00.00.00.00', 'CONTRIBUIÇÕES', 'Registra o valor utilizado para transferências correntes e de capital aos entes da Federação e a entidades privadas sem fins lucrativos, exceto para os serviços essenciais de assistência social, médica e educacional;', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1057, 2, '3.5.3.1.1.02.00.00.00.00', 'AUXÍLIOS', 'Registra o valor utilizado para transferências de capital aos entes da Federação e a entidades privadas sem fins lucrativos;', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1058, 2, '3.6.3.3.1.05.00.00.00.00', 'PERDAS INVOLUNTÁRIAS COM MATERIAIS EM TRÂNSITO', 'Registra a variação patrimonial diminutiva com perdas involuntárias de materiais em trânsito.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1059, 2, '3.5.3.1.1.03.00.00.00.00', 'SUBVENÇÕES SOCIAIS', 'Registra o valor utilizado para transferências correntes às entidades públicas ou privadas sem fins lucrativos para os serviços essenciais de assistência social, médica e educacional;', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1060, 2, '3.5.7.2.1.01.00.00.00.00', 'DIÁRIAS ', 'Registra as variações patrimoniais diminutivas de diárias decorrentes de delegação ou descentralização aos consórcios públicos para execução de ações de responsabilidade exclusiva do delegante.', 1, true, 2, 'N');
