insert into contabilidade.planocontadetalhe values (1064, 2, '3.5.7.2.1.05.00.00.00.00', 'OUTROS SERVIÇOS DE TERCEIRO - PESSOA JURÍDICA', 'Registra as variações patrimoniais diminutivas de outros serviços de terceiro, pessoa jurídica, decorrentes de delegação ou descentralização aos consórcios públicos para execução de ações de responsabilidade exclusiva do delegante.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1065, 2, '3.5.7.2.1.06.00.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', 'Registra as variações patrimoniais diminutivas de indenizações e restituições decorrentes de delegação ou descentralização aos consórcios públicos para execução de ações de responsabilidade exclusiva do delegante.', 1, true, 2, 'N');
