insert into contabilidade.planocontadetalhe values (1067, 2, '3.6.1.1.1.01.01.00.00.00', 'REAVALIAÇÃO DE MÁQUINAS, APARELHOS, EQUIPAMENTOS E FERRAMENTAS', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para máquinas, ferramentas, aparelhos, equipamentos, acessórios etc, quando esse for inferior ao valor liquido contábil.', 1, true, 2, ' ');
insert into contabilidade.planocontadetalhe values (1068, 2, '3.6.1.1.1.01.02.00.00.00', 'REAVALIAÇÃO DE BENS DE INFORMÁTICA', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes paraequipamentos de processamento de dados e sistemas aplicativos, entre outros, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1069, 2, '3.6.1.1.1.01.03.00.00.00', 'REAVALIAÇÃO DE MÓVEIS E UTENSÍLIOS', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para mobiliário em geral e utensílios, entre outros, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1139, 2, '3.6.1.7.1.03.00.00.00.00', 'AJUSTE PARA PERDAS EM CRÉDITOS DE TRANSFERÊNCIAS A RECEBER', 'Registra a variação patrimonial diminutiva com ajuste para perdas em créditos de transferências a receber.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1070, 2, '3.6.1.1.1.01.04.00.00.00', 'REAVALIAÇÃO DE MATERIAIS CULTURAIS, EDUCACIONAIS E DE COMUNICAÇÃO', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para materiais bibliográficos, discotecas, filmotecas, de comunicação, entre outros, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1071, 2, '3.6.1.1.1.01.05.00.00.00', 'REAVALIAÇÃO DE VEÍCULOS', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para transportes aéreos, aquáticos e terrestres, entre outros, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1072, 2, '3.6.1.1.1.01.06.00.00.00', 'REAVALIAÇÃO DE PEÇAS E CONJUNTOS DE REPOSIÇÃO', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para peças e conjuntos de reposição destinados a substituição em máquinas e equipamentos, aeronaves e embarcações, bens de segurança e socorro, entre outros, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1073, 2, '3.6.1.1.1.01.07.00.00.00', 'REAVALIAÇÃO DE BENS MÓVEIS EM ANDAMENTO', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para bens móveis em andamento, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1074, 2, '3.6.1.1.1.01.08.00.00.00', 'REAVALIAÇÃO DE BENS MÓVEIS EM ALMOXARIFADO', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para bens permanentes a serem estocados em almoxarifado, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1075, 2, '3.6.1.1.1.01.09.00.00.00', 'REAVALIAÇÃO DE ARMAMENTOS', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para armas que constituem objetos de defesa ou preparativos de guerra, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (1076, 2, '3.6.1.1.1.01.10.00.00.00', 'REAVALIAÇÃO DE SEMOVENTES', 'Registra a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para animais destinados a produção, reprodução, engorda e aos serviços utilitários em geral, quando esse for inferior ao valor liquido contábil.', 1, true, 2, 'N');
