insert into contabilidade.planocontadetalhe values (1436, 2, '7.3.2.1.1.02.00.00.00.00', 'INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA TRIBUTÁRIA - TAXAS', 'Registra o valor dos créditos de origem tributária de taxas passíveis de serem inscritos em dívida ativa  pelo órgão competente.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1437, 2, '7.9.1.2.9.00.00.00.00.00', 'OUTRAS RESPONSABILIDADES DE TERCEIROS', 'Registra o valor de outras responsabilidades de terceiros, de natureza eventual, por títulos, valores e bens, para as quais não há contas especificas.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1438, 2, '7.4.1.1.1.00.00.00.00.00', 'DEMANDAS JUDICIAIS', 'Registra a estimativa do montante relativo a ações judiciais em andamento contra o ente federativo nas quais haja a probabilidade de que o ganho de causa venha a ser da outra parte. Ao fazer a estimativa, considerar os principais tipos de ação judicial, tais como:a) Demandas judiciais contra a atividade reguladora do Estado: Indexação e controles de preços; De ordem tributária e previdenciária; Outras demandas judiciais;b) Demandas judiciais contra empresas estatais dependentes;c) Demandas judiciais contra a administração direta e indireta: Privatizações, liquidação ou extinção de órgãos ou empresas; Reajustes salariais;d) Demandas trabalhistas contra o ente federativo;e) Outras demandas judiciais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1439, 2, '7.4.1.1.2.00.00.00.00.00', 'DÍVIDAS EM PROCESSO DE RECONHECIMENTO', 'Registra as Dívidas ainda não assumidas formalmente que apresentam probabilidade de serem incorporadas ao passivo devido, por exemplo, a decisões judiciais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1440, 2, '7.4.1.1.3.00.00.00.00.00', 'AVAIS E GARANTIAS CONCEDIDAS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser gasto pelo ente federativo para honrar fianças e avais concedidos em operações de crédito direta ou indiretamente pelo ente federativo a favor de outros entes federativos e entidades dos setores público e privado, inclusive com recursos de fundos de aval, a assunção de risco creditício em linhas de crédito, o seguro de crédito à exportação e outras garantias de natureza semelhante que representem compromisso de adimplência de obrigação financeira ou contratual.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1441, 2, '7.4.1.1.4.00.00.00.00.00', 'ASSUNÇÃO DE PASSIVOS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser gasto pelo ente federativo com o objetivo de proteger o cidadão ou sistemas importantes do Mercado contra inadimplências, falências, altos prejuízos, ou garantir a credibilidade desses sistemas frente a fatores agudos adversos. Ao fazer a estimativa, considerar os principais tipos de situações que podem advir:a) de órgãos da administração direta ou indireta;b) de entidades privadas;c) de Regime Próprio de Previdência Social dos Servidores Públicos;d) de Entes da Federação;e) do Regime Geral de Previdência Social;f) do Banco Central; eg) da assunção de outros passivos.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1442, 2, '7.4.1.1.5.00.00.00.00.00', 'ASSISTÊNCIAS DIVERSAS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo com o objetivo de fazer frente a calamidades públicas e que, por não serem recorrentes, não foram planejados. Recomenda-se que o ente federativo explicite o tipo de assistência emergencial que ele está prevendo, por exemplo, se é assistência contra seca, enchentes, catástrofes, epidemias etc.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1443, 2, '7.4.1.1.9.00.00.00.00.00', 'OUTROS PASSIVOS CONTINGENTES', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo para fazer frente a outros tipos de passivos contingentes.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1444, 2, '7.4.2.1.1.00.00.00.00.00', 'FRUSTRAÇÃO DE ARRECADAÇÃO', 'Registra a estimativa do montante de redução de arrecadação que apresenta probabilidade de vir a ocorrer no exercício, decorrente de, por exemplo, cenários macroeconômicos desfavoráveis não previstos na época da elaboração do Orçamento.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1445, 2, '7.4.2.1.2.00.00.00.00.00', 'RESTITUIÇÃO DE TRIBUTOS A MAIOR', 'Registra a estimativa do montante de devolução de tributos a maior que apresenta probabilidade de vir a ocorrer no exercício.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1446, 2, '7.4.2.1.3.00.00.00.00.00', 'DISCREPÂNCIA DE PROJEÇÕES', 'Registra a estimativa do montante de redução no valor dos ingressos ou de aumento no valor dos desembolsos que apresentam probabilidade de vir a ocorrer no exercício, decorrentes de evolução desfavorável de indicadores econômicos empregados na época da elaboração do Orçamento, tais como:a) Taxa de crescimento econômico;b) Taxa de inflação;c) Taxa de câmbio;d) Taxa de juros;e) Salário mínimo;f) Outros indicadores.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1447, 2, '7.4.2.1.4.00.00.00.00.00', 'OUTROS RISCOS FISCAIS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo para fazer frente a outros tipos de riscos fiscais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1448, 2, '7.5.2.0.0.00.00.00.00.00', 'PRESTAÇÃO DE CONTAS DE CONSÓRCIOS PÚBLICOS', 'Registra contas relacionadas ao registro do controle da prestação de contas dos Consórcios Públicos.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1449, 2, '7.5.3.1.0.00.00.00.00.00', 'VALORES TRANSFERIDOS POR CONTRATO DE RATEIO', 'Registra os valores repassados pelo ente para execução das despesas nos Consórcios Públicos, com base no Contrato de Rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1450, 2, '7.5.3.2.0.00.00.00.00.00', 'DESPESAS EXECUTADAS EM CONSÓRCIOS PÚBLICOS', 'Registra as despesas executadas em consórcios públicos atribuídas ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1451, 2, '7.5.3.3.0.00.00.00.00.00', 'EXECUÇÃO DE RP NÃO PROCESSADOS EM CONSÓRCIOS PÚBLICOS', 'Registra o somatório dos valores relativos à transferência, liquidação e pagamento das despesas empenhadas, não liquidadas no exercício financeiro de sua inscrição e não pagas, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1452, 2, '7.5.3.4.0.00.00.00.00.00', 'EXECUÇÃO DE RP PROCESSADOS EM CONSÓRCIOS PÚBLICOS', 'Registra o somatório dos valores relativos à transferência, liquidação e pagamento das despesas empenhadas, liquidadas no exercício financeiro de sua inscrição e não pagas, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1453, 2, '7.5.3.5.0.00.00.00.00.00', 'INSUFICIÊNCIA DE CAIXA DO CONSÓRCIO PÚBLICO', 'Registra a parcela total da insuficiência de caixa gerada no consórcio público atribuída ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1454, 2, '7.9.1.1.1.00.00.00.00.00', 'DEPÓSITOS JUDICIAIS', 'Registra os depósitos judiciais efetuados por terceiros em títulos, em conta vinculada etc, sob a responsabilidade da unidade, ou depositados por esta para terceiros.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1455, 2, '7.9.1.1.3.00.00.00.00.00', 'OUTRAS RESPONSABILIDADES COM TERCEIROS', 'REGISTRA RESPONSABILIDADES COM TERCEIROS, NÃO INDIVIDUALIZADAS NAS OUTRAS CONTAS DESSE GRUPO.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1456, 2, '7.9.1.2.1.00.00.00.00.00', 'CONTROLE DE ADIANTAMENTOS/SUPRIMENTOS DE FUNDOS CONCEDIDOS', 'Registra os valores concedidos a servidor responsável para atender despesas que não possam subordinar-se ao processo normal de aplicação, até a sua prestação de contas.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1457, 2, '7.9.1.2.2.00.00.00.00.00', 'ATIVOS DE PPP CONTABILIZADOS NO ENTE', 'Registra o valor contábil líquido dos ativos produzidos por meio de Parcerias Público Privadas e contabilizados no Ente (Poder Concedente). Deverá ser debitada para fins de incorporação do ativo e deverá ser creditada para fins de registro da depreciação.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1458, 2, '1.2.1.1.1.04.01.05.00.00', 'DÍVIDA ATIVA DO IPTU', 'Compreende os valores relativos a créditos inscritos de Dívida Ativa do iptu.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1459, 2, '7.9.2.0.0.00.00.00.00.00', 'DIVERSOS RESPONSÁVEIS EM APURAÇÃO', 'Registra as responsabilidades em fase de apuração decorrentes de atos praticados por gestores, servidores ou terceiros que possam resultarem prejuízos para a fazenda nacional.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1460, 2, '7.9.3.0.0.00.00.00.00.00', 'CONTROLE DE OPERAÇÕES DE CRÉDITO PARA FINS DA LRF', 'Compreende as contas relacionadas ao fluxo de operações de crédito no exercício financeiro para fins de preenchimento do Demonstrativo previsto no artigo 55, I, d da LRF. ', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (2477, 2, '2.1.1.4.1.05.00.00.00.00', 'FGTS', 'Registra os valores dos encargos sociais a recolher ao FGTS, gerados por pagamentos de salários.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (1461, 2, '7.9.4.0.0.00.00.00.00.00', 'CONTROLE DE OBRIGAÇÕES SEM AUTORIZAÇÃO ORÇAMENTÁRIA PARA FINS DA LRF', 'Compreende as contas relacionadas ao reconhecimento de obrigações patrimoniais realizadas sem autorização orçamentária, para fins de preenchimento do Relatório de Gestão Fiscal, previsto no art. 54 da LRF. ', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1462, 2, '1.2.1.1.1.04.01.06.00.00', 'DÍVIDA ATIVA DO ITBI', 'Compreende os valores relativos a créditos inscritos de Dívida Ativa do itbi.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1463, 2, '8.9.1.2.2.02.00.00.00.00', '(-) DEPRECIAÇÃO DOS ATIVOS CONTABILIZADOS NO ENTE', 'Registra o valor da depreciação dos ativos oriundos de contratos de Parcerias Público Privadas. Tal depreciação deve estar reconhecida no ativo imobilizado do ente (Poder Concedente), mas deve ser registrada em conta de controle a fim de obter a informação segregada dos ativos produzidos por meio de PPP.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (1464, 2, '1.0.0.0.0.00.00.00.00.00', 'ATIVO', 'Compreende os recursos controlados por uma entidade como consequência de eventos passados e dos quais se espera que fluam benefícios econômicos ou potencial de serviços futuros a unidade.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1465, 2, '1.1.0.0.0.00.00.00.00.00', 'ATIVO CIRCULANTE', 'Compreende os ativos que atendam a qualquer um dos seguintes critérios: sejam caixa ou equivalente de caixa; sejam realizáveis ou mantidos para venda ou consumo dentro do ciclo operacional da entidade; sejam mantidos primariamente para negociação; sejam realizáveis no curto prazo.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1466, 2, '1.1.1.0.0.00.00.00.00.00', 'CAIXA E EQUIVALENTES DE CAIXA', 'Compreende o somatório dos valores em caixa e em bancos, bem como equivalentes, que representam recursos com livre movimentação para aplicação nas operações da entidade e para os quais não haja restrições para uso imediato.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1467, 2, '1.1.1.1.0.00.00.00.00.00', 'CAIXA E EQUIVALENTES DE CAIXA EM MOEDA NACIONAL', 'Compreende o somatório dos valores em caixa e em bancos, bem como equivalentes, que representam recursos com livre movimentação para aplicação nas operações da entidade e para os quais não haja restrições para uso imediato.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1468, 2, '1.1.1.1.1.00.00.00.00.00', 'CAIXA E EQUIVALENTES DE CAIXA EM MOEDA NACIONAL - CONSOLIDAÇÃO', 'Compreende o somatório dos valores de caixa e equivalentes de caixa em moeda nacional. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1469, 2, '1.1.1.1.1.06.00.00.00.00', 'CONTA ÚNICA RPPS', 'Compreende os valores disponíveis na Conta Única do RPPS.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1470, 2, '1.1.1.1.1.19.00.00.00.00', 'BANCOS CONTA MOVIMENTO - DEMAIS CONTAS', 'Compreende o somatório das disponibilidades existentes em outras contas bancárias, que não sejam a Conta Única, agregadas pelos subitens identificativos dos bancos correspondentes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1471, 2, '1.1.1.1.1.50.00.00.00.00', 'APLICAÇÕES FINANCEIRAS DE LIQUIDEZ IMEDIATA', 'Compreende as aplicações financeiras de alta liquidez em moeda nacional, que são prontamente conversíveis em valores conhecidos de caixa e que estão sujeitas a um insignificante risco de mudança de valor.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1472, 2, '1.1.1.1.2.00.00.00.00.00', 'CAIXA E EQUIVALENTES DE CAIXA EM MOEDA NACIONAL - INTRA OFSS', 'Compreende o somatório dos valores de caixa e equivalentes de caixa em moeda nacional. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1474, 2, '1.1.1.2.1.00.00.00.00.00', 'CAIXA E EQUIVALENTES DE CAIXA EM MOEDA ESTRANGEIRA - CONSOLIDAÇÃO', 'Compreende o somatório dos valores de caixa e equivalentes de caixa em moeda estrangeira. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1475, 2, '1.1.2.0.0.00.00.00.00.00', 'CRÉDITOS A CURTO PRAZO', 'Compreende os valores a receber por fornecimento de bens, serviços, créditos tributários, dívida ativa, transferências e empréstimos e financiamentos concedidos realizáveis em até 12 meses da data das demonstrações.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1476, 2, '1.1.2.1.0.00.00.00.00.00', 'CRÉDITOS TRIBUTÁRIOS A RECEBER', 'Compreende os valores relativos a créditos a receber oriundos das variações patrimoniais aumentativas tributárias, realizáveis em até 12 meses da data das demonstrações. Os tributos são: impostos, taxas, contribuições de melhoria, contribuições e empréstimos compulsórios.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1477, 2, '1.1.2.1.1.00.00.00.00.00', 'CRÉDITOS TRIBUTÁRIOS A RECEBER - CONSOLIDAÇÃO', 'Compreende os valores relativos a créditos a receber oriundos das variações patrimoniais aumentativas tributárias, realizáveis em até 12 meses da data das demonstrações. Os tributos são: impostos, taxas, contribuições de melhoria, contribuições e empréstimos compulsórios. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1478, 2, '1.1.2.1.1.01.00.00.00.00', 'IMPOSTOS', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança da modalidade de tributo cuja obrigação tem por fato gerador uma situação independente de qualquer atividade estatal específica relativa ao contribuinte.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1479, 2, '1.1.2.1.1.02.00.00.00.00', 'TAXAS', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança da modalidade de tributo que tem como fato gerador o exercício regular do poder de polícia ou a utilização efetiva ou potencial de serviço público específico e divisível, prestado ao contribuinte ou posto a sua disposição.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1480, 2, '1.1.2.1.1.03.00.00.00.00', 'CONTRIBUIÇÕES DE MELHORIA', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança dos valores decorrentes da contribuição instituída para fazer face ao custo de obras públicas de que decorra valorização imobiliária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1481, 2, '1.1.2.1.1.04.00.00.00.00', 'DEMAIS CONTRIBUIÇÕES', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança das demais contribuições.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1482, 2, '1.1.2.1.1.05.00.00.00.00', 'CONTRIBUIÇÕES PREVIDENCIÁRIAS A RECEBER', 'Compreende os valores relativos aos créditos a receber, decorrentes das contribuições para o financiamento da previdência social.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1483, 2, '1.1.2.1.2.00.00.00.00.00', 'CRÉDITOS TRIBUTÁRIOS A RECEBER - INTRA OFSS', 'Compreende os valores relativos a créditos a receber oriundos das variações patrimoniais aumentativas tributárias, realizáveis em até 12 meses da data das demonstrações. Os tributos são: impostos, taxas, contribuições de melhoria, contribuições e empréstimos compulsórios. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1505, 2, '1.1.2.3.3.07.00.00.00.00', 'CRÉDITOS DECORRENTES DE TRANSFERÊNCIAS MULTIGOVERNAMENTAIS', 'Compreende os recursos a receber pelo estado ou município decorrentes de transferência multigovernamentais.', 1, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (1484, 2, '1.1.2.1.2.01.00.00.00.00', 'IMPOSTOS', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança da modalidade de tributo cuja obrigação tem por fato gerador uma situação independente de qualquer atividade estatal específica relativa ao contribuinte.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1485, 2, '1.1.2.1.2.02.00.00.00.00', 'TAXAS', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança da modalidade de tributo que tem como fato gerador o exercício regular do poder de polícia ou a utilização efetiva ou potencial de serviço público específico e divisível, prestado ao contribuinte ou posto a sua disposição.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1486, 2, '1.1.2.1.2.03.00.00.00.00', 'CONTRIBUIÇÕES DE MELHORIA', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança dos valores decorrentes da contribuição instituída para fazer face ao custo de obras públicas de que decorra valorização imobiliária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1487, 2, '1.1.2.1.2.04.00.00.00.00', 'DEMAIS CONTRIBUIÇÕES', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança das demais contribuições.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1488, 2, '1.1.2.1.2.05.00.00.00.00', 'CONTRIBUIÇÕES PREVIDENCIÁRIAS A RECEBER', 'Compreende os valores relativos aos créditos a receber, decorrentes das contribuições para o financiamento da previdência social.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1489, 2, '1.1.2.1.3.00.00.00.00.00', 'CRÉDITOS TRIBUTÁRIOS A RECEBER - INTER OFSS - UNIÃO', 'Compreende os valores relativos a créditos a receber oriundos das variações patrimoniais aumentativas tributárias realizáveis em até 12 meses da data das demonstrações. Os tributos são: impostos, taxas, contribuições de melhoria, contribuições e empréstimos compulsórios. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e a União.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1490, 2, '1.1.2.1.3.05.00.00.00.00', 'CONTRIBUIÇÕES PREVIDENCIÁRIAS A RECEBER', 'Compreende os valores relativos aos créditos a receber, decorrentes das contribuições para o financiamento da previdência social.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1491, 2, '1.1.2.4.1.02.00.00.00.00', 'FINANCIAMENTOS CONCEDIDOS', 'Compreende o somatório dos valores dos financiamentos concedidos por autorizações legais ou vinculações a contratos e acordos, exceto os concedidos com recursos do RPPS.', 1, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (1492, 2, '1.1.2.1.4.00.00.00.00.00', 'CRÉDITOS TRIBUTÁRIOS A RECEBER - INTER OFSS - ESTADO', 'Compreende os valores relativos a créditos a receber oriundos das variações patrimoniais aumentativas tributárias realizáveis em até 12 meses da data das demonstrações. Os tributos são: impostos, taxas, contribuições de melhoria, contribuições e empréstimos compulsórios. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1493, 2, '1.1.2.1.4.05.00.00.00.00', 'CONTRIBUIÇÕES PREVIDENCIÁRIAS A RECEBER', 'Compreende os valores relativos aos créditos a receber, decorrentes das contribuições para o financiamento da previdência social.', 1, false, 0, 'N');
