insert into contabilidade.planocontadetalhe values (1539, 2, '1.1.2.5.1.01.08.00.00.00', 'DÍVIDA ATIVA DO ITR', 'Compreende os valores relativos a créditos a receber, inscritos em dívida ativa, decorrentes da cobrança imposto sobre a propriedade territorial rural, devidos aos municípios ou ao Distrito Federal devido a convênios firmados com a União, conforme inciso III, parágrafo 4º do artigo 153 da Constituição Federal.', 1, false, 0, 'N');
