insert into contabilidade.planocontadetalhe values (1664, 2, '1.2.1.1.1.02.00.00.00.00', 'CLIENTES', 'Compreende os valores das faturas/duplicatas a receber decorrentes das vendas a prazo de mercadorias ou serviços que ocorrem no curso normal das operações da entidade (com vencimento após 12 (doze) meses da data das demonstrações), representando um direito a cobrar de seus clientes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1665, 2, '1.2.1.1.1.03.00.00.00.00', 'EMPRÉSTIMOS E FINANCIAMENTOS CONCEDIDOS', 'Compreende o somatório dos valores de empréstimos e financiamentos concedidos por autorizações legais ou vinculações a contratos e acordos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1666, 2, '1.2.1.1.1.04.00.00.00.00', 'DÍVIDA ATIVA TRIBUTÁRIA', 'Compreende os valores dos créditos de dívida ativa tributária inscritos, realizáveis após os doze meses seguintes a data de publicação dos demonstrativos contábeis.', 1, false, 0, 'N');
