insert into contabilidade.planocontadetalhe values (1676, 2, '1.2.1.2.0.00.00.00.00.00', 'DEMAIS CRÉDITOS E VALORES A LONGO PRAZO', 'Compreende os valores a receber por demais transações, com vencimento no longo prazo.', 1, false, 0, 'N');
