insert into contabilidade.planocontadetalhe values (1678, 2, '1.2.1.2.1.01.00.00.00.00', 'ADIANTAMENTOS CONCEDIDOS A PESSOAL E A TERCEIROS', 'Compreende as antecipações concedidas à pessoal, tais como antecipações de salários e ordenados, adiantamentos de 13º salário, adiantamentos de ferias e outros, alem da entrega de numerário a terceiros sem vinculação especifica ao fornecimento de bens ou serviços.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1679, 2, '1.2.1.2.1.02.00.00.00.00', 'TRIBUTOS A RECUPERAR/COMPENSAR', 'Compreende o somatório dos valores dos tributos a recuperar/compensar.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1680, 2, '1.2.1.2.1.04.00.00.00.00', 'CRÉDITOS POR DANOS AO PATRIMÔNIO PROVENIENTES DE CRÉDITOS ADMINISTRATIVOS', 'Compreende os valores realizáveis após os doze meses seguintes a data de publicação das demonstrações contábeis, provenientes de direitos oriundos de danos ao patrimônio, apurados em processos administrativos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1681, 2, '1.2.1.2.1.05.00.00.00.00', 'CRÉDITOS POR DANOS AO PATRIMÔNIO APURADOS EM TOMADA DE CONTAS ESPECIAL', 'Compreende os valores realizáveis após os doze meses seguintes a data de publicação das demonstrações contábeis, provenientes de direitos oriundos de danos ao patrimônio, apurados em tomada de contas especial.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1730, 2, '1.2.3.1.1.99.00.00.00.00', 'DEMAIS BENS MÓVEIS', 'Compreende o valor da aquisição ou incorporação dos demais bens móveis não classificados em grupos específicos.', 1, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (1682, 2, '1.2.1.2.1.06.00.00.00.00', 'DEPÓSITOS RESTITUÍVEIS E VALORES VINCULADOS', 'Compreende os valores de depósitos e cauções efetuados e recebidos pela entidade para garantia de contratos, bem como para direito de uso, exploração temporária de bens ou de natureza judicial, depósitos compulsórios e demais recursos vinculados, realizáveis após os doze meses seguintes a data de publicação das demonstrações contábeis.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1683, 2, '1.2.1.2.1.98.00.00.00.00', 'OUTROS CRÉDITOS A RECEBER E VALORES A LONGO PRAZO', 'Compreende os outros créditos e valores realizáveis após os doze meses seguintes a data da publicação das demonstrações contábeis, provenientes de direitos obtidos junto a diversos devedores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1684, 2, '1.2.1.2.1.98.99.00.00.00', 'OUTROS CRÉDITOS A RECEBER E VALORES A LONGO PRAZO', 'Compreende o valor de outros créditos a receber e valores realizáveis no longo prazo.', 1, false, 0, 'N');
