insert into contabilidade.planocontadetalhe values (1686, 2, '1.2.1.3.0.00.00.00.00.00', 'INVESTIMENTOS E APLICAÇÕES TEMPORÁRIAS A LONGO PRAZO', 'Compreende as aplicações de recursos em títulos, valores mobiliários e imobiliários, não destinadas à negociação e que não façam parte das atividades operacionais da entidade, resgatáveis no longo prazo.', 1, false, 0, 'N');
