insert into contabilidade.planocontadetalhe values (1689, 2, '1.2.1.3.1.02.00.00.00.00', 'APLICAÇÃO TEMPORÁRIA EM METAIS PRECIOSOS', 'Compreendem as aplicações de recursos em metais preciosos, não destinados a negociação e que não façam parte das atividades operacionais da entidade, resgatáveis após os doze meses seguintes a data da publicação das demonstrações contábeis.', 1, false, 0, 'N');
