insert into contabilidade.planocontadetalhe values (1782, 2, '3.0.0.0.0.00.00.00.00.00', 'VARIAÇÃO PATRIMONIAL DIMINUTIVA', 'Compreende o decréscimo no beneficio econômico durante o período contábil sob a forma de saída de recurso ou redução de ativo ou incremento em passivo, que resulte em decréscimo do patrimônio liquido e que não seja proveniente de distribuição aos proprietários da entidade.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1783, 2, '3.1.0.0.0.00.00.00.00.00', 'PESSOAL E ENCARGOS', 'Compreende a remuneração do pessoal ativo civil ou militar, correspondente ao somatório das variações patrimoniais diminutivas com subsídios, vencimentos, soldos e vantagens pecuniárias fixas ou variáveis estabelecidas em lei decorrentes do pagamento pelo efetivo exercício do cargo, emprego ou função de confiança no setor público. Compreende ainda, obrigações trabalhistas de responsabilidade do empregador, incidentes sobre a folha de pagamento dos órgãos e demais entidades do setor público, contribuições a entidades fechadas de previdência e benefícios eventuais a pessoal civil e militar, destacados os custos de pessoal e encargos inerentes as mercadorias e produtos vendidos e serviços prestados.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1784, 2, '3.1.1.0.0.00.00.00.00.00', 'REMUNERAÇÃO A PESSOAL', 'Compreende a remuneração do pessoal ativo civil ou militar, correspondente ao somatório das variações patrimoniais diminutivas com subsídios, vencimentos, soldos e vantagens pecuniárias fixas e variáveis estabelecidas em lei decorrentes do pagamento pelo efetivo exercício do cargo, emprego ou função de confiança no setor público, bem como as com contratos de terceirização de mão de obra que se refiram à substituição de servidores e empregados públicos.', 1, false, 0, 'N');
