insert into contabilidade.planocontadetalhe values (1856, 2, '3.2.3.2.1.00.00.00.00.00', 'BENEFÍCIOS DE PRESTAÇÃO CONTINUADA AO PORTADOR DE DEFICIÊNCIA - CONSOLIDAÇÃO', 'Compreendem os benefícios de prestação continuada ao portador de deficiência e renda mensal vitalícia - invalidez. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1857, 2, '3.2.3.9.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS DE PRESTAÇÃO CONTINUADA - CONSOLIDAÇÃO', 'Compreende outros benefícios de prestação continuada. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1858, 2, '3.2.4.0.0.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS', 'Compreendem as provisões suplementares e provisórias, prestadas aos cidadãos e as famílias em virtude de nascimento, morte, situações de vulnerabilidade temporária e de calamidade pública. As provisões relativas a programas, projetos, serviços e benefícios diretamente vinculados ao campo da saúde, educação, integração nacional e das demais políticas setoriais não se incluem na modalidade de benefícios eventuais da assistência social.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1859, 2, '3.2.4.1.0.00.00.00.00.00', 'AUXÍLIO POR NATALIDADE', 'Compreende o beneficio eventual que visa o pagamento de auxílio por natalidade as famílias cuja renda mensal per capita seja inferior a 1/4 (um quarto) do salário mínimo.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1860, 2, '3.2.4.1.1.00.00.00.00.00', 'AUXÍLIO POR NATALIDADE - CONSOLIDAÇÃO', 'Compreende o beneficio eventual que visa o pagamento de auxílio por natalidade as famílias cuja renda mensal per capita seja inferior a 1/4 (um quarto) do salário mínimo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1861, 2, '3.2.4.2.0.00.00.00.00.00', 'AUXÍLIO POR MORTE', 'Compreende o beneficio eventual que visa o pagamento de auxílio por morte as famílias cuja renda mensal per capita seja inferior a 1/4 (um quarto) do salário mínimo.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1862, 2, '3.2.4.2.1.00.00.00.00.00', 'AUXÍLIO POR MORTE - CONSOLIDAÇÃO', 'Compreende o beneficio eventual que visa o pagamento de auxílio por morte as famílias cuja renda mensal per capita seja inferior a 1/4 (um quarto) do salário mínimo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1863, 2, '3.2.4.3.0.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS POR SITUAÇÕES DE VULNERABILIDADE TEMPORÁRIA', 'Compreende as provisões suplementares e provisórias, prestadas aos cidadãos e as famílias em virtude de situações de vulnerabilidade temporária, com prioridade para a criança, a família, o idoso, a pessoa portadora de deficiência, a gestante e a nutriz.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1864, 2, '3.3.1.1.0.00.00.00.00.00', 'CONSUMO DE MATERIAL', 'Compreende as variações patrimoniais diminutivas provenientes de requisição de material de consumo para uso interno.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1865, 2, '3.2.4.3.1.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS POR SITUAÇÕES DE VULNERABILIDADE TEMPORÁRIA - CONSOLIDAÇÃO', 'Compreende as provisões suplementares e provisórias, prestadas aos cidadãos e as famílias em virtude de situações de vulnerabilidade temporária, com prioridade para a criança, a família, o idoso, a pessoa portadora de deficiência, a gestante e a nutriz. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1866, 2, '3.2.4.4.0.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS EM CASO DE CALAMIDADE PÚBLICA', 'Compreendem os benefícios para atendimento de vitimas de calamidade pública, de modo a assegurar lhes a sobrevivência e a reconstrução de sua autonomia. Entende-se se por estado de calamidade pública o reconhecimento pelo poder público de situação anormal, advinda de baixas ou altas temperaturas, tempestades, enchentes, inversão térmica, desabamentos, incêndios, epidemias, causando sérios danos à comunidade afetada, inclusive a incolumidade ou a vida de seus integrantes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1867, 2, '3.2.9.2.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS - RGPS', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários do RGPS, não abrangidas nos grupos anteriores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1888, 2, '3.3.2.1.0.00.00.00.00.00', 'DIÁRIAS', 'Compreende as variações patrimoniais diminutivas provenientes da utilização de diárias pagas aos servidores, empregados públicos e colaboradores eventuais, para fazer face a despesas com pousada, alimentação e locomoção urbana.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1868, 2, '3.2.4.4.1.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS EM CASO DE CALAMIDADE PÚBLICA - CONSOLIDAÇÃO', 'Compreendem os benefícios para atendimento de vitimas de calamidade pública, de modo a assegurar - lhes a sobrevivência e a reconstrução de sua autonomia. Entende-se por estado de calamidade pública o reconhecimento pelo poder público de situação anormal, advinda de baixas ou altas temperaturas, tempestades, enchentes, inversão térmica, desabamentos, incêndios, epidemias, causando sérios danos à comunidade afetada, inclusive a incolumidade ou a vida de seus integrantes. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1869, 2, '3.2.4.9.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS EVENTUAIS', 'Compreende a variação patrimonial diminutiva proveniente de outros benefícios eventuais', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1870, 2, '3.2.4.9.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS EVENTUAIS - CONSOLIDAÇÃO', 'Compreende a variação patrimonial diminutiva proveniente de outros benefícios eventuais.Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1871, 2, '3.2.5.0.0.00.00.00.00.00', 'POLÍTICAS PÚBLICAS DE TRANSFERÊNCIA DE RENDA', 'Compreende as políticas públicas que visem contribuir para a redução da fome, da pobreza, da desigualdade e de outras formas de privação vividas pelas famílias mais excluídas, considerando três dimensões: o alivio imediato da pobreza, por meio da transferência de renda diretamente as famílias pobres e extremamente pobres; a contribuição para a redução da pobreza da geração seguinte, por meio do reforço do direito de acesso aos serviços de saúde e de educação, com o cumprimento das condicionalidades nestas áreas; e a articulação de ações complementares, de forma a desenvolver as capacidades das famílias beneficiárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1872, 2, '3.2.5.0.1.00.00.00.00.00', 'POLÍTICAS PÚBLICAS DE TRANSFERÊNCIA DE RENDA - CONSOLIDAÇÃO', 'Compreende as políticas públicas que visem contribuir para a redução da fome, da pobreza, da desigualdade e de outras formas de privação vividas pelas famílias mais excluídas, considerando três dimensões: o alivio imediato da pobreza, por meio da transferência de renda diretamente as famílias pobres e extremamente pobres; a contribuição para a redução da pobreza da geração seguinte, por meio do reforço do direito de acesso aos serviços de saúde e de educação, com o cumprimento das condicionalidades nestas áreas; e a articulação de ações complementares, de forma a desenvolver as capacidades das famílias beneficiarias. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1873, 2, '3.2.9.0.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS E ASSISTENCIAIS', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários ou assistenciais, não abrangidas nos grupos anteriores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1874, 2, '3.2.9.1.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS - RPPS', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários do RPPS, não abrangidas nos grupos anteriores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1875, 2, '3.3.3.0.0.00.00.00.00.00', 'DEPRECIAÇÃO, AMORTIZAÇÃO E EXAUSTÃO', 'Compreende o decréscimo no beneficio de um bem durante o período contábil decorrente de depreciação, amortização e exaustão.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1876, 2, '3.2.9.1.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS - RPPS - CONSOLIDAÇÃO', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários do RPPS, não abrangidas nos grupos anteriores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1877, 2, '3.2.9.2.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS - RGPS - CONSOLIDAÇÃO', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários do RGPS, não abrangidas nos grupos anteriores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1878, 2, '3.2.9.3.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS - MILITAR', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários do regime próprio dos militares, não abrangidas nos grupos anteriores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1879, 2, '3.2.9.3.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS - MILITAR - CONSOLIDAÇÃO', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários do regime próprio dos militares, não abrangidas nos grupos anteriores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1880, 2, '3.2.9.9.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS E ASSISTENCIAIS', 'Compreende as variações patrimoniais diminutivas provenientes de outros benefícios previdenciários e assistenciais', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1881, 2, '3.2.9.9.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS E ASSISTENCIAIS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes de outros benefícios previdenciários e assistenciais. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1882, 2, '3.3.0.0.0.00.00.00.00.00', 'USO DE BENS, SERVIÇOS E CONSUMO DE CAPITAL FIXO', 'Compreende o somatório das variações patrimoniais diminutivas com manutenção e operação da maquina pública, exceto despesas com pessoal e encargos que serão registradas em grupo específico (despesas de pessoal e encargos). Compreende: diárias, material de consumo, material de distribuição gratuita, passagens e despesas com locomoção, serviços de terceiros, arrendamento mercantil operacional, aluguel, depreciação, amortização, exaustão, entre outras.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1883, 2, '3.3.1.0.0.00.00.00.00.00', 'USO DE MATERIAL DE CONSUMO', 'Compreende as variações patrimoniais diminutivas provenientes da distribuição do material de consumo. Um material é considerado de consumo quando for de duração inferior a dois anos, frágil, perecível, incorporável, transformável ou cuja finalidade seja para consumo imediato ou para reposição.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1884, 2, '3.3.1.1.1.00.00.00.00.00', 'CONSUMO DE MATERIAL - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes de requisição de material de consumo para uso interno.Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1885, 2, '3.3.1.2.0.00.00.00.00.00', 'DISTRIBUIÇÃO DE MATERIAL GRATUITO', 'Compreende as variações patrimoniais diminutivas provenientes da distribuição de material gratuito.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2408, 2, '1.2.3.8.1.01.07.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE BENS MÓVEIS EM ANDAMENTO', 'Registra a depreciação acumulada de bens móveis em andamento.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (1886, 2, '3.3.1.2.1.00.00.00.00.00', 'DISTRIBUIÇÃO DE MATERIAL GRATUITO - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes da distribuição de material gratuito. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1889, 2, '3.3.2.1.1.00.00.00.00.00', 'DIÁRIAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes da utilização de diárias pagas aos servidores, empregados públicos e colaboradores eventuais, para fazer face a despesas com pousada, alimentação e locomoção urbana.Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1890, 2, '3.3.2.2.0.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PF', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa física fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1891, 2, '3.3.2.2.1.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PF - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa física fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1892, 2, '3.3.2.3.0.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PJ', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa jurídica fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1893, 2, '3.3.2.3.1.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PJ - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa jurídica fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1894, 2, '3.3.2.4.0.00.00.00.00.00', 'CONTRATO DE TERCEIRIZAÇÃO POR SUBSTITUIÇÃO DE MÃO DE OBRA - ART. 18, § 1º, LC 101/00', 'Compreende a apropriação das despesas relativas à mão de obra, constantes dos contratos de terceirização, classificáveis no grupo de despesa com pessoal e encargos em obediência ao disposto no artigo 18, § 1º, da lei complementar nº 101, de 2000.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1895, 2, '3.3.2.4.1.00.00.00.00.00', 'CONTRATO DE TERCEIRIZAÇÃO POR SUBSTITUIÇÃO DE MÃO DE OBRA - ART. 18 § 1º, LC 101/00 - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas relativas à mão de obra, constantes dos contratos de terceirização, classificáveis no grupo de despesa com pessoal e encargos em obediência ao disposto no artigo 18, § 1º, da lei complementar nº 101, de 2000. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1896, 2, '3.3.3.1.0.00.00.00.00.00', 'DEPRECIAÇÃO', 'Compreende a redução do valor dos bens tangíveis pelo desgaste ou perda de utilidade por uso, ação da natureza ou obsolescência.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1897, 2, '3.3.3.1.1.00.00.00.00.00', 'DEPRECIAÇÃO - CONSOLIDAÇÃO', 'Compreende a redução do valor dos bens tangíveis pelo desgaste ou perda de utilidade por uso, ação da natureza ou obsolescência. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1898, 2, '3.3.3.1.1.01.00.00.00.00', 'DEPRECIAÇÃO DE IMOBILIZADO', 'Compreende a redução do valor dos bens tangíveis, do ativo imobilizado, pelo desgaste ou perda de utilidade por uso, ação da natureza ou obsolescência.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1899, 2, '3.3.3.2.0.00.00.00.00.00', 'AMORTIZAÇÃO', 'Compreende a redução do valor aplicado na aquisição de direitos de propriedade e quaisquer outros, inclusive ativos intangíveis, com existência ou exercício de duração limitada, ou cujo objeto sejam bens de utilização por prazo legal ou contratualmente limitado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1900, 2, '3.3.3.2.1.00.00.00.00.00', 'AMORTIZAÇÃO - CONSOLIDAÇÃO', 'Compreende a redução do valor aplicado na aquisição de direitos de propriedade e quaisquer outros, inclusive ativos intangíveis, com existência ou exercício de duração limitada, ou cujo objeto sejam bens de utilização por prazo legal ou contratualmente limitado. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1901, 2, '3.3.3.3.0.00.00.00.00.00', 'EXAUSTÃO', 'Compreende a redução do valor decorrente da exploração dos recursos minerais, florestais e outros recursos naturais esgotáveis.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1902, 2, '3.3.3.3.1.00.00.00.00.00', 'EXAUSTÃO - CONSOLIDAÇÃO', 'Compreende a redução do valor decorrente da exploração dos recursos minerais, florestais e outros recursos naturais esgotáveis. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1903, 2, '3.4.0.0.0.00.00.00.00.00', 'VARIAÇÕES PATRIMONIAIS DIMINUTIVAS FINANCEIRAS', 'Compreende as variações patrimoniais diminutivas com operações financeiras, tais como: juros incorridos, descontos concedidos, comissões, despesas bancarias e correções monetárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1904, 2, '3.4.1.0.0.00.00.00.00.00', 'JUROS E ENCARGOS DE EMPRÉSTIMOS E FINANCIAMENTOS OBTIDOS', 'Compreende a variação patrimonial diminutiva com juros e encargos de empréstimos e financiamentos contraídos com pessoas jurídicas de direito público ou privado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1905, 2, '3.4.1.1.0.00.00.00.00.00', 'JUROS E ENCARGOS DA DÍVIDA CONTRATUAL INTERNA', 'Compreende a variação patrimonial diminutiva com juros e encargos da dívida pública interna derivada de obrigações assumidas com pessoas jurídicas de direito público ou privado com vistas à execução de obras, fornecimento de bens ou prestação de serviços, ou da celebração de contratos de financiamento.', 1, false, 0, 'N');
