insert into contabilidade.planocontadetalhe values (1990, 2, '3.5.1.4.2.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS PARA APORTES DE RECURSOS PARA O RGPS  - INTRA OFSS', 'Compreende o valor das transferências para os aportes financeiros da União para a cobertura de déficits financeiros ou atuariais do RGPS. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1991, 2, '3.5.2.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS INTER GOVERNAMENTAIS', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências a União, Estados, Distrito Federal, Municípios, inclusive as entidades vinculadas, de bens e/ou valores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1992, 2, '3.5.2.1.0.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1993, 2, '3.5.2.1.1.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS). Por exemplo, empresa independente de um ente recebendo transferência de outro ente. (a empresa independente não pertence ao OFSS mas pertence ao conceito de governamental).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1994, 2, '3.5.3.1.0.00.00.00.00.00', 'TRANSFERÊNCIAS A INSTITUIÇÕES PRIVADAS SEM FINS LUCRATIVOS', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências de bens e valores a instituições privadas sem fins lucrativos que não tenham vinculo com a administração pública, exemplo: ONGs, OSCIPS e OS.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1995, 2, '3.5.2.1.3.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - INTER OFSS - UNIÃO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) de entes públicos distintos, para permitir a consolidação nacional realizada pelo Poder Executivo da União.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1996, 2, '3.5.2.1.4.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - INTER OFSS - ESTADO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1997, 2, '3.5.2.1.5.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - INTER OFSS - MUNICÍPIO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1998, 2, '3.5.2.2.0.00.00.00.00.00', 'TRANSFERÊNCIAS AO FUNDEB', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência de recursos dos entes, incluindo a complementação da União, para a composição do Fundo de Manutenção e Desenvolvimento da Educação Básica e de Valorização dos Profissionais da Educação, instituído pela E.C. n.º 53/2006 e regulamentado pela lei 11.494/2007.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (1999, 2, '3.5.2.2.4.00.00.00.00.00', 'TRANSFERÊNCIAS AO FUNDEB - INTER-OFSS - ESTADO ', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência de recursos dos entes, incluindo a complementação da União, para a composição do Fundo de Manutenção e Desenvolvimento da Educação Básica e de Valorização dos Profissionais da Educação, instituído pela E.C. nº 53/2006 e regulamentado pela lei 11.494/2007. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2000, 2, '3.5.2.3.0.00.00.00.00.00', 'TRANSFERÊNCIAS VOLUNTÁRIAS', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências voluntárias a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, bem como a demais entidades, de bens e/ou valores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2001, 2, '3.5.2.3.1.00.00.00.00.00', 'TRANSFERÊNCIAS VOLUNTÁRIAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências voluntárias a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS). Por exemplo, empresa independente de um ente recebendo transferência de outro ente. (A empresa independente não pertence ao OFSS, mas pertence ao conceito de governamental).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2002, 2, '3.5.2.3.3.00.00.00.00.00', 'TRANSFERÊNCIAS VOLUNTÁRIAS - INTER OFSS - UNIÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências voluntárias de bens e/ou valores. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e a União.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2003, 2, '3.5.2.3.4.00.00.00.00.00', 'TRANSFERÊNCIAS VOLUNTÁRIAS - INTER-OFSS - ESTADO ', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências voluntárias de bens e/ou valores. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2240, 2, '5.2.2.1.2.00.00.00.00.00', 'DOTAÇÃO ADICIONAL POR TIPO DE CREDITO', 'Compreende o somatório dos valores monetários da dotação orçamentária decorrentes da abertura de créditos adicionais e seus cancelamentos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2004, 2, '3.5.2.3.5.00.00.00.00.00', 'TRANSFERÊNCIAS VOLUNTÁRIAS - INTER-OFSS - MUNICÍPIO ', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências voluntárias de bens e/ou valores. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2005, 2, '3.5.2.4.0.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS', 'Compreende as variações patrimoniais diminutivas decorrentes demais transferências a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2006, 2, '3.5.2.4.1.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes demais transferências a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2007, 2, '3.5.2.4.3.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS - INTER OFSS - UNIÃO', 'Compreende as variações patrimoniais diminutivas decorrentes demais transferências a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e a União.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2008, 2, '3.5.2.4.4.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS - INTER OFSS - ESTADO', 'Compreende as variações patrimoniais diminutivas decorrentes demais transferências a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2083, 2, '3.6.3.3.1.00.00.00.00.00', 'PERDAS INVOLUNTÁRIAS COM ESTOQUES - CONSOLIDAÇÃO', 'Compreende o desfazimento físico involuntário de bem do estoque, como o que resulta de sinistros como incêndio e inundações. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2009, 2, '3.5.2.4.5.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS - INTER OFSS - MUNICÍPIO', 'Compreende as variações patrimoniais diminutivas decorrentes demais transferências a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2010, 2, '3.5.3.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS A INSTITUIÇÕES PRIVADAS', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências financeiras a instituições privadas, inclusive de bens e valores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2011, 2, '3.6.2.0.0.00.00.00.00.00', 'PERDAS COM ALIENAÇÃO', 'Compreende a perda com alienação de ativos, ou seja, quando o valor alienado do ativo e menor que o seu valor contábil, de maneira que a diferença compreende a perda.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2012, 2, '3.5.3.1.1.00.00.00.00.00', 'TRANSFERÊNCIAS A INSTITUIÇÕES PRIVADAS SEM FINS LUCRATIVOS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências de bens e valores a instituições privadas sem fins lucrativos que não tenham vinculo com a administração pública, exemplo: ONGs,OSCIPS E OS. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2013, 2, '3.5.3.2.0.00.00.00.00.00', 'TRANSFERÊNCIAS A INSTITUIÇÕES PRIVADAS COM FINS LUCRATIVOS', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências de bens e valores a instituições privadas com fins lucrativos que não tenham vínculos com administração pública.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2014, 2, '3.5.3.2.1.00.00.00.00.00', 'TRANSFERÊNCIAS A INSTITUIÇÕES PRIVADAS COM FINS LUCRATIVOS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências de bens e valores a instituições privadas com fins lucrativos, que não tenham vínculos com a administração pública. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2015, 2, '3.5.4.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS A INSTITUIÇÕES MULTIGOVERNAMENTAIS', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências a instituições multigovernamentais, da quais o ente transferidor não participe.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2016, 2, '3.5.4.0.1.00.00.00.00.00', 'TRANSFERÊNCIAS A INSTITUIÇÕES MULTIGOVERNAMENTAIS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências a instituições multigovernamentais, das quais o ente transferidor não participe. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2017, 2, '3.5.5.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS A CONSÓRCIOS PÚBLICOS', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências a consórcios públicos, dos quais o ente transferidor participe.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2018, 2, '3.5.5.0.1.00.00.00.00.00', 'TRANSFERÊNCIAS A CONSÓRCIOS PÚBLICOS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes das transferências a consórcios públicos, dos quais o ente transferidor participe. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2019, 2, '3.5.6.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS AO EXTERIOR', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências a organismos e fundos internacionais, de governos estrangeiros e instituições privadas com ou sem fins lucrativos no exterior.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2020, 2, '3.5.6.0.1.00.00.00.00.00', 'TRANSFERÊNCIAS AO EXTERIOR - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências a organismos e fundos internacionais, de governos estrangeiros e instituições privadas com ou sem fins lucrativos no exterior. Compreende os valores de operações efetuadas entre uma unidade pertencente ao orçamento fiscal e da seguridade social (OFSS) com unidade que não pertença ao OFSS de ente público.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2021, 2, '3.5.7.0.0.00.00.00.00.00', 'EXECUÇÃO ORÇAMENTÁRIA DELEGADA', 'Compreende as variações patrimoniais diminutivas decorrentes de transferência de recursos financeiros, decorrentes de delegação ou descentralização aos Entes (União, Estados, Distrito Federal ou Municípios) ou Consórcios Públicos para execução de ações de responsabilidade exclusiva do delegante.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2022, 2, '3.5.7.1.0.00.00.00.00.00', 'EXECUÇÃO ORÇAMENTÁRIA DELEGADA A ENTES', 'Compreende as variações patrimoniais diminutivas decorrentes de transferência de recursos financeiros, decorrentes de delegação ou descentralização aos Entes (União, Estados, Distrito Federal, Municípios ou consórcios públicos) para execução de ações de responsabilidade exclusiva do delegante.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2023, 2, '3.5.7.1.3.00.00.00.00.00', 'EXECUÇÃO ORÇAMENTÁRIA DELEGADA A ENTES - INTER OFSS - UNIÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferência de recursos financeiros, decorrentes de delegação ou descentralização aos Entes (União, Estados, Distrito Federal ou Municípios) para execução de ações de responsabilidade exclusiva do delegante. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e a União.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2024, 2, '3.5.7.1.4.00.00.00.00.00', 'EXECUÇÃO ORÇAMENTÁRIA DELEGADA A ENTES - INTER OFSS - ESTADO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferência de recursos financeiros, decorrentes de delegação ou descentralização aos Entes (União, Estados, Distrito Federal ou Municípios) para execução de ações de responsabilidade exclusiva do delegante. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2025, 2, '3.5.7.1.5.00.00.00.00.00', 'EXECUÇÃO ORÇAMENTÁRIA DELEGADA A ENTES - INTER OFSS - MUNICÍPIO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferência de recursos financeiros, decorrentes de delegação ou descentralização aos Entes (União, Estados, Distrito Federal ou Municípios) para execução de ações de responsabilidade exclusiva do delegante. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2026, 2, '3.5.7.2.0.00.00.00.00.00', 'EXECUÇÃO ORÇAMENTÁRIA DELEGADA A CONSÓRCIOS', 'Compreende as variações patrimoniais diminutivas decorrentes de transferência de recursos financeiros, decorrentes de delegação ou descentralização aos Consórcios Públicos para execução de ações de responsabilidade exclusiva do delegante.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2084, 2, '3.6.3.9.0.00.00.00.00.00', 'OUTRAS PERDAS INVOLUNTÁRIAS', 'Compreendem o desfazimento físico involuntário de outros bens, como as que resultam de sinistros como incêndio e inundações.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2027, 2, '3.5.7.2.1.00.00.00.00.00', 'EXECUÇÃO ORÇAMENTÁRIA DELEGADA A CONSÓRCIOS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferência de recursos financeiros, decorrentes de delegação ou descentralização aos Consórcios Públicos para execução de ações de responsabilidade exclusiva do delegante. Compreende os valores de operações efetuadas entre uma unidade pertencente ao orçamento fiscal e da seguridade social (OFSS) com unidade que não pertença ao OFSS de ente público.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2028, 2, '3.5.9.0.0.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS E DELEGAÇÕES CONCEDIDAS', 'Compreende as variações patrimoniais diminutivas decorrentes de demais transferências e delegações concedidas não compreendidas nas contas anteriores. ', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2029, 2, '3.5.9.0.1.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS CONCEDIDAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de demais transferências concedidas não compreendidas nas contas anteriores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2030, 2, '3.6.2.1.0.00.00.00.00.00', 'PERDAS COM ALIENAÇÃO DE INVESTIMENTOS', 'Compreende a perda com alienação de investimentos, ou seja, quando o valor alienado do ativo e menor que o seu valor contábil, de maneira que a diferença compreende a perda.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2031, 2, '3.6.0.0.0.00.00.00.00.00', 'DESVALORIZAÇÃO E PERDA DE ATIVOS E INCORPORAÇÃO DE PASSIVOS', 'Compreende a variação patrimonial diminutiva com desvalorização e perdas de ativos, nos casos de reavaliação, redução a valor recuperável, provisões para perdas, perdas com alienação e perdas involuntárias. Compreende também a variação patrimonial diminutiva com incorporação de passivo.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2032, 2, '3.6.1.0.0.00.00.00.00.00', 'REAVALIAÇÃO, REDUÇÃO A VALOR RECUPERÁVEL E AJUSTE PARA PERDAS', 'Compreende as variações patrimoniais diminutivas relativas a reavaliação, redução a valor recuperável e ajuste para perdas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2033, 2, '3.6.1.1.0.00.00.00.00.00', 'REAVALIAÇÃO DE IMOBILIZADO', 'Compreende a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para bens do ativo imobilizado, quando esse for inferior ao valor liquido contábil.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2034, 2, '3.6.1.1.1.00.00.00.00.00', 'REAVALIAÇÃO DE IMOBILIZADO - CONSOLIDAÇÃO', 'Compreende a variação patrimonial diminutiva relativa à adoção do valor de mercado ou de consenso entre as partes para bens do ativo imobilizado, quando esse for inferior ao valor liquido contábil. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
