insert into contabilidade.planocontadetalhe values (2094, 2, '3.7.1.1.0.00.00.00.00.00', 'IMPOSTOS', 'Compreende as variações patrimoniais diminutivas com impostos, apurados no período de competência, e vinculados à atividade administrativa do contribuinte, definidos no CTN.', 1, false, 0, 'N');
