insert into contabilidade.planocontadetalhe values (2247, 2, '5.2.2.2.1.00.00.00.00.00', 'DESCENTRALIZAÇÃO INTERNA DE CRÉDITOS - PROVISÃO', 'Compreende os valores dos créditos descentralizados das unidades orçamentárias para as unidades administrativas sob sua jurisdição ou entre estas, no âmbito do próprio ministério, secretaria ou órgão equivalente, recebidos ao longo do exercício.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2248, 2, '5.2.2.2.2.00.00.00.00.00', 'DESCENTRALIZAÇÃO EXTERNA DE CRÉDITOS - DESTAQUE', 'Compreende a descentralização do credito inicial ou adicional recebido de unidades ou órgãos pertencentes a ministérios, secretarias e órgãos equivalentes distintos ou entre estes e entidades da administração indireta.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2249, 2, '5.2.2.2.2.01.00.00.00.00', 'ADMINISTRAÇÃO DIRETA                ', 'Compreende o valor referente a movimentação de créditos decorrentes de recebimento ou de transferência de outros órgãos e entidades de igual ou de diferente unidade orçamentária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2250, 2, '5.2.2.2.2.02.00.00.00.00', 'ADMINISTRAÇÃO INDIRETA                 ', 'Compreende o valor referente a movimentação de créditos decorrentes de recebimento ou de transferência de outros órgãos e entidades de igual ou de diferente unidade orçamentária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2251, 2, '5.2.2.2.9.00.00.00.00.00', 'OUTRAS DESCENTRALIZAÇÕES DE CRÉDITOS', 'Compreende as movimentações de créditos recebidos por cisão, fusão, extinção, compensação e outras transações.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2252, 2, '5.2.2.3.0.00.00.00.00.00', 'DETALHAMENTO DE CREDITO', 'Compreende o somatório relativos às dotações detalhadas relativas a cada ação orçamentária (projeto, atividade ou operações especiais).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2253, 2, '5.2.2.9.0.00.00.00.00.00', 'OUTROS CONTROLES DA DESPESA ORÇAMENTÁRIA', 'Compreende o somatório dos valores das despesas pré-empenhadas e empenhadas por detalhamento específico.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2254, 2, '5.2.2.9.1.00.00.00.00.00', 'PRÉ-EMPENHOS DA DESPESA', 'Compreende o credito orçamentário pré-empenhado, para atender objetivo especifico, nos casos em que a despesa a ser realizada, por suas características, exige um intervalo de tempo desde a decisão da realização da despesa até a efetivação da emissão de NE.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2255, 2, '5.2.2.9.2.00.00.00.00.00', 'EMPENHOS POR EMISSÃO', 'Compreende o somatório dos valores de empenho emitido por ato emanado de autoridade competente que cria para o poder público a obrigação de pagar as despesas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2256, 2, '5.2.2.9.2.01.00.00.00.00', 'EXECUÇÃO DA DESPESA POR NOTA DE EMPENHO', 'Compreende os valores executados da despesa, detalhados por nota de empenho.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2257, 2, '5.3.0.0.0.00.00.00.00.00', 'INSCRIÇÃO DE RESTOS A PAGAR', 'Compreende o somatório relativo ao valor da inscrição das despesas empenhadas e não pagas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2258, 2, '5.3.1.0.0.00.00.00.00.00', 'INSCRIÇÃO DE RP NÃO PROCESSADOS', 'Compreende o somatório dos valores inscritos em restos a pagar não-processados relativos às despesas empenhadas e não liquidadas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2259, 2, '5.3.2.0.0.00.00.00.00.00', 'INSCRIÇÃO DE RP PROCESSADOS', 'Compreende o somatório dos valores inscritos em restos a pagar processados.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2260, 2, '6.2.1.3.0.00.00.00.00.00', '(-) DEDUÇÕES DA RECEITA ORÇAMENTÁRIA', 'Compreende o somatório dos valores relativos às deduções de receitas realizadas (devoluções, restituições, e outras), detalhada por natureza de receita.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2261, 2, '6.2.1.3.1.00.00.00.00.00', '(-) DEDUÇÕES POR TRANSFERÊNCIAS CONSTITUCIONAIS E LEGAIS', 'Compreende o somatório dos valores relativos as deduções de receitas realizadas por transferências constitucionais e legais, detalhada por natureza de receita.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2262, 2, '7.0.0.0.0.00.00.00.00.00', 'CONTROLES DEVEDORES', 'Compreende as contas em que são registrados atos potenciais e controles específicos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2291, 2, '7.2.1.0.0.00.00.00.00.00', 'DISPONIBILIDADES POR DESTINAÇÃO', 'Compreende as contas que registram os controles sobre as disponibilidades por destinação de recursos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2263, 2, '7.1.0.0.0.00.00.00.00.00', 'ATOS POTENCIAIS', 'Compreende contas relacionadas às situações não compreendidas no patrimônio, mas que, direta ou indiretamente, possam vir à afeta-lo, exclusive as que dizem respeito a atos e fatos ligados a execução orçamentária e financeira e as contas com função precípua de controle.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2264, 2, '7.1.1.0.0.00.00.00.00.00', 'ATOS POTENCIAIS ATIVOS', 'Compreende os atos e fatos que possam vir a aumentar o ativo da entidade governamental.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2265, 2, '7.1.1.1.0.00.00.00.00.00', 'GARANTIAS E CONTRAGARANTIAS RECEBIDAS', 'Compreende contas relacionadas ao registro de avais, fianças e hipotecas recebidas no país e no exterior, bem como emolumentos consulares e outras garantias e contragarantias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2266, 2, '7.1.1.1.1.00.00.00.00.00', 'GARANTIAS E CONTRAGARANTIAS RECEBIDAS - CONSOLIDAÇÃO', 'Compreende o valor recebido de compromisso de adimplência de obrigação financeira ou contratual, no país. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (2267, 2, '7.1.1.1.1.01.00.00.00.00', 'GARANTIAS RECEBIDAS NO PAÍS', 'COMPREENDE O VALOR RECEBIDO DE COMPROMISSO DE ADIMPLÊNCIA DE OBRIGAÇÃO FINANCEIRA OU CONTRATUAL, NO PAÍS.', 1, false, 0, 'N');
