insert into contabilidade.planocontadetalhe values (2270, 2, '7.1.1.1.1.04.00.00.00.00', 'CONTRAGARANTIAS RECEBIDAS NO EXTERIOR', 'Compreende o valor das contragarantias recebidas referentes às concessões de garantias concedidas sobre obrigações contraídas no exterior.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2271, 2, '7.1.1.2.0.00.00.00.00.00', 'DIREITOS CONVENIADOS E OUTROS INSTRUMENTOS CONGÊNERES', 'Compreende contas relacionadas ao registro, no convenente, dos convênios firmados entre entidades, objetivando o cumprimento de finalidades comuns.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2272, 2, '7.1.1.2.1.00.00.00.00.00', 'DIREITOS CONVENIADOS E OUTROS INSTRUMENTOS CONGÊNERES - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro, no convenente, dos convênios firmados entre entidades objetivando o cumprimento de finalidades comuns. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2273, 2, '7.1.1.3.0.00.00.00.00.00', 'DIREITOS CONTRATUAIS', 'Compreende contas relacionadas ao registro dos valores de direitos contratuais, quando a administração pública participa como contratada.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2274, 2, '7.1.1.3.1.00.00.00.00.00', 'DIREITOS CONTRATUAIS - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro dos valores de direitos contratuais, quando a administração pública participa como contratada. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2275, 2, '7.1.1.9.0.00.00.00.00.00', 'OUTROS ATOS POTENCIAIS ATIVOS', 'Compreende os atos potenciais ativos não contemplados em contas especificas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2276, 2, '7.1.1.9.1.00.00.00.00.00', 'OUTROS ATOS POTENCIAIS ATIVOS - CONSOLIDAÇÃO', 'Compreende os atos potenciais ativos não contemplados em contas especificas. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2277, 2, '7.1.2.0.0.00.00.00.00.00', 'ATOS POTENCIAIS PASSIVOS', 'Compreende os atos e fatos que possam vir a aumentar o passivo ou diminuir o ativo da entidade governamental.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2278, 2, '7.1.2.1.0.00.00.00.00.00', 'GARANTIAS E CONTRAGARANTIAS CONCEDIDAS', 'Compreende contas relacionadas ao registro de avais, fianças e hipotecas concedidas no país e no exterior, bem como emolumentos consulares e outras garantias e contragarantias.', 1, false, 0, 'N');
