insert into contabilidade.planocontadetalhe values (2282, 2, '7.1.2.1.1.03.00.00.00.00', 'CONTRAGARANTIAS CONCEDIDAS NO PAÍS', 'Compreende o valor das contragarantias concedidas referentes as concessões de garantias concedidas sobre obrigações contraídas no pais.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2283, 2, '7.1.2.1.1.04.00.00.00.00', 'CONTRAGARANTIAS CONCEDIDAS NO EXTERIOR', 'Compreende o valor das contragarantias concedidas referentes às concessões de garantias concedidas sobre obrigações contraídas no exterior.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2284, 2, '7.1.2.2.0.00.00.00.00.00', 'OBRIGAÇÕES CONVENIADAS E OUTROS INSTRUMENTOS CONGÊNERES', 'Compreende contas relacionadas ao registro, no concedente, do valor dos convênios firmados entre entidades, objetivando o cumprimento de finalidades comuns.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2285, 2, '7.1.2.2.1.00.00.00.00.00', 'OBRIGAÇÕES CONVENIADAS E OUTROS INSTRUMENTOS CONGÊNERES - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro, no convenente, dos convênios firmados entre entidades objetivando o cumprimento de finalidades comuns. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
