insert into contabilidade.planocontadetalhe values (2287, 2, '7.1.2.3.1.00.00.00.00.00', 'OBRIGAÇÕES CONTRATUAIS - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro dos valores de obrigações contratuais, quando a administração pública participa como contratante. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2288, 2, '7.1.2.3.1.08.00.00.00.00', 'CONTRATOS DE PPP', 'Compreende o valor dos passivos decorrentes de contrato de PPP, incluindo o valor das obrigações contratuais com as contraprestações futuras, assim como os riscos não provisionados e outros passivos contingentes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2289, 2, '7.1.2.9.0.00.00.00.00.00', 'OUTROS ATOS POTENCIAIS PASSIVOS', 'Compreende os atos potenciais passivos não contemplados em contas especificas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3591, 2, '8.9.1.2.1.04.00.00.00.00', 'ADIANTAMENTOS IMPUGNADOS', 'Registra os valores dos adiantamentos concedidos impugnados.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (2293, 2, '7.2.1.2.0.00.00.00.00.00', 'LIMITE DE RESTOS A PAGAR POR DESTINAÇÃO', 'Compreende as contas que controlam a inscrição do limite de restos a pagar, classificados por destinação de recursos', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2294, 2, '7.2.1.3.0.00.00.00.00.00', 'RECURSO DIFERIDO POR DESTINAÇÃO', 'Compreende as contas que controlam a inscrição dos recursos diferidos. Recursos diferidos são as sobras de recursos financeiros ao final do exercício (recurso diferido), que deveriam ser devolvidas a conta única, porem, ao invés disso, essas sobras são liberadas para uso (apenas no exercício seguinte) através de descontos das transferências financeiras (cota, repasse e sub-repasse) do exercício seguinte.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2295, 2, '7.2.2.0.0.00.00.00.00.00', 'PROGRAMAÇÃO FINANCEIRA', 'Compreende as contas que registram as cotas, repasses e sub-repasses para atender a despesas orçamentárias, restos a pagar autorizados e demais dispêndios extra orçamentários.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2296, 2, '7.2.3.0.0.00.00.00.00.00', 'INSCRIÇÃO DO LIMITE ORÇAMENTÁRIO', 'Compreende as contas que controlam o limite de empenho e movimentação de créditos orçamentários, conforme estabelecidos em ato próprio dos poderes e ministério público.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3389, 2, '8.1.2.1.1.03.03.00.00.00', 'AVAIS EXECUTADOS', 'Registra o valor recebido das contragarantias recebidas por aval no país.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (2297, 2, '7.2.4.0.0.00.00.00.00.00', 'CONTROLES DA ARRECADAÇÃO', 'Compreende as contas que registram o valor da arrecadação liquida da receita orçamentária, por meio de documentos de arrecadação como DARF, GRU, DAR, entre outros.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2298, 2, '7.2.4.1.0.00.00.00.00.00', 'CONTROLES DE DOCUMENTOS DE ARRECADAÇÃO', 'COMPREENDE AS CONTAS QUE REGISTRAM O VALOR DA ARRECADACAO LIQUIDA DA RECEITA ORCAMENTARIA, POR MEIO DE DOCUMENTOS DE ARRECADAÇÃO.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2299, 2, '7.3.0.0.0.00.00.00.00.00', 'DÍVIDA ATIVA', 'Compreende o controle dos créditos a serem inscritos em dívida ativa, dos que se encontram em processamento. Compreende as contas que controlam os créditos passiveis de serem encaminhados e inscritos em dívida ativa o de inscrição e a tramitação dos créditos inscritos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2300, 2, '7.3.1.0.0.00.00.00.00.00', 'CONTROLE DO ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA', 'Compreende os valores passiveis de serem encaminhados e inscritos em dívida ativa.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2301, 2, '7.3.1.1.0.00.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA', 'Compreende o montante dos créditos passíveis de serem encaminhados para inscrição em Dívida Ativa.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2302, 2, '7.3.1.1.1.00.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA TRIBUTÁRIA', 'Compreende o montante dos créditos passíveis de serem encaminhados para inscrição em Dívida Ativa Tributária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2303, 2, '7.3.1.1.2.00.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA NÃO-TRIBUTÁRIA', 'Compreende o montante dos créditos a encaminhar e encaminhados para inscrição em Dívida Ativa Não-Tributária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2304, 2, '7.3.2.0.0.00.00.00.00.00', 'CONTROLE DA INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA', 'Compreende as contas que controlam os créditos passiveis de serem inscritos em dívida ativa pelo órgão responsável pela inscrição em dívida ativa.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2305, 2, '7.3.2.1.0.00.00.00.00.00', 'INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA', 'Compreende os valores dos créditos passíveis de serem inscritos em dívida ativa pelo órgão responsável pela inscrição em dívida ativa.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2306, 2, '7.3.2.1.1.00.00.00.00.00', 'INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA TRIBUTÁRIA', 'Compreende o valor dos créditos de origem tributária passíveis de serem inscritos em dívida ativa pelo órgão competente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2307, 2, '7.3.2.1.2.00.00.00.00.00', 'INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA NÃO-TRIBUTÁRIA', 'Compreende o valor dos créditos de natureza não-tributária passíveis de serem inscritos em dívida ativa pelo órgão competente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2308, 2, '7.4.0.0.0.00.00.00.00.00', 'RISCOS FISCAIS', 'Compreende as contas que controlam os riscos fiscais que não preencham os requisitos para reconhecimento como passivo, conforme identificados no anexo de riscos fiscais da lei de diretrizes orçamentárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2309, 2, '7.4.1.0.0.00.00.00.00.00', 'CONTROLE DE PASSIVOS CONTINGENTES', 'Compreende as contas que registram o controle dos riscos fiscais que não preencham os requisitos para reconhecimento como passivo classificados como passivos contingentes, conforme identificados no anexo de riscos fiscais da lei de diretrizes orçamentárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2310, 2, '7.4.1.1.0.00.00.00.00.00', 'PASSIVOS CONTINGENTES', 'Compreende os riscos fiscais classificados como Passivos Contingentes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (2311, 2, '7.9.1.2.0.00.00.00.00.00', 'RESPONSABILIDADE DE TERCEIROS POR VALORES, TÍTULOS E BENS', 'Compreende contas relacionadas ao registro da responsabilidade de terceiros com a administração pública por títulos, valores e bens.', 1, false, 0, 'N');
