insert into contabilidade.planocontadetalhe values (2379, 2, '1.2.1.2.1.99.07.00.00.00', '(-) AJUSTE DE PERDAS DE CRÉDITOS APURADOS EM DÍVIDA ATIVA NÃO TRIBUTÁRIA - DEMAIS CRÉDITOS', 'Registra os valores dos ajustes para perdas estimadas de créditos de dívida ativa não tributária, exceto as derivadas de clientes, em processo de inscrição e já inscritos, realizáveis após os doze meses seguintes a data de publicação dos demonstrativos contábeis.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2380, 2, '1.2.1.2.1.99.98.00.00.00', '(-) AJUSTE DE PERDAS DE CRÉDITOS APURADOS EM OUTROS CRÉDITOS A RECEBER E VALORES A LONGO PRAZO', 'Registra os valores dos ajustes para perdas estimadas de outros créditos e valores realizáveis após os 12 (doze) meses seguintes a data da publicação das demonstrações contábeis, provenientes de direitos obtidos junto a diversos devedores.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2381, 2, '1.2.1.3.1.99.01.00.00.00', '(-) AJUSTE DE PERDAS COM TÍTULOS E VALORES MOBILIÁRIOS ', 'Registra os valores de ajustes para perdas em títulos e valores mobiliários a longo prazo.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2383, 2, '1.2.1.3.1.99.02.00.00.00', '(-) AJUSTE DE PERDAS COM APLICAÇÕES TEMPORARIAS EM METAIS PRECIOSOS', 'Registra os valores de ajustes para perdas em aplicações em ouro e outros metais preciosos.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2384, 2, '1.2.1.3.1.99.03.00.00.00', '(-) AJUSTE DE PERDAS COM APLICAÇÕES EM SEGMENTO DE IMÓVEIS', 'Registra os valores de ajustes para perdas em investimentos de longo prazo realizados em segmento de imóveis, realizáveis no longo prazo.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2385, 2, '1.2.1.3.1.99.04.00.00.00', '(-) AJUSTE DE PERDAS DE FUNDOS AVALIADOS A VALOR DE MERCADO', 'Registra os valores de ajustes para perdas de fundos, quando avaliados a valor de mercado.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2386, 2, '1.2.1.4.1.99.00.00.00.00', '(-) AJUSTE DE PERDAS DE ESTOQUES', 'Registra o registro das perdas conhecidas em estoques e calculadas por estimativa, relativas a estoques deteriorados ou obsoletos e para dar cobertura a diferenças físicas, quando tais perdas não puderem ser baixadas das próprias contas, pelo fato de não estarem identificados os itens específicos e por constituírem estimativas, bem como o registro de perdas estimadas para os estoque que tiverem o valor de mercado inferior ao seu custo. Conta a ser utilizada apenas para os estoques classificados no ativo realizável a longo prazo.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2387, 2, '1.2.2.1.1.01.97.00.00.00', '(-) AMORTIZAÇÃO DE ÁGIO NA AQUISIÇÃO DE PARTICIPAÇÕES AVALIADAS PELO MEP', 'Registra as participações permanentes da unidade em outras entidades em forma de ações ou cotas, avaliadas pelo método de equivalência patrimonial - Amortização de Ágio na Aquisição de Participações.', 2, true, 2, 'P');
