insert into contabilidade.planocontadetalhe values (3592, 2, '8.9.1.2.1.05.00.00.00.00', 'ADIANTAMENTOS EM INDADIMPLENCIA', 'Registra os valores dos adiantamentos concedidos em inadimplência.', 2, true, 1, 'N');
