insert into contabilidade.planocontadetalhe values (2405, 2, '1.2.3.8.1.01.04.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE MATERIAIS CULTURAIS, EDUCACIONAIS E DE COMUNICAÇÃO', 'Registra a depreciação acumulada de materiais bibliográficos, discotecas, filmotecas, de comunicação, entre outros.', 2, true, 2, 'P');
