insert into contabilidade.planocontadetalhe values (2407, 2, '1.2.3.8.1.01.06.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE PEÇAS E CONJUNTOS DE REPOSIÇÃO', 'Registra a depreciação acumulada de peças e conjuntos de reposição destinados a substituição em máquinas e equipamentos, aeronaves e embarcações, bens de segurança e socorro, entre outros.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2409, 2, '1.2.3.8.1.01.08.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE BENS MÓVEIS EM ALMOXARIFADO', 'Registra a depreciação acumulada de bens permanentes a serem estocados em almoxarifado.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2410, 2, '1.2.3.8.1.01.09.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE ARMAMENTOS', 'Registra a depreciação acumulada de armas que constituem objetos de defesa ou preparativos de guerra.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2411, 2, '1.2.3.8.1.01.10.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE SEMOVENTES', 'Registra a depreciação acumulada de animais destinados a produção, reprodução, engorda e aos serviços utilitários em geral.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2412, 2, '1.2.3.8.1.01.99.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE DEMAIS BENS MÓVEIS', 'Registra a depreciação acumulada de demais bens móveis não classificados em grupos específicos.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2413, 2, '1.2.3.8.1.02.01.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE BENS DE USO ESPECIAL', 'Registra a depreciação acumulada de bens como edifícios ou terrenos destinados a serviço ou estabelecimento da administração estadual ou municipal, inclusive os de suas autarquias e fundações públicas.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2414, 2, '1.2.3.8.1.02.02.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE BENS DOMINICAIS', 'Registra a depreciação acumulada de bens que constituem o patrimônio das pessoas jurídicas de direito público, como objeto de direito pessoal, ou real, de cada uma dessas entidades. Compreende ainda, não dispondo a lei em contrário, os bens pertencentes as pessoas jurídicas de direito público a que se tenha dado estrutura de direito privado.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2415, 2, '1.2.3.8.1.02.03.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE BENS DE USO COMUM DO POVO', 'Registra a depreciação acumulada de bens de uso comum do povo construídos ou adquiridos por pessoas jurídicas de direito público.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2416, 2, '2.1.1.3.1.03.01.00.00.00', 'PRECATÓRIOS DE BENEFÍCIOS ASSISTENCIAIS - REGIME ESPECIAL - ANTES DE 05/05/2000 ', 'Registra as obrigações referentes a precatórios de benefícios assistenciais,  sujeitos ao regime especial instituído pela emenda constitucional 62/2009, anteriores a 05/05/2000 (Lei de Responsabilidade Fiscal).', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (2417, 2, '1.2.3.8.1.02.05.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE INSTALAÇÕES', 'Registra a depreciação acumulada de equipamentos, materiais e custo de implantação de instalações que, não obstante integradas aos edifícios, devem ser segregadas das obras civis, como, por exemplo, as instalações elétricas, hidráulicas, sanitárias, de vapor, de ar comprimido, frigoríficas, contra incêndio, de comunicações, de climatização, para combustíveis, gases, de antipoluição, para cozinha, dentre outros. E também aplicável a construção de trechos ferroviários.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2418, 2, '1.2.3.8.1.02.06.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE BENFEITORIAS EM PROPRIEDADE DE TERCEIROS', 'Registra a depreciação acumulada de construções em terrenos arrendados de terceiros e instalações e outras benfeitorias em prédios alugados. São considerados somente os gastos com construção e instalação que se incorporam ao imóvel e revertem ao proprietário do imóvel ao final da locação.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2419, 2, '1.2.3.8.1.02.99.00.00.00', '(-) DEPRECIAÇÃO ACUMULADA DE DEMAIS BENS IMÓVEIS', 'Registra a depreciação acumulada de os demais bens imóveis não classificados anteriormente nesse plano de contas.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2420, 2, '1.2.3.8.1.03.00.00.00.00', '(-) EXAUSTÃO ACUMULADA - BENS MÓVEIS', 'Registra a diminuição do valor dos elementos do ativo imobilizado, bens móveis, devido à perda do valor, decorrente de sua exploração, de direitos cujo objeto sejam recursos minerais ou florestais ou bens aplicados nessa exploração.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2421, 2, '1.2.3.8.1.04.00.00.00.00', '(-) EXAUSTÃO ACUMULADA - BENS IMÓVEIS', 'Registra a diminuição do valor dos elementos do ativo imobilizado, bens imóveis, devido à perda do valor, decorrente de sua exploração, de direitos cujo objeto sejam recursos minerais ou florestais ou bens aplicados nessa exploração.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2422, 2, '1.2.3.8.1.05.00.00.00.00', '(-) AMORTIZAÇÃO ACUMULADA - BENS MÓVEIS', 'Registra a diminuição do valor dos elementos do ativo imobilizado, bens móveis, devido à perda do valor do capital aplicado na aquisição de direitos de propriedade industrial ou comercial e quaisquer outros com existência ou exercício de duração limitada ou cujo objeto sejam bens de utilização por prazo legal ou contratualmente limitado.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2423, 2, '1.2.3.8.1.06.00.00.00.00', '(-) AMORTIZAÇÃO ACUMULADA - BENS IMÓVEIS', 'Registra a diminuição do valor dos elementos do ativo imobilizado, bens Imóveis, devido à perda do valor do capital aplicado na aquisição de direitos de propriedade industrial ou comercial e quaisquer outros com existência ou exercício de duração limitada ou cujo objeto sejam bens de utilização por prazo legal ou contratualmente limitado.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2424, 2, '1.2.4.8.1.01.00.00.00.00', '(-) AMORTIZAÇÃO ACUMULADA - SOFTWARES', 'Registra a diminuição do valor do capital aplicado em softwares, pertencentes ao ativo intangível.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2425, 2, '1.2.4.8.1.02.00.00.00.00', '(-) AMORTIZAÇÃO ACUMULADA - MARCAS, DIREITOS E PATENTES', 'Registra a diminuição do valor do capital aplicado em marcas, direitos e patentes, pertencentes ao ativo intangível.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2426, 2, '1.2.4.8.1.03.00.00.00.00', '(-) AMORTIZAÇÃO ACUMULADA - DIREITO DE USO DE IMÓVEIS', 'Registra a diminuição do valor do capital aplicado em direito de uso de imóveis, pertencentes ao ativo intangível.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2427, 2, '1.2.4.9.1.01.00.00.00.00', '(-) REDUÇÃO AO VALOR RECUPERÁVEL DE INTANGÍVEL - SOFTWARES', 'Registra os valores de ajuste ao valor recuperável do software pertencente ao ativo intangível, quando esse for inferior ao valor liquido contábil.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2428, 2, '1.2.4.9.1.02.00.00.00.00', '(-) REDUÇÃO AO VALOR RECUPERÁVEL DE INTANGÍVEL - MARCAS, DIREITOS E PATENTES', 'Registra os valores de ajuste ao valor recuperável das marcas, direitos e patentes, quando esse for inferior ao valor liquido contábil.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2429, 2, '1.2.4.9.1.03.00.00.00.00', '(-) REDUÇÃO AO VALOR RECUPERÁVEL DE INTANGÍVEL - DIREITO DE USO', 'Registra os valores de ajuste ao valor recuperável de direito de uso, quando esse for inferior ao valor liquido contábil.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2430, 2, '1.2.5.9.1.01.00.00.00.00', '(-) AMORTIZAÇÃO ACUMULADA - GASTOS DE IMPLANTAÇÃO E PRÉ-OPERACIONAIS', 'Registra a perda do valor do capital aplicado em despesas pré-operacionais.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2431, 2, '1.2.5.9.1.02.00.00.00.00', '(-) AMORTIZAÇÃO ACUMULADA - GASTOS DE REORGANIZAÇÃO', 'Registra a perda do valor do capital aplicado em gastos de reestruturação.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2432, 2, '2.1.1.1.1.01.01.00.00.00', 'SALÁRIOS, REMUNERAÇÕES E BENEFÍCIOS', 'Registra as obrigações referentes a salários ou remunerações, bem como benefícios aos quais o empregado ou servidor tenha direito.', 2, true, 2, 'X');
