insert into contabilidade.planocontadetalhe values (2434, 2, '2.1.1.1.1.01.03.00.00.00', 'FÉRIAS', 'Registra as obrigações referentes a férias, geralmente apropriadas como percentuais da folha de pagamento da unidade.', 2, true, 2, 'X');
