insert into contabilidade.planocontadetalhe values (2436, 2, '2.1.1.1.1.01.05.00.00.00', 'PARTICIPAÇÕES A EMPREGADOS', 'Registra os valores das obrigações constituídas para pagamento de participações a empregados.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (2437, 2, '2.1.1.2.2.07.00.00.00.00', 'APORTE PARA COBERTURA DE DÉFICIT ATUARIAL A PAGAR  ', 'Registra os valores devidos ao RPPS pelo ente a título de aporte para cobertura de déficit atuarial.', 2, true, 2, 'P');
