insert into contabilidade.planocontadetalhe values (2674, 2, '2.1.8.4.1.01.00.00.00.00', 'DEBÊNTURES CONVERSÍVEIS EM AÇÕES NO CURTO PRAZO', 'Registra os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do título e que são conversíveis em ações, com vencimento até o termino do exercício seguinte.', 2, true, 2, 'X');
