insert into contabilidade.planocontadetalhe values (2676, 2, '2.1.8.4.1.96.00.00.00.00', 'ÁGIO NA EMISSÃO DE DEBÊNTURES', 'Registra os valores de ágio na emissão de debentures, que são os valores recebidos que supera o de resgate desses títulos na data do próprio recebimento ou do valor formalmente atribuído as debentures.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (2677, 2, '2.1.8.4.1.99.00.00.00.00', 'OUTROS TÍTULOS', 'Registra outros títulos de dívida com vencimento até o termino do exercício seguinte.', 2, true, 2, 'X');
