insert into contabilidade.planocontadetalhe values (2679, 2, '2.1.8.6.1.02.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DA CONTRAPRESTAÇÃO DE SERVIÇOS EM CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger a contraprestação referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (2680, 2, '2.1.8.8.1.04.01.00.00.00', 'DEPÓSITOS E CAUÇÕES', 'Registra os valores das obrigações exigíveis contraídas com o recebimento de depósitos e/ou cauções vinculados a contratos ou outros instrumentos, para garantias de operações específicas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2681, 2, '2.1.8.8.1.01.01.00.00.00', 'RPPS - RETENÇÕES SOBRE VENCIMENTOS E VANTAGENS', 'Registra o valor das retenções procedidas em pagamentos de servidores a ser recolhido ao RPPS do servidor.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2682, 2, '2.1.8.8.1.01.02.00.00.00', 'INSS', 'Registra o valor das retenções procedidas em pagamentos de empregados regidos pela consolidação das leis do trabalho (CLT) ou de serviços de terceiros, a ser recolhido ao instituto nacional do seguro social/previdência social.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2683, 2, '2.1.8.8.1.01.03.00.00.00', ' ENCARGOS SOCIAIS - OUTRAS ENTIDADES', 'Registra o valor a ser recolhido a outras entidades, em função de dispositivos legais com as quais a entidade mantenha convenio de arrecadação direta. Exemplo: FNDE, INCRA, DPC, SENAI, SESI, SENAC, FAER, SEBRAE, SENAT, SESCOOP, SESC.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2684, 2, '2.1.8.8.1.01.04.00.00.00', 'IMPOSTO SOBRE A RENDA RETIDO NA FONTE - IRRF', 'Registra os valores referentes as retenções na fonte, pela entidade, do imposto sobre a renda, relativas as importâncias pagas a terceiros ou creditadas a servidores ou empregados, sobre as quais incida o referido imposto.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2685, 2, '2.1.8.8.1.01.05.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', 'Registra os valores relativos as indenizações e restituições de tributos com autorizações concedidas, porém não devolvidas aos legítimos credores pela entidade.', 2, true, 2, 'F');
