insert into contabilidade.planocontadetalhe values (2687, 2, '2.1.8.8.1.01.07.00.00.00', ' ICMS ', 'Registra os valores relativos ao imposto sobre a circulação de mercadorias e serviços retidos por força de legislação ou por acordo entre as partes envolvidas na transação em consignações.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2688, 2, '2.1.8.8.1.01.08.00.00.00', ' ISS ', 'Registra os valores do imposto sobre serviços retidos em consignações por forca de legislação, circunstancias ou acordo entre as partes envolvidos na transação, em situações que a entidade como parte contratante, atue como substituta tributária.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2689, 2, '2.1.8.8.1.01.09.00.00.00', 'OUTROS TRIBUTOS ESTADUAIS E MUNICIPAIS', 'Registra os valores em consignação relativos a outros tributos a serem repassados aos estados e municípios.', 2, true, 2, 'F');
