insert into contabilidade.planocontadetalhe values (2875, 2, '2.2.7.9.1.99.00.00.00.00', 'OUTRAS PROVISÕES A LONGO PRAZO', 'Registra os valores das provisões não classificáveis nas demais contas deste plano de contas.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (2876, 2, '2.2.8.6.1.01.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger a contraprestação referente à incorporação dos ativos.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (2877, 2, '2.2.8.6.2.02.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DA CONTRAPRESTAÇÃO DE SERVIÇOS EM CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger a contraprestação referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (2878, 2, '2.2.8.8.1.04.04.00.00.00', 'DEPÓSITOS DE VEICULOS ALIENÁVEIS', 'Registra o valor dos recebimentos provenientes da alienação de veículos removidos, recolhidos ou apreendidos de acordo com a resolução do Contran nº 178/2005, pendentes de transferências diversas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2879, 2, '2.2.8.8.1.01.01.00.00.00', 'RPPS - RETENÇÕES SOBRE VENCIMENTOS E VANTAGENS', 'Registra o valor das retenções procedidas em pagamentos de servidores a ser recolhido ao RPPS do servidor.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2880, 2, '2.2.8.8.1.01.02.00.00.00', 'INSS', 'Registra o valor das retenções procedidas em pagamentos de empregados regidos pela consolidação das leis do trabalho (CLT) ou de serviços de terceiros, a ser recolhido ao instituto nacional do seguro social/previdência social.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2881, 2, '2.2.8.8.1.01.03.00.00.00', ' ENCARGOS SOCIAIS - OUTRAS ENTIDADES', 'Registra o valor a ser recolhido a outras entidades, em função de dispositivos legais com as quais a entidade mantenha convenio de arrecadação direta. Exemplo: FNDE, INCRA, DPC, SENAI, SESI, SENAC, FAER, SEBRAE, SENAT, SESCOOP, SESC.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2882, 2, '2.2.8.8.1.01.04.00.00.00', 'IMPOSTO SOBRE A RENDA RETIDO NA FONTE - IRRF', 'Registra os valores referentes as retenções na fonte, pela entidade, do imposto sobre a renda, relativas as importâncias pagas a terceiros ou creditadas a servidores ou empregados, sobre as quais incida o referido imposto.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2883, 2, '2.2.8.8.1.01.05.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', 'Registra os valores relativos as indenizações e restituições de tributos com autorizações concedidas, porém não devolvidas aos legítimos credores pela entidade.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2884, 2, '2.2.8.8.1.01.06.00.00.00', 'IMPOSTOS E CONTRIBUIÇÕES DIVERSOS', 'Registra os valores referentes as retenções na fonte, pela entidade, de impostos e contribuições diversos, relativas as importâncias pagas a terceiros, sobre os quais incidam os referidos tributos.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2885, 2, '2.2.8.8.1.01.07.00.00.00', ' ICMS ', 'Registra os valores relativos ao imposto sobre a circulação de mercadorias e serviços retidos por foca de legislação ou por acordo entre as partes envolvidas na transação em consignações.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2886, 2, '2.2.8.8.1.01.08.00.00.00', ' ISS ', 'Registra os valores do imposto sobre serviços retidos em consignações por forca de legislação, circunstancias ou acordo entre as partes envolvidos na transação, em situações que a entidade como parte contratante, atue como substituta tributária.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2887, 2, '2.2.8.8.1.01.09.00.00.00', 'OUTROS TRIBUTOS ESTADUAIS E MUNICIPAIS', 'Registra os valores em consignação relativos a outros tributos a serem repassados aos estados e municípios.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2888, 2, '2.2.8.8.1.01.10.00.00.00', 'PENSÃO ALIMENTÍCIA', 'Registra os valores das retenções em folha procedidas em pagamentos a empregados ou a servidores, autorizados por esses ou por determinação judicial, a título de pensão alimentícia.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2889, 2, '2.2.8.8.1.01.11.00.00.00', 'PLANOS DE PREVIDÊNCIA E ASSISTÊNCIA MÉDICA', 'Registra os descontos efetuados em folhas de pagamento do pessoal para posterior recolhimento a favor de instituições de previdência e assistência medica.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2890, 2, '2.2.8.8.1.01.12.00.00.00', 'ASSISTÊNCIA À SAÙDE - ADMINISTRAÇÃO PRÓPRIA', 'Registra os valores das consignações retidos pela entidade, ou entregues a essa, que serão administrados pelo próprio órgão/entidade para assistência a saúde de seus servidores.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2891, 2, '2.2.8.8.1.01.13.00.00.00', 'RETENÇÕES - ENTIDADES REPRESENTATIVAS DE CLASSES', 'Registra os descontos efetuados em folhas de pagamento de pessoal para posterior recolhimento a favor de instituições representativas de classe.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2892, 2, '2.2.8.8.1.01.14.00.00.00', 'RETENÇÕES - PLANOS DE SEGUROS', 'Registra os valores das retenções efetuadas em folha de pagamento dos servidores referentes as contribuições a planos de seguro de vida em grupo.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2893, 2, '2.2.8.8.1.01.15.00.00.00', 'RETENÇÕES - EMPRÉSTIMOS E FINANCIAMENTOS', 'Registra os valores das retenções efetuadas em folha de pagamento dos servidores referentes a empréstimos e financiamentos concedidos por terceiros, bem como retenções de parcelas de consórcios e segurados do INSS.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2894, 2, '2.2.8.8.1.01.16.00.00.00', 'RETENÇÃO RELATIVA A VALE TRANSPORTE', 'Registra os valores das consignações retidas pela entidade a serem repassados para as empresas prestadoras dos serviços de fornecimento de vale-transporte. Trata-se dos valores referentes aos 6% instituídos pelo artigo 9º do decreto nº 95.247/1987 e suas alterações.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2895, 2, '2.2.8.8.1.01.17.00.00.00', 'RETENÇÃO RELATIVA A VALE ALIMENTAÇÃO', 'Registra os valores das consignações retidas pela entidade a serem repassados para as empresas prestadoras dos serviços de fornecimento de cartão-alimentação/refeição.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2896, 2, '2.2.8.8.1.01.99.00.00.00', 'OUTROS CONSIGNATÁRIOS', 'Registra os valores das consignações retidos pela entidade, ou entregues a essa, em confiança, por diversos contribuintes sujeitos a recolhimentos e ou pagamentos aos favorecidos.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2897, 2, '2.2.8.8.1.02.00.00.00.00', 'GARANTIAS', 'Registra a garantia do cumprimento de uma obrigação, que se efetiva com o oferecimento de bens ou recursos financeiros ou ainda retenções de um percentual do valor do contrato como garantia de cumprimento do mesmo.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2898, 2, '2.2.8.8.1.03.01.00.00.00', 'DEPÓSITOS RECEBIDOS POR DETERMINAÇÃO JUDICIAL', 'Registra os valores de obrigações, decorrentes de depósitos recebidos por determinação judicial.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2899, 2, '2.2.8.8.1.03.02.00.00.00', 'DEPÓSITOS PARA RECURSOS JUDICIAIS', 'Registra os valores relativos as obrigações contraídas por depósitos efetuados a favor da entidade destinados ao exercício de direito de recurso junto a justiça e encontrados vinculados em contas bancarias.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2900, 2, '2.2.8.8.1.04.01.00.00.00', 'DEPÓSITOS E CAUÇÕES', 'Registra os valores das obrigações exigíveis contraídas com o recebimento de depósitos e/ou cauções vinculados a contratos ou outros instrumentos, para garantias de operações especificas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2901, 2, '2.2.8.8.1.04.02.00.00.00', 'DEPÓSITOS ESPECIAIS', 'Registra o valor relativo a retenções de depósitos vinculados a restituir no curso do exercício seguinte a entidade transferidora, decorrente de contratos ou instrumentos congêneres, convenção, acordo ou ajuste.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2902, 2, '2.2.8.8.1.04.03.00.00.00', 'DEPÓSITOS DE TERCEIROS', 'Registra os valores relativos aos depósitos de terceiros de diversas origens, inclusive desconhecidas, não reclamados pelos credores.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2903, 2, '2.2.8.8.1.04.99.00.00.00', 'OUTROS DEPÓSITOS', 'Registra compromissos exigíveis até o termino do exercício seguinte, decorrentes de recebimentos a título de depósitos, para os quais não haja contas especificas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2904, 2, '2.2.8.8.1.99.00.00.00.00', 'OUTROS VALORES RESTITUÍVEIS', 'Registra outros valores restituíveis em posse da entidade, no papel de fiel depositária.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (2905, 2, '4.1.2.2.1.71.00.00.00.00', 'TAXA DE SEGURANÇA CONTRA INCÊNDIO', 'Registra as variações patrimoniais aumentativas com Taxa de Segurança Contra Incêndio.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2906, 2, '4.1.2.2.1.72.00.00.00.00', 'TAXA DE SEGURANÇA OSTENSIVA CONTRA DELITOS', 'Registra as variações patrimoniais aumentativas com Taxa de Segurança Ostensiva Contra Delitos.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2907, 2, '4.1.1.2.1.01.00.00.00.00', 'IMPOSTO S/ PROPRIEDADE TERRITORIAL RURAL', 'Registra a variação patrimonial aumentativa decorrente de imposto sobre propriedade territorial rural', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2908, 2, '4.1.1.2.1.02.00.00.00.00', 'IMPOSTO SOBRE A PROPRIEDADE PREDIAL E TERRITORIAL URBANA', 'Registra a variação patrimonial aumentativa decorrente de imposto sobre a propriedade predial e territorial urbana.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2909, 2, '4.1.1.2.1.03.01.00.00.00', 'IR - PESSOAS FÍSICAS', 'Registra o valor total que incide sobre os rendimentos e ganhos de capital percebidos pelas pessoas físicas residentes ou domiciliadas no Brasil. Integram o rendimento bruto sujeito à incidência desse imposto o ganho de capital decorrente da alienação de bens e direitos e os ganhos líquidos auferidos em operações realizadas nas bolsas de valores e assemelhadas.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2910, 2, '4.1.1.2.1.03.02.00.00.00', 'IR - PESSOAS JURÍDICAS', 'Registra a variação patrimonial aumentativa decorrente de imposto sobre a renda de pessoas jurídicas. ', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2912, 2, '4.1.2.1.1.03.00.00.00.00', 'TAXA DE LICENCA PARA FUNCIONAMENTO DE ESTABELECIMENTOS COMERCIAIS,  INDUSTRIAIS E PRESTADORA DE SERVIÇOS', 'Registra o valor da VPA de taxa de fiscalização da localização de estabelecimentos comerciais, industrias e de prestação de serviços, instituída pela legislação do uso e ocupação do solo urbano às posturas municipais relativas à segurança, ordem e tranqüilidade públicas. Ex: taxa de fiscalização ou vistoria em estabelecimentos de qualquer natureza.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2913, 2, '4.1.2.1.1.04.00.00.00.00', 'TAXA DE PUBLICIDADE COMERCIAL', 'Registra o valor da VPA de receita de taxa de utilização de espaço público destinado para divulgação de produtos do comércio.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2914, 2, '4.1.2.1.1.05.00.00.00.00', 'TAXA DE APREENSAO E DEPOSITO', 'Registra o valor da VPA  de taxa de apreensão e depósito de bens oriundos de irregularidades apresentadas quando da fiscalização.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2915, 2, '4.1.2.1.1.06.00.00.00.00', 'TAXA DE FUNCIONAMENTO DE ESTABELECIMENTOS EM HORARIO ESPECIAL', 'Registra o valor da VPA de taxa de funcionamento de estabelecimentos em horário especial. A respectiva taxa é destinada a bares, boates, barracas por ocasião de festas carnavalescas, podendo funcionar a qualquer hora.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2916, 2, '4.1.2.1.1.07.00.00.00.00', 'TAXA DE LICENCA PARA EXECUCAO DE OBRAS', 'Registra o valor da VPA de taxa de licença para execução de obras. A respectiva taxa é cobrada do responsável pela execução das obras em observância aos critérios descritos em lei.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2917, 2, '4.1.2.1.1.08.00.00.00.00', 'TAXA DE AUTORIZACAO DE FUNCIONAMENTO DE TRANSPORTE', 'Registra o valor da VPA de taxa de autorização de funcionamento de transporte, caracterizada pela autorização aos proprietários de veículos que prestam serviços à comunidade de transporte de passageiros tais como: transporte escolar, de funcionários e outros passageiros. Ex: taxa de fiscalização de veículos de uso coletivo', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2918, 2, '4.1.2.1.1.09.00.00.00.00', 'TAXA DE UTILIZACAO DE AREA DE DOMINIO PUBLICO', 'Registra o valor da VPA de taxa de utilização de área de domínio público, referente a espaço ocupado por balcões, barracas, mesas, tabuleiros e semelhantes, nas feiras, vias e logradouros públicos ou como depósito de materiais ou estacionamento de veículos em locais designados pela prefeitura.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2919, 2, '4.1.2.1.1.10.00.00.00.00', 'TAXA DE APROVACAO DO PROJETO DE CONSTRUCAO CIVIL', 'Registra o valor da VPA da taxa de licença e aprovação do projeto de construção civil correspondente a certificação das condições de utilização de prédios e residências.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2920, 2, '4.1.2.1.1.11.01.00.00.00', 'TAXA DE FISCALIZAÇÃO DE VIGILÂNCIA SANITÁRIA - SAÚDE', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxa de Fiscalização de Vigilância Sanitária - Saúde.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2921, 2, '4.1.2.1.1.11.02.00.00.00', 'TAXA DE FISCALIZAÇÃO DE VIGILÂNCIA SANITÁRIA - ANIMAL', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxa de Fiscalização de Vigilância Sanitária - Animal.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2922, 2, '4.1.2.2.1.07.00.00.00.00', 'EMOLUMENTOS E CUSTAS JUDICIAIS', 'Registra as variações patrimoniais aumentativas com Emolumentos e Custas Judiciais.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2923, 2, '4.1.2.1.1.12.00.00.00.00', 'TAXA DE ALINHAMENTO E NIVELAMENTO', 'Registra o valor da VPA da taxa de fiscalização da metragem no tocante ao alinhamento e nivelamento de áreas ou terrenos.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2924, 2, '4.1.2.1.1.13.00.00.00.00', 'TAXA DE APREENSAO, DEPOSITO OU LIBERACAO DE ANIMAIS', 'Registra o valor da VPA da taxa de apreensão, depósito ou liberação de animais recolhidos em ruas em função de riscos causados à população.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2949, 2, '4.1.3.4.1.01.00.00.00.00', 'CONTRIBUIÇÃO DE MELHORIA PELA PAVIMENTAÇÃO E OBRAS COMPLEMENTARES', 'Registra a variação patrimonial aumentativa decorrente de contribuição de melhoria pela pavimentação e obras complementares.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2925, 2, '4.1.2.1.1.14.00.00.00.00', 'TAXA DE CONTROLE E FISCALIZAÇÃO AMBIENTAL', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxa de Controle e Fiscalização Ambiental.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2926, 2, '4.1.2.1.1.15.01.00.00.00', 'ATOS DA ADMINISTRAÇÃO EM GERAL', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxas de Serviços de Atos Administração em Geral.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2927, 2, '4.1.2.1.1.15.02.00.00.00', 'ATOS DA SAÚDE PÚBLICA', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxas de Serviços de Atos da Saúde Pública.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2928, 2, '4.1.2.1.1.70.00.00.00.00', 'TAXA DE PREVENÇÃO CONTRA SINISTRO', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxas de Prevenção Contra Sinistro.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2929, 2, '4.1.2.1.1.71.00.00.00.00', 'TAXA DE FISCALIZAÇÃO SOBRE SERVIÇOS PÚBLICOS DE SANEAMENTO BÁSICO - AGESAN', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxas de Fiscalização sobre Serviços Públicos de Saneamento Básico.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2930, 2, '4.1.2.1.1.72.00.00.00.00', 'TAXA DE REGULAÇÃO DE SERVIÇOS DE GÁS CANALIZADO', 'Registra as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxas de Regulação de Serviços de Gás Canalizado.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (2932, 2, '4.1.2.2.1.02.00.00.00.00', 'TAXAS DE SERVICOS CADASTRAIS', 'Registra a VPA proveniente das taxas de serviços  cadastrais, tendo como fato gerador a  apuração, inscrição e cobrança da divida ativa, da taxa de serviços cadastrais.                                ', 2, true, 2, 'N');
