insert into contabilidade.planocontadetalhe values (3002, 2, '4.3.3.1.1.22.00.00.00.00', 'SERVICOS DE HOSPEDAGEM E ALIMENTAÇÃO', 'Registra as variações patrimoniais aumentativas provenientes de serviços de hospedagem e alimentação prestados. VPA proporcionada por hospedagem, com ou sem alimentação, fornecimento de refeições, lanches e bebidas para consumo imediato. Excluem as VPA provenientes de empresas fornecedoras de alimentos preparados para hospitais, fabricas, etc. Que se classificam em VPA de produtos alimentares.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3003, 2, '4.3.3.1.1.23.00.00.00.00', 'CONCESSAO E PERMISSAO - SERV. DE TRANSPORT', 'Registra as variações patrimoniais aumentativas associadas a outorga dos serviços públicos de transporte a iniciativa privada, proveniente da atuação da fiscalização, da normatização e do controle dos serviços concedidos.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3004, 2, '4.3.3.1.1.24.00.00.00.00', 'OUTORGA SERV. EXPL. PROD PETR. GAS NATURAL', 'Registra as variações patrimoniais aumentativas provenientes de atividades de exploração, desenvolvimento e produção de petróleo e gás natural, exercidas mediante contratos de concessão.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3005, 2, '4.3.3.1.1.25.00.00.00.00', 'OUTORGA DE DIREITOS DE USO DE REC. HIDR.', 'Registra as variações patrimoniais aumentativas provenientes de outorga a particulares de direitos de uso da agua.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3006, 2, '4.3.3.1.1.26.00.00.00.00', 'OUTORGA DIREITOS DE EXPL. E PESQ. MINER', 'Registra as variações patrimoniais aumentativas provenientes da compensação financeira pela exploração de recursos minerais devido por todas as empresas que realizam o aproveitamento de uma jazida mineral garantida a essas a propriedade do produto da lavra.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3007, 2, '4.3.3.1.1.27.00.00.00.00', ' CONCESSÃO FLORESTAL', 'Registra as variações patrimoniais aumentativas provenientes de atividades de exploração e gestão de direitos de uso de florestas públicas, recursos florestais, produtos e serviços delas decorrentes, para produção sustentável, exercidas mediante contratos de concessão, a particulares que estejam em condições de atender as exigências do respectivo edital de licitação e que demonstrem capacidade para seu desempenho, por sua conta e risco e prazo determinado.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3008, 2, '4.3.3.1.1.28.00.00.00.00', 'CONCESSÃO E PERMISSÃO - DIR. USO DE BENS PÚBLICOS', 'Registra as variações patrimoniais aumentativas provenientes da utilização e exploração de bens públicos por terceiros.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3009, 2, '4.3.3.1.1.29.00.00.00.00', 'COMPENSAÇÕES FINANCEIRAS', 'Registra as variações patrimoniais aumentativas provenientes dos recursos decorrentes do artigo 20, parágrafo 1º da constituição federal, o qual dispõe: "e assegurada, nos termos da lei, aos estados, ao distrito federal e aos municípios, bem como aos órgãos da administração direta da União, participação no resultado da exploração de petróleo ou gás natural, de recursos hídricos para fins de geração de energia elétrica e de outros recursos minerais no respectivo território, plataforma continental, mar territorial ou zona economia exclusiva, ou compensação financeira por essa exploração".', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3010, 2, '4.3.3.1.1.30.00.00.00.00', 'SERVICOS DE TRANSPORTE', 'Registra as variações patrimoniais aumentativas provenientes de serviços de transporte rodoviário, ferroviário, hidroviário, aéreo, especiais e tarifa de pedágio.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3011, 2, '4.3.3.1.1.31.00.00.00.00', 'SERVICOS PORTUARIOS', 'Registra as variações patrimoniais aumentativas provenientes de serviços portuários. Abrangem os recursos oriundos da exploração dos portos, terminais marítimos, atracadouros e ancoradouros, referentes a estiva, desestiva, dragagem, atracação, sinalização, comunicação náutica, docagem, etc....', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3012, 2, '4.6.5.3.1.01.00.00.00.00', 'REVERSÃO DE REDUÇÃO A VALOR RECUPERÁVEL DE SOFTWARES', 'Registra a variação patrimonial aumentativa com a reversão de redução a valor recuperável de serviços de softwares.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3013, 2, '4.3.3.1.1.32.00.00.00.00', 'SERVICOS DE SOCORRO MARITIMO', 'Registra as variações patrimoniais aumentativas provenientes de serviço de socorro marítimo prestado. VPA de serviços de salvamento, por navio de socorro ou equipe de salvamento da marinha, de embarcação e cargas perigosas (desencalhe, mergulho, outros socorros), bem como serviços de reboque marítimo, dentre outros.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3107, 2, '4.5.2.3.4.01.00.00.00.00', 'CONTRIBUIÇÕES', 'Registra a variação patrimonial aumentativa decorrente de transferências correntes e de capital aos entes da Federação e a entidades privadas sem fins lucrativos, exceto para os serviços essenciais de assistência social, médica e educacional;', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3014, 2, '4.3.3.1.1.33.00.00.00.00', 'SERVICOS DE SAUDE', 'Registra as variações patrimoniais aumentativas provenientes da prestação de serviços de saúde: hospitalares gerais ou especializados, maternidade, centro de reabilitação, assistência médico-odontológica (inclusive ambulatorial), saúde pública, etc.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3015, 2, '4.3.3.1.1.34.00.00.00.00', 'CERTIFICAÇÃO E HOMOLOGAÇÃO DE PROD. TELECOM.', 'Registra as variações patrimoniais aumentativas provenientes de certificação e homologação de prod. Telecom.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3016, 2, '4.3.3.1.1.35.00.00.00.00', 'SERVIÇOS DE METROLOGIA E CERTIFICAÇÃO', 'Registra as variações patrimoniais aumentativas provenientes de serviços de metrologia e certificação prestados. VPA de serviços metrológicos em geral, tais como aferição de medidas e instrumentos de medir, serviços de arqueação de tanques para armazenagem, etc.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3017, 2, '4.3.3.1.1.36.00.00.00.00', 'SERVIÇOS DE INSPEÇÃO E FISCALIZAÇÃO', 'Registra as variações patrimoniais aumentativas provenientes de serviços de inspeção e fiscalização prestados. VPA proporcionada pela constatação das condições higiênico-sanitárias e técnicas de produtos ou estabelecimentos, ou resultantes de ação externa e direta dos órgãos do poder público destinada a verificação do cumprimento da legislação.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3018, 2, '4.3.3.1.1.37.00.00.00.00', 'SERVIÇOS ADMINISTRATIVOS', 'Registra as variações patrimoniais aumentativas provenientes de serviços administrativos prestados, das atividades de apoio administrativo executadas em organizações de qualquer natureza, tais como: a) taxas de expedição de certificados; b) taxas de registro, renovação, vistoria, licença, cadastramento, etc. C) datilografia, microfilmagem, copias xerográficas, heliográficas, fotostáticas, etc. d) taxas de inscrição em concursos. e) taxa de administração de serviços. F) venda de editais.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3019, 2, '4.3.3.1.1.38.00.00.00.00', 'SERVIÇOS DE METEOROLOGIA', 'Registra as variações patrimoniais aumentativas provenientes de serviços de meteorologia prestados. VPA proporcionada pelo fornecimento de dados meteorológicos e de pareceres técnicos, bem como conserto, comparação e aferição de equipamentos de meteorologia.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3020, 2, '4.4.2.4.1.13.00.00.00.00', 'MULTAS E JUROS DEMAIS CONTRIBUIÇÕES', 'Registra os valores relativos aos créditos aos créditos a receber, decorrentes da cobrança das demais contribuições.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3021, 2, '4.3.3.1.1.39.00.00.00.00', 'SERVIÇOS EDUCACIONAIS', 'Registra as variações patrimoniais aumentativas provenientes das atividades do sistema educacional, cuja natureza esteja diretamente relacionada a formação do educando (matriculas, anuidades, etc.). As VPA de atividades auxiliares, de apoio ou derivadas dos serviços educacionais propriamente ditos, devem ser classificadas nos títulos apropriados. Exemplos: matriculas e anuidades, serviços educacionais, taxas de expedição de documentos, copias xerográficas, heliográficas, etc, serviço de credenciamento, autorização e reconhecimento de cursos.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3022, 2, '4.3.3.1.1.40.00.00.00.00', 'SERVIÇOS AGROPECUÁRIOS', 'Registra as variações patrimoniais aumentativas provenientes de serviços agropecuário: a) preparo e correção de solos (destocamento, aração, gradeação, calagem, adubação, drenagem, irrigação, capina, desmatamento, etc.); b) semeadura e plantio; c) combate a pragas (pulverização, dedetização); d)colheita e preparação de produtos agrícolas; e)inseminação artificial; f)incubação de ovos; g)vacinação de animais; h) outros serviços auxiliares de agricultura e pecuária.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3023, 2, '4.3.3.1.1.41.00.00.00.00', 'SERVIÇOS DE REPARAÇÃO, MANUTENÇÃO E INSTALAÇÃO', 'Registra as variações patrimoniais aumentativas provenientes de serviços de reparação, manutenção e instalação prestados. VPA de serviços de reparação de artefatos de metal; reparação e manutenção de maquinas e aparelhos de uso doméstico; reparação, manutenção e instalação de maquinas e aparelhos elétricos e eletrônicos e de comunicação; reparação e manutenção de instalações elétricas, de gás, de agua, etc. incluem-se também, nesse título, os serviços de confecção sob medida. Não são considerados nesse título, classificando-se em VPA industrial: reparação e manutenção de veículos ferroviários, embarcações e aeronaves (indústria de material de transporte).', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3024, 2, '4.3.3.1.1.42.00.00.00.00', 'SERVIÇOS RECREATIVOS E CULTURAIS', 'Registra as variações patrimoniais aumentativas provenientes de serviços recreativos e culturais prestados. VPA proporcionada pela exploração de instalações para recreação, pratica desportiva e cultural (cinemas, teatros, salões para recitais, concertos, conferencias, planetários, estádios desportivos, autódromos, museus, bibliotecas, promoção e/ou produção de espetáculos artísticos culturais e esportivos).', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3025, 2, '4.3.3.1.1.44.00.00.00.00', 'SERVIÇOS DE REGISTRO DO COMÉRCIO', 'Registra as variações patrimoniais aumentativas provenientes de serviços de registro do comercio prestados. VPA advinda da prestação de serviços de registro do comercio e atividades afins, tratando-se de: a) serviços prestados pela junta comercial do distrito federal; b) serviços constantes do cadastro nacional de empresa, devidos no âmbito das juntas comerciais dos estados e do distrito federal; c) remuneração de serviços prestados pelo departamento nacional de registro do comercio.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3026, 2, '4.3.3.1.1.45.00.00.00.00', 'SERVIÇOS DE FORNECIMENTO DE ÁGUA', 'Registra as variações patrimoniais aumentativas provenientes de serviços prestados de fornecimento de agua aos irrigantes aa amortização dos investimentos da infraestrutura de irrigação dos projetos públicos, conforme dec.89.496, de 29.03de 84, relativos a tarifas k-1 e k-2.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3027, 2, '4.3.3.1.1.46.00.00.00.00', 'SERVIÇOS DE PERFURAÇÃO E INSTALAÇÃO DE POÇOS', 'Registra as variações patrimoniais aumentativas provenientes de serviços prestados de perfuração e instalação de poços tubulares profundos, poços artesianos ou similares.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3028, 2, '4.3.3.1.1.47.00.00.00.00', 'SERVIÇOS DE GEOPROCESSAMENTO', 'Registra as variações patrimoniais aumentativas provenientes de serviços prestados a terceiros de interpretação de imagem aerofotogrametrica, execução de mapas digitalizados diversos, venda de cartas e imagens de satélites, videográficas e similares.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3029, 2, '4.3.3.1.1.48.00.00.00.00', 'SERVIÇOS DE CADASTRAMENTO DE FORNECEDORES', 'Registra as variações patrimoniais aumentativas provenientes de prestação de serviços de cadastramento de empresas fornecedoras de bens e serviços ao governo federal.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3030, 2, '4.3.3.1.1.52.00.00.00.00', 'TAR.INSCR.CONCURSOS E PROCESSOS SELETIVOS', 'Registra as variações patrimoniais aumentativas provenientes de tarifas de inscrição em concursos e processos seletivos.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3031, 2, '4.3.3.1.1.54.00.00.00.00', 'GARANTIAS E AVAIS', 'Registra as variações patrimoniais aumentativas provenientes da prestação de garantias e avais nas operações de credito. Fato gerador: concessão de garantias e avais, de natureza não-financeiras.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3108, 2, '4.5.2.3.4.02.00.00.00.00', 'AUXÍLIOS', 'Registra a variação patrimonial aumentativa decorrente de transferências de capital aos entes da Federação e a entidades privadas sem fins lucrativos;', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3032, 2, '4.3.3.1.1.55.00.00.00.00', 'CREDENCIAMENTO EMP.PREST.SERVICOS DE VISTORIA', 'Registra as variações patrimoniais aumentativas provenientes do valor a ser pago por empresas na obtenção de credenciamento para prestação de serviços de vistoria. - Destinação legal: órgão arrecadador; - amparo legal: resolução CONTRAN 282, de 26 de junho de 2008.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3033, 2, '4.3.3.1.1.56.00.00.00.00', 'SERVICOS VETERINARIOS', 'Registra as variações patrimoniais aumentativas provenientes de serviços veterinários.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3034, 2, '4.3.3.1.1.59.00.00.00.00', 'SERVICOS FINANCEIROS', 'Registra as variações patrimoniais aumentativas com serviços financeiros', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3035, 2, '4.3.3.1.1.60.00.00.00.00', 'SERV. COMERCIAIS DE PRODUTOS, DADOS E MAT. INFORMÁTICA', 'Registra as variações patrimoniais aumentativas provenientes da comercialização de produtos, dados e materiais de informática, tais como disquetes, softwares, programas, CD-Rom, fitas magnéticas e assemelhados, bem como informações em redes e sistemas de dados disponíveis em meio de hardware.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3036, 2, '4.3.3.1.1.61.00.00.00.00', 'SERVIÇOS DE TURISMO', 'Registra as variações patrimoniais aumentativas com serviços de turismo.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3037, 2, '4.3.3.1.1.99.00.00.00.00', 'OUTRAS VARIAÇÕES PATRIMONIAIS AUMENTATIVAS PROVENIENTES DE PRESTAÇÃO DE SERVIÇOS', 'Registra as variações patrimoniais aumentativas provenientes da prestação de outros serviços não enquadrados nos itens anteriores.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3038, 2, '4.4.2.4.1.14.00.00.00.00', 'MULTAS E JUROS CONTRIBUIÇÃO PARA O CUSTEIO DO SERVIÇO DE ILUMINAÇÃO PÚBLICA', 'Registra os valores relativos aos créditos a receber dos Municípios e o Distrito Federal que instituirem contribuição, na forma das respectivas leis, para o custeio do serviço de iluminação pública, conforme artigo 149-A da Constituição Federal de 1988. ', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3039, 2, '4.4.2.3.1.05.00.00.00.00', 'JUROS E ENCARGOS DE MORA DA DIVIDA ATIVA DO PROGRAMA TROCA-TROCA', 'Registra o valor da arrecadação de VPA de multas e juros de mora da dívida ativa do programa troca-troca.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3040, 2, '4.4.2.3.1.06.00.00.00.00', 'MULTAS E JUROS DE MORA DA DIVIDA ATIVA DA PRESTACAO DE SERVICOS', 'Registra o valor da arrecadação de VPA de multas e juros de mora da dívida ativa da prestação de serviços.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3041, 2, '4.4.2.3.1.07.00.00.00.00', 'MULTAS E JUROS DE MORA DA DIVIDA ATIVA DOS AUTOS DE INFRACAO', 'Registra o valor da arrecadação de VPA de multas e juros de mora da dívida ativa dos autos de infração.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3042, 2, '4.4.2.3.1.99.00.00.00.00', 'OUTROS JUROS E ENCARGOS DE MORA SOBRE FORNECIMENTO DE BENS E SERVICOS', 'Registra a variação patrimonial aumentativa proveniente de outros juros e encargos de mora sobre fornecimento de bens e servicos', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3043, 2, '4.4.2.4.1.01.00.00.00.00', 'MULTAS E JUROS SOBRE IMPOSTO SOBRE A RENDA E PROVENTOS DE QUALQUER NATUREZA', 'Registra os valores relativos a créditos a receber, decorrentes da cobrança do Imposto sobre a Renda e Proventos de Qualquer Natureza retidos na fonte.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3044, 2, '4.4.2.4.1.05.00.00.00.00', 'MULTAS E JUROS SOBRE IPTU', 'Registra os valores relativos a créditos a receber, decorrentes da cobrança do Imposto sobre a Propriedade Predial e Territorial Urbana.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3045, 2, '4.4.2.4.1.06.00.00.00.00', 'MULTAS E JUROS SOBRE ITBI', 'Registra os valores relativos a créditos a receber, decorrentes da cobrança do Imposto sobre Transmissão "Inter Vivos" de Bens Imóveis e de Direitos Reais sobre Imóveis', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3046, 2, '4.4.2.4.1.07.00.00.00.00', 'MULTAS E JUROS SOBRE ISS', 'Registra os valores relativos aos créditos a receber, decorrentes do Imposto Sobre Serviços de Qualquer Natureza.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3047, 2, '4.4.2.4.1.08.00.00.00.00', 'MULTAS E JUROS SOBRE ITR', 'Registra os valores relativos aos créditos a receber, decorrentes do Imposto Sobre a Propriedade Territorial Rural, devidos aos municípios ou ao Distrito Federal devido a convênios firmados com a União, conforme inciso III, parágrafo 4º do artigo 153 da Constituição Federal.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3048, 2, '4.4.2.4.1.09.00.00.00.00', 'MULTAS E JUROS SOBRE TAXAS PELO EXERCÍCIO DO PODER DE POLÍCIA', 'Registra os valores relativos aos créditos a receber, decorrentes da cobrança das taxas pelo exercício do poder de polícia.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3049, 2, '4.4.2.4.1.10.00.00.00.00', 'MULTAS E JUROS TAXAS PELA PRESTAÇÃO DE SERVIÇOS', 'Registra os valores relativos aos créditos a receber, decorrentes da cobrança das taxas pela prestação de serviços.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3050, 2, '4.4.2.4.1.11.00.00.00.00', 'MULTAS E JUROS CONTRIBUIÇÕES DE MELHORIA', 'Registra os valores relativos aos créditos a receber, decorrentes da cobrança dos valores decorrentes da contribuição instituída para fazer face ao custo de obras públicas de que decorra valorização imobiliária.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3051, 2, '4.4.2.4.1.15.00.00.00.00', 'MULTAS E JUROS CRÉDITOS TRIBUTARIOS PARCELADOS', 'Registra os valores relativos aos créditos aos créditos a receber, decorrentes de Créditos Tributários Parcelados.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3052, 2, '4.4.2.4.1.16.00.00.00.00', 'MULTAS E JUROS DE DÍVIDA ATIVA TRIBUTÁRIA', 'Registra os valores de multas e juros de dívida ativa tributária.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3053, 2, '4.4.2.4.1.99.00.00.00.00', 'JUROS E ENCARGOS DE MORA SOBRE OUTROS CRÉDITOS TRIBUTÁRIOS', 'Registra os juros e encargos de mora sobre outros créditos tributários.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3054, 2, '4.4.5.1.1.01.00.00.00.00', 'REMUNERAÇÃO DE DEPÓSITOS DE RECURSOS VINCULADOS', 'Registra o valor da VPA proveniente da remuneração de depositos bancarios de recursos vinculados, tais como: royalties, FUNDEB, fundo de saúde, regime próprio de previdência social dos servidores, manutenção e desenvolvimento do ensino, ações e serviços públicos de saúde (ASPS), fundetur, etc. da remuneração de aplicações financeiras de recursos vinculados, tais como: royalties, FUNDEB, fundo de saúde, regime próprio de previdência social dos servidores, manutenção e desenvolvimento do ensino, ASPS, etc.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (3055, 2, '4.4.5.1.1.02.00.00.00.00', 'REMUNERAÇÃO DE DEPÓSITOS DE RECURSOS NÃO VINCULADOS', 'Registra o valor da VPA proveniente da remuneração de depósitos bancários de recursos não vinculados, inclusive recursos próprios. ', 2, true, 2, 'N');
