insert into contabilidade.planocontadetalhe values (3525, 2, '8.3.2.5.2.01.00.00.00.00', 'BAIXAS DE CRÉDITOS INSCRITOS EM DÍVIDA ATIVA NÃO-TRIBUTÁRIA EM COBRANÇA ADMNISTRATIVA', 'Registra as baixas de créditos inscritos em dívida ativa não-tributária, em cobrança administrativa.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3526, 2, '8.3.2.5.2.02.00.00.00.00', 'BAIXAS DE CRÉDITOS INSCRITOS EM DÍVIDA ATIVA NÃO-TRIBUTÁRIA EM COBRANÇA JUDICIAL', 'Registra as baixas de créditos inscritos em dívida ativa não-tributária, em cobrança judicial.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3542, 2, '8.4.2.1.3.00.00.00.00.00', 'DISCREPÂNCIA DE PROJEÇÕES', 'Registra a estimativa do montante de redução no valor dos ingressos ou de aumento no valor dos desembolsos que apresentam probabilidade de vir a ocorrer no exercício, decorrentes de evolução desfavorável de indicadores econômicos empregados na época da elaboração do Orçamento, tais como:a) Taxa de crescimento econômico;b) Taxa de inflação;c) Taxa de câmbio;d) Taxa de juros;e) Salário mínimo;f) Outros indicadores.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3527, 2, '8.4.1.1.1.00.00.00.00.00', 'DEMANDAS JUDICIAIS', 'Registra a estimativa do montante relativo a ações judiciais em andamento contra o ente federativo nas quais haja a probabilidade de que o ganho de causa venha a ser da outra parte. Ao fazer a estimativa, considerar os principais tipos de ação judicial, tais como:a) Demandas judiciais contra a atividade reguladora do Estado: Indexação e controles de preços; De ordem tributária e previdenciária; Outras demandas judiciais;b) Demandas judiciais contra empresas estatais dependentes;c) Demandas judiciais contra a administração direta e indireta: Privatizações, liquidação ou extinção de órgãos ou empresas; Reajustes salariais;d) Demandas trabalhistas contra o ente federativo;e) Outras demandas judiciais.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3528, 2, '8.4.1.1.2.00.00.00.00.00', 'DÍVIDAS EM PROCESSO DE RECONHECIMENTO', 'Registra as Dívidas ainda não assumidas formalmente que apresentam probabilidade de serem incorporadas ao passivo devido, por exemplo, a decisões judiciais.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3529, 2, '8.4.1.1.3.00.00.00.00.00', 'AVAIS E GARANTIAS CONCEDIDAS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser gasto pelo ente federativo para honrar fianças e avais concedidos em operações de crédito direta ou indiretamente pelo ente federativo a favor de outros entes federativos e entidades dos setores público e privado, inclusive com recursos de fundos de aval, a assunção de risco creditício em linhas de crédito, o seguro de crédito à exportação e outras garantias de natureza semelhante que representem compromisso de adimplência de obrigação financeira ou contratual.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3530, 2, '8.4.1.1.4.00.00.00.00.00', 'ASSUNÇÃO DE PASSIVOS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser gasto pelo ente federativo com o objetivo de proteger o cidadão ou sistemas importantes do Mercado contra inadimplências, falências, altos prejuízos, ou garantir a credibilidade desses sistemas frente a fatores agudos adversos. Ao fazer a estimativa, considerar os principais tipos de situações que podem advir:a) de órgãos da administração direta ou indireta;b) de entidades privadas;c) de Regime Próprio de Previdência Social dos Servidores Públicos;d) de Entes da Federação;e) do Regime Geral de Previdência Social;f) do Banco Central; eg) da assunção de outros passivos.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3531, 2, '8.4.1.1.5.00.00.00.00.00', 'ASSISTÊNCIAS DIVERSAS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo com o objetivo de fazer frente a calamidades públicas e que, por não serem recorrentes, não foram planejados. Recomenda-se que o ente federativo explicite o tipo de assistência emergencial que ele está prevendo, por exemplo, se é assistência contra seca, enchentes, catástrofes, epidemias etc.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3532, 2, '8.4.1.1.9.00.00.00.00.00', 'OUTROS PASSIVOS CONTINGENTES', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo para fazer frente a outros tipos de passivos contingentes.', 2, true, 1, 'N');
