insert into contabilidade.planocontadetalhe values (3564, 2, '8.5.3.2.4.04.00.00.00.00', 'INFORMAÇÃO DA EXECUÇÃO DO CONSORCIO PARA CONSOLIDAÇÃO - DESPESA COM MDE', 'REGISTRA A INFORMAÇÃO ENVIADA PELO CONSORCIO REFERENTE A APLICAÇÃO DAS TRANSFERENCIAS RECEBIDAS DO ENTE CONSORCIADO PARA PAGAMENTO DE DESPESA COM MANUTENÇÃO E DESENVOLVIMENTO DO ENSINO - MDE. ESTA CONTA DEVERÁ SER ACUMULADA NOS BIMESTRES E TRAZER APENAS A APLICAÇÃO EM EDUCAÇÃO PARA FINS DE CONSOLIDAÇÃO DO MÍNIMO CONSTITUCIONAL, OU SEJA TRANSFERÊNCIAS AO CONSÓRCIO COM RECURSO VINCULADO 20.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3565, 2, '8.5.3.2.4.05.00.00.00.00', 'INFORMAÇÃO DA EXECUÇÃO DO CONSORCIO PARA CONSOLIDAÇÃO - DESPESA COM FUNDEB', 'REGISTRA A INFORMAÇÃO ENVIADA PELO CONSORCIO REFERENTE A APLICAÇÃO DAS TRANSFERENCIAS RECEBIDAS DO ENTE CONSORCIADO PARA PAGAMENTO DE DESPESA COM O FUNDEB. ESTA CONTA DEVERÁ SER ACUMULADA NOS BIMESTRES E TRAZER APENAS A APLICAÇÃO NO FUNDEB PARA FINS DE CONSOLIDAÇÃO DO MÍNIMO CONSTITUCIONAL, OU SEJA TRANSFERÊNCIAS AO CONSÓRCIO COM RECURSO VINCULADO 31.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3566, 2, '8.5.3.2.4.06.00.00.00.00', 'INFORMAÇÃO DA INSUFICIÊNCIA DE CAIXA DO CONSORCIO PUBLICO', 'REGISTRA A INFORMAÇÃO DA INSUFICIÊNCIA DE CAIXA DO CONSORCIO PÚBLICO  PARA FINS DE ANALISE DO CUMPRIMENTO DO ART. 42 DA LRF, CONFORME ART. 11, § 2º DA PORTARIA STN Nº 72/2012.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3567, 2, '8.5.3.2.4.07.00.00.00.00', 'OPERAÇÕES DE CREDITO IRREGULARES', 'REGISTRA AS OPERAÇÕES DE CREDITO NÃO AUTORIZADAS POR RESOLUÇÃO DO SENADO FEDERAL,  AS VEDADAS E OUTRAS IRREGULARMENTE REALIZADAS E QUE DEVERÃO SER CONSOLIDADAS NOS DEMONSTRATIVOS DE OPERAÇÃO DE CREDITO E DIVIDA CONSOLIDADA LIQUIDA DO ENTE CONSORCIADO EM ATENDIMENTO AO ART. 18, PARÁGRAFO ÚNICO DA PORTARIA STN N° 72/2012..', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3568, 2, '8.5.3.2.4.08.00.00.00.00', 'INFORMAÇÃO DO ATENDIMENTO AO ART. 25 DA LC 141/2012', 'REGISTRA A APLICAÇÃO DIRETA EEXECUTADA PELO CONSORCIO PUBLICO À CONTA DOS RECURSOS REFERENTES À DIFERENÇA DA APLICAÇÃO MÍNIMA EM ASPS QUE DEIXOU DE SER APLICADA EM EXERCICIOS ANTERIORES. ATENDIMENTO AO ART. 25 DA LEI COMPLEMENTAR 141/2012 E PARÁGRAFO ÚNICO DO ART. 10 DA PORTARIA STN Nº 72/2012. O CONSORCIO DEVERÁ EXECUTAR ESTA DESPESA NA MODALIDADE DE APLICAÇÃO 96.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3569, 2, '8.5.3.2.5.00.00.00.00.00', 'EMPENHOS A LIQUIDAR INSCRITOS EM RESTOS A PAGAR NAO PROCESSADOS', 'Demonstra o montante dos valores a liquidar inscritos em restos a pagar nao processados, executadas em consórcios públicos atribuídas ao ente consorciado com base no contrato de rateio', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3570, 2, '8.5.3.2.6.00.00.00.00.00', 'EMPENHOS EM LIQUIDAÇÃO INSCRITOS EM RESTOS A PAGAR NAO PROCESSADOS', 'Demonstra o montante dos valores em liquidação inscritos em restos a pagar nao processados, executadas em consórcios públicos atribuídas ao ente consorciado com base no contrato de rateio', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3571, 2, '8.5.3.2.7.00.00.00.00.00', 'EMPENHOS LIQUIDADOS INSCRITOS EM RESTOS A PAGAR PROCESSADOS', 'Demonstra o montante dos valores liquidados inscritos em restos a pagar processados, executadas em consórcios públicos atribuídas ao ente consorciado com base no contrato de rateio', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3572, 2, '8.5.3.3.1.00.00.00.00.00', 'RP NÃO PROCESSADOS A LIQUIDAR', 'Registra o valor dos RP não processados ainda não liquidados, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3573, 2, '8.5.3.3.2.00.00.00.00.00', 'RP NÃO PROCESSADOS EM LIQUIDAÇÃO', 'Registra o valor dos RP não processados ainda não liquidados cujo fato gerador já ocorreu em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3574, 2, '8.5.3.3.3.00.00.00.00.00', 'RP NÃO PROCESSADOS LIQUIDADOS A PAGAR', 'Registra o valor dos RP não processados, liquidados após sua inscrição e ainda não pagos em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3575, 2, '8.5.3.3.4.00.00.00.00.00', 'RP NÃO PROCESSADOS PAGOS', 'Registra o valor dos RP não processados, liquidados após sua inscrição e pagos em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3576, 2, '8.5.3.3.5.00.00.00.00.00', 'RP NÃO PROCESSADOS A LIQUIDAR BLOQUEADOS', 'Registra o valor de restos a pagar não processados a liquidar, bloqueado por decreto em consórcios públicos atribuídas ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3577, 2, '8.5.3.3.6.00.00.00.00.00', 'RP NÃO PROCESSADOS TRANSFERIDOS', 'Registra o valor da transferência de restos a pagar não processados pela desincorporação de saldos decorrentes de transformação, cisão, fusão e extinção de órgãos ou UG.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3578, 2, '8.5.3.3.7.00.00.00.00.00', 'RP NÃO PROCESSADOS - INSCRIÇÃO NO EXERCÍCIO', 'Registra o valor das despesas empenhadas inscritas no exercício como restos a pagar não processados, a ser transferido no exercício seguinte para a conta 631100000 e 631200000, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3579, 2, '8.5.3.4.1.00.00.00.00.00', 'RP PROCESSADOS A PAGAR', 'Registra o valor dos RP processados e não pagos em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3580, 2, '8.5.3.4.4.00.00.00.00.00', 'RP PROCESSADOS - INSCRIÇÃO NO EXERCÍCIO', 'Registra o valor das despesas empenhadas inscritas no exercício como restos a pagar processados, a ser transferido no exercício seguinte para a conta 632100000, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3581, 2, '8.5.3.4.5.00.00.00.00.00', 'RP PROCESSADOS CANCELADOS', 'Registra o cancelamento de restos a pagar processados por insuficiência de recursos, pela inscrição indevida ou para atender dispositivo legal, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3582, 2, '8.5.3.5.0.00.00.00.00.00', 'INSUFICIÊNCIA DE CAIXA DO CONSÓRCIO PÚBLICO', 'Registra a parcela total da insuficiência de caixa gerada no consórcio público atribuída ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3583, 2, '8.5.3.6.0.00.00.00.00.00', 'OPERAÇÃO DE CRÉDITO CONTRATADA PELO CONSÓRCIO PÚBLICO', 'Compreende a parcela da operação de crédito contratada pelo consórcio público atribuída ao ente consorciado com base no contrato de rateio.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3584, 2, '8.5.3.7.0.00.00.00.00.00', 'DÍVIDA CONSOLIDADA REFERENTE AO CONSÓRCIO PÚBLICO', 'Compreende o valor da dívida consolidada do consórcio público.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3585, 2, '8.9.1.1.1.00.00.00.00.00', 'DEPÓSITOS JUDICIAIS', 'Registra os depósitos judiciais efetuados por terceiros em títulos, em conta vinculada etc, sob a responsabilidade da unidade, ou depositados por esta para terceiros.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3586, 2, '8.9.1.1.2.00.00.00.00.00', 'DEPÓSITOS COMPULSÓRIOS', 'Registra o somatório dos valores dos depósitos compulsórios realizados.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3594, 2, '8.9.1.2.2.01.00.00.00.00', 'ATIVOS DE PPP CONTABILIZADOS NO ENTE', 'Registra o valor dos ativos oriundos de contratos de Parcerias Público Privadas-PPP. Tais bens devem estar reconhecidos no ativo imobilizado do ente (Poder Concedente), mas devem ser registrados em conta de controle a fim de obter a informação segregada dos ativos produzidos por meio de PPP.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3595, 2, '8.9.1.2.9.00.00.00.00.00', 'EXECUÇÃO DE OUTRAS RESPONSABILIDADES DE TERCEIROS', 'Registra responsabilidades com terceiros, não individualizadas nas outras contas desse grupo.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3596, 2, '8.9.2.1.0.00.00.00.00.00', 'PAGAMENTOS INDEVIDOS', 'Registra as responsabilidades em apuração de gestores e outros responsáveis e corresponsáveis por pagamentos efetuados indevidamente, inclusive em casos de superfaturamento.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3597, 2, '8.9.2.2.0.00.00.00.00.00', 'SALDOS NÃO RECOLHIDOS', 'Registra os saldos dos valores entregues a funcionários ou servidores, não recolhidos dentro dos prazos estabelecidos em normas legais ou administrativas cujos processos estejam em fase de apuração.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3598, 2, '8.9.2.3.0.00.00.00.00.00', 'DESFALQUES OU DESVIOS', 'Registra os desfalques ou desvios de bens e valores da União ou de entidades públicas, em apuração, decorrentes de processos por ações administrativas, policiais ou judiciais.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3599, 2, '8.9.2.4.0.00.00.00.00.00', 'DÉBITOS DE TERCEIROS EM PRESTAÇÃO DE SERVIÇOS', 'Registra os valores relativos as responsabilidades por irregularidades em prestação de serviços por terceiros, cujos processos estejam em fase de apuração.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3600, 2, '8.9.2.5.0.00.00.00.00.00', 'RESPONSÁVEIS POR DANOS OU PERDAS', 'Registra o registro das responsabilidades em inquéritos administrativos ou policiais contra servidores ou terceiros, por danos e perdas causados a fazenda nacional, e que estejam em fase de apuração.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3601, 2, '8.9.2.6.0.00.00.00.00.00', 'PAGAMENTOS SEM RESPALDO ORÇAMENTÁRIO', 'Registra as despesas realizadas sem existência de credito que as comporte, resultando em pagamento sem prévio empenho ou ainda com classificação imprópria (diferente daquela prevista no orçamento), em fase de apuração.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3602, 2, '8.9.2.7.0.00.00.00.00.00', 'CRÉDITOS ANTECIPADOS', 'Registra os valores pagos sobre dotações decorrentes de créditos antecipados não realizados até o fechamento do exercício, em fase de apuração.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3603, 2, '8.9.2.8.0.00.00.00.00.00', 'FALTA OU IRREGULARIDADE DE COMPROVAÇÃO', 'Registra os valores correspondentes à falta de documentação comprobatória da execução da despesa, inclusive as apresentações fora do prazo legal, bem como a inadimplência na comprovação de instrumentos de transferência ou ainda irregularidade na documentação, em fase de apuração.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3604, 2, '8.9.2.9.1.01.00.00.00.00', 'FALTA DE LICITAÇÃO', 'Registra os valores relativos a bens e serviços adquiridos sem a necessária licitação, infringindo a legislação especifica, em processos motivados, por determinações administrativas e ou inquéritos, em fase de apuração.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3605, 2, '8.9.2.9.1.02.00.00.00.00', 'FALTA DE RECOLHIMENTO DE OBRIGAÇÕES PECUNIÁRIAS', 'Registra os valores correspondentes a falta de recolhimento ou pagamento de obrigações pecuniárias, tais como: parte do empregador de obrigações patronais, valores descontados de terceiros e não recolhidos, não pagamento de obrigações para com a União.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3606, 2, '8.9.2.9.1.04.00.00.00.00', 'DÉBITOS ORIUNDOS DE USO OU ALUGUEL DE IMOVEL', 'Registra os débitos oriundos de uso ou aluguel de imóveis.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3607, 2, '8.9.2.9.1.05.00.00.00.00', 'ADIANTAMENTOS CONCEDIDOS IRREGULARMENTE', 'Registra os adiantamentos concedidos irregularmente.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3608, 2, '8.9.2.9.1.06.00.00.00.00', 'FRAUDES EM BENEFÍCIOS', 'Registra os valores em apuração de fraudes em benefícios.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3609, 2, '8.9.2.9.1.99.00.00.00.00', 'DEMAIS RESPONSABILIDADES EM APURAÇÃO', 'Registra responsabilidades em apuração não individualizadas nas outras contas desse grupo.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3610, 2, '1.1.2.9.3.02.00.00.00.00', '(-) PERDAS ESTIMADAS EM CLIENTE', 'Compreende o ajuste para perdas estimadas com o não recebimento das faturas/duplicatas a receber decorrentes de vendas de mercadorias ou da prestação de serviços.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3611, 2, '2.2.2.6.1.04.00.00.00.00', 'ENCARGOS - FINANCIAMENTOS EXTERNOS', 'Compreende os valores exigíveis decorrentes dos encargos de contratos de financiamentos externos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3612, 2, '8.9.3.1.1.00.00.00.00.00', 'AQUISIÇÃO FINANCIADA DE BENS E ARRENDAMENTO MERCANTIL FINANCEIRO', 'Registra os compromissos financeiros assumidos em razão de financiamentos, como  a aquisição financiada de bens, o arrendamento mercantil financeiro e outras operações assemelhadas nas quais a Administração Pública seja a contratante. Caso o ente tenha optado pelo registro de receita de capital no momento da aquisição, a operação não deve ser contabilizada nesta conta de controle, uma vez que integrará a linha de "Empréstimos" no demonstrativo.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3613, 2, '8.9.3.1.2.00.00.00.00.00', 'ANTECIPAÇÃO DE RECEITA PELA VENDA A TERMO DE BENS E SERVIÇOS', 'Registra as antecipações recebidas por operações de fornecimento de bens ou prestação de serviços e que ensejem a devolução da quantia recebida, caso a operação não ocorra, nos termos do art. 29, inciso III da LC 101/2000.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3614, 2, '8.9.3.1.3.00.00.00.00.00', 'ASSUNÇÃO, RECONHECIMENTO E CONFISSÃO DE DÍVIDAS (LRF, ART. 29, § 1º)', 'Registra as operações equiparadas a operações de crédito pelo art. 29 §1º da LC nº 101/2000', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3615, 2, '8.9.3.2.1.00.00.00.00.00', 'AQUISIÇÃO FINANCIADA DE BENS E ARRENDAMENTO MERCANTIL FINANCEIRO', 'Registra os compromissos financeiros assumidos em razão de financiamentos, como  a aquisição financiada de bens, o arrendamento mercantil financeiro e outras operações assemelhadas nas quais a Administração Pública seja a contratante. Caso o ente tenha optado pelo registro de receita de capital no momento da aquisição, a operação não deve ser contabilizada nesta conta de controle, uma vez que integrará a linha de "Empréstimos" no demonstrativo.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3616, 2, '8.9.3.2.2.00.00.00.00.00', 'ANTECIPAÇÃO DE RECEITA PELA VENDA A TERMO DE BENS E SERVIÇOS', 'Registra as antecipações recebidas por operações de fornecimento de bens ou prestação de serviços e que ensejem a devolução da quantia recebida, caso a operação não ocorra, nos termos do art. 29, inciso III da LC 101/2000.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3617, 2, '8.9.3.2.3.00.00.00.00.00', 'ASSUNÇÃO, RECONHECIMENTO E CONFISSÃO DE DÍVIDAS (LRF, ART. 29, § 1º)', 'Registra as operações equiparadas a operações de crédito pelo art. 29 §1º da LC nº 101/2000', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3618, 2, '8.9.3.3.1.01.00.00.00.00', 'TRIBUTOS', 'Registra os valores dos parcelamentos de tributos do ente no âmbito da SRF e PGFN.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3621, 2, '8.9.4.0.0.00.00.00.00.00', 'CONTROLE DE OBRIGAÇÕES SEM AUTORIZAÇÃO ORÇAMENTÁRIA PARA FINS DA LRF', 'Compreende as contas relacionadas  ao reconhecimento de obrigações patrimoniais realizadas sem autorização orçamentária, para fins de preenchimento do Relatório de Gestão Fiscal, previsto no art. 54 da LRF.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (3622, 2, '1.1.2.9.4.01.00.00.00.00', '(-) PERDAS ESTIMADAS EM CRÉDITOS TRIBUTÁRIOS A RECEBER', 'Compreende o ajuste para perdas estimadas com o não recebimento dos créditos tributários', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3623, 2, '1.1.2.9.0.00.00.00.00.00', '(-) AJUSTE DE PERDAS DE CRÉDITOS A CURTO PRAZO', 'Compreende o ajuste para perdas estimadas com o não recebimento de valores referentes a créditos a curto prazo, por inadimplência de terceiros e outras.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3624, 2, '1.1.2.9.1.00.00.00.00.00', '(-) AJUSTE DE PERDAS DE CRÉDITOS A CURTO PRAZO - CONSOLIDAÇÃO', 'Compreende o ajuste para perdas estimadas com o não recebimento de valores referentes a créditos a curto prazo, por inadimplência de terceiros e outras. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3625, 2, '1.1.2.9.1.01.00.00.00.00', '(-) PERDAS ESTIMADAS EM CRÉDITOS TRIBUTÁRIOS A RECEBER', 'Compreende o ajuste para perdas estimadas com o não recebimento dos créditos tributários', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3626, 2, '1.1.2.9.1.01.01.00.00.00', '(-) PERDAS ESTIMADAS EM IMPOSTOS', 'Compreende o ajuste para perdas estimadas com o não recebimento de impostos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3627, 2, '1.1.2.9.1.02.00.00.00.00', '(-) PERDAS ESTIMADAS EM CLIENTE', 'Compreende o ajuste para perdas estimadas com o não recebimento das faturas/duplicatas a receber decorrentes de vendas de mercadorias ou da prestação de serviços.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3628, 2, '1.1.2.9.1.03.00.00.00.00', '(-) PERDAS ESTIMADAS EM CRÉDITOS DE EMPRÉSTIMOS E FINANCIAMENTOS CONCEDIDOS', 'Compreende o ajuste para perdas estimadas com o não recebimento de créditos de empréstimos e financiamentos concedidos.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3629, 2, '1.1.2.9.1.04.00.00.00.00', '(-) PERDAS ESTIMADAS EM CRÉDITOS DE DÍVIDA ATIVA TRIBUTÁRIA', 'Compreende o ajuste para perdas estimadas com o não recebimento dos créditos inscritos em dívida ativa tributária.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3630, 2, '1.1.2.9.1.04.01.00.00.00', '(-) PERDAS ESTIMADAS EM CRÉDITOS DE DÍVIDA ATIVA TRIBUTÁRIA - IMPOSTOS', 'Compreende o ajuste para perdas estimadas com o não recebimento dos créditos inscritos em dívida ativa tributária decorrentes de impostos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3631, 2, '1.1.2.9.2.00.00.00.00.00', '(-) AJUSTE DE PERDAS DE CRÉDITOS A CURTO PRAZO - INTRA OFSS', 'Compreende o ajuste para perdas estimadas com o não recebimento de valores referentes a créditos a curto prazo, por inadimplência de terceiros e outras. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'N');
