insert into contabilidade.planocontadetalhe values (3823, 2, '2.1.7.6.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS DECORRENTES DE CONTRATOS DE PPP A CURTO PRAZO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos riscos de demanda, construção, disponibilidade ou outros riscos decorrentes de contratos de PPP, de acordo com a Lei n. 11.079/2004.', 2, false, 0, 'N');
