insert into contabilidade.planocontadetalhe values (3835, 2, '2.1.8.3.1.00.00.00.00.00', 'ARRENDAMENTO OPERACIONAL A PAGAR - CONSOLIDAÇÃO', 'Compreende as obrigações a curto prazo referentes a arrendamentos mercantis, nos quais não são transferidos ao arrendador os riscos e benefícios inerentes a propriedade, não havendo a possibilidade de opção de compra do bem arrendado. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3836, 2, '2.1.8.4.0.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A CURTO PRAZO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no curto prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3837, 2, '2.1.8.4.1.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A CURTO PRAZO - CONSOLIDAÇÃO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no curto prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3838, 2, '2.1.8.6.0.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto a referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3839, 2, '2.1.8.6.1.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP - CONSOLIDAÇÃO', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto a referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço. Compreende os valores de operações efetuadas entre uma unidade pertencente ao orçamento fiscal e da seguridade social (OFSS) com pessoa ou unidade que não pertença ao OFSS de ente público, alem dos demais fatos que não compreendem transações entre partes.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3840, 2, '2.1.8.7.0.00.00.00.00.00', 'DEPÓSITOS DE INSTITUIÇÕES AUTORIZADAS A OPERAR PELO BACEN', 'Compreende as disponibilidades mantidas no Bacen por diversas instituições, a exemplo das reservas bancárias e depósitos compulsórios. Conta de uso exclusivo da União.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3841, 2, '2.1.8.7.1.00.00.00.00.00', 'DEPÓSITOS DE INSTITUIÇÕES AUTORIZADAS A OPERAR PELO BACEN - CONSOLIDAÇÃO', 'Compreende as disponibilidades mantidas no Bacen por diversas instituições, a exemplo das reservas bancárias e depósitos compulsórios. Conta de uso exclusivo da União. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3842, 2, '2.1.8.8.0.00.00.00.00.00', 'VALORES RESTITUÍVEIS', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositaria, exigíveis no curto prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3843, 2, '2.1.8.8.1.00.00.00.00.00', 'VALORES RESTITUÍVEIS - CONSOLIDAÇÃO', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositaria, exigíveis no curto prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3844, 2, '2.1.8.8.1.01.00.00.00.00', 'CONSIGNAÇÕES ', 'Compreende os valores entregues em confiança ou em consignações, geralmente retidos em folha de pagamento de empregados ou servidores ou nos pagamentos referentes a compras de bens e serviços.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3845, 2, '2.1.8.8.1.03.00.00.00.00', 'DEPÓSITOS JUDICIAIS', 'Compreende os valores de obrigações, decorrentes de depósitos recebidos por determinação judicial e depósitos para recursos judiciais.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3846, 2, '2.1.8.8.1.04.00.00.00.00', 'DEPÓSITOS NÃO JUDICIAIS', 'Compreende os valores de obrigações, decorrentes de depósitos recebidos, exceto depósitos judiciais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3847, 2, '2.1.8.9.0.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A CURTO PRAZO', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no curto prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3848, 2, '2.1.8.9.1.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A CURTO PRAZO - CONSOLIDAÇÃO', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no curto prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3849, 2, '2.1.8.9.1.01.00.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', 'Compreende as obrigações relativas a indenizações e restituições.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3850, 2, '2.1.8.9.1.05.00.00.00.00', 'CONVÊNIOS A PAGAR', 'Compreende os valores a pagar retidos a título de convênio.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3895, 2, '2.2.2.2.0.00.00.00.00.00', 'EMPRÉSTIMOS A LONGO PRAZO - EXTERNO', 'Compreende os empréstimos contratuais ou mobiliários assumidos no exterior e transacionados, em regra, em moeda estrangeira, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3851, 2, '2.1.8.9.1.09.00.00.00.00', 'TERMOS DE PARCERIA A PAGAR', 'Compreende os valores a pagar retidos a título de termo de parceria, que é um instrumento jurídico de fomento e gestão das relações de parceria entre organizações do terceiro setor - qualificadas como organização da sociedade civil de interesse público - OSCIP, e o poder público, com objetivo de imprimir maior agilidade gerencial aos projetos, reduzir as excessivas exigências burocráticas e realizar controle dos resultados. O Termo de Parceria foi instituído e disciplinado pela Lei nº 9.790/99, de 23 de março de 1999 e Decreto nº 3.100/99.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3852, 2, '2.1.8.9.2.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A CURTO PRAZO - INTRA OFSS', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no curto prazo. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (3853, 2, '2.2.0.0.0.00.00.00.00.00', 'PASSIVO NÃO-CIRCULANTE', 'Compreende as obrigações conhecidas e estimadas que não atendam a nenhum dos critérios para serem classificadas no passivo circulante.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3854, 2, '2.2.1.0.0.00.00.00.00.00', 'OBRIGAÇÕES TRABALHISTAS, PREVIDENCIÁRIAS E ASSISTENCIAIS A PAGAR A LONGO PRAZO', 'Compreende as obrigações referentes a salários ou remunerações, bem como benefícios aos quais o empregado ou servidor tenha direito, aposentadorias, reformas, pensões e encargos a pagar, benefícios assistenciais, inclusive os precatórios decorrentes dessas obrigações, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3855, 2, '2.2.1.1.0.00.00.00.00.00', 'PESSOAL A PAGAR', 'Compreende as obrigações a longo prazo referentes a salários ou remunerações, bem como benefícios aos quais o empregado ou servidor tenha direito, quando pagos em data posterior a qual forem incorridos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3856, 2, '2.2.1.1.1.00.00.00.00.00', 'PESSOAL A PAGAR - CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo referentes a salários ou remunerações, bem como benefícios aos quais o empregado ou servidor tenha direito, quando pagos em data posterior a qual forem incorridos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3857, 2, '2.2.1.1.1.03.00.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ESPECIAL', 'Compreende as obrigações referentes a precatórios de salários ou remunerações, bem como a benefícios aos quais o empregado ou servidor tenha direito, sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3858, 2, '2.2.1.1.1.04.00.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ORDINÁRIO', 'Compreende as obrigações referentes a precatórios de proventos de aposentadoria, reformas ou pensões,  não sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3859, 2, '2.2.1.2.0.00.00.00.00.00', 'BENEFÍCIOS PREVIDENCIÁRIOS A PAGAR', 'Compreende as obrigações a longo prazo referentes a proventos de aposentadoria, reformas ou pensões aos quais o aposentado, reformado ou pensionista tenha direito, quando pagos em data posterior a qual forem incorridos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3860, 2, '2.2.1.2.1.00.00.00.00.00', 'BENEFÍCIOS PREVIDENCIÁRIOS A PAGAR - CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo referentes a proventos de aposentadoria, reformas ou pensões aos quais o aposentado, reformado ou pensionista tenha direito, quando pagos em data posterior a qual forem incorridos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3861, 2, '8.9.3.3.1.00.00.00.00.00', 'PARCELAMENTO DE DÍVIDAS', 'Compreende os montantes referentes ao parcelamento de dívidas.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3862, 2, '2.2.1.2.1.02.00.00.00.00', 'PRECATÓRIOS DE BENEFÍCIOS PREVIDENCIÁRIOS - REGIME ESPECIAL', 'Compreende as obrigações referentes a precatórios de proventos de aposentadoria, reformas ou pensões, sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'N');
