insert into contabilidade.planocontadetalhe values (3864, 2, '2.3.3.4.0.00.00.00.00.00', 'CORREÇÃO MONETÁRIA DO CAPITAL REALIZADO', 'Compreende o resultado da correção monetária do capital realizado, enquanto não capitalizado.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3865, 2, '2.2.1.3.0.00.00.00.00.00', 'BENEFÍCIOS ASSISTENCIAIS A PAGAR', 'Compreende as obrigações a longo prazo relativas aos benefícios assistenciais, quando pagos em data posterior a qual forem incorridos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3866, 2, '2.2.1.3.1.00.00.00.00.00', 'BENEFÍCIOS ASSISTENCIAIS A PAGAR - CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo relativas aos benefícios assistenciais, quando pagos em data posterior a qual forem incorridos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
