insert into contabilidade.planocontadetalhe values (3868, 2, '2.2.1.3.1.03.00.00.00.00', 'PRECATÓRIOS DE BENEFÍCIOS ASSISTENCIAIS - REGIME ORDINÁRIO', 'Compreende as obrigações referentes a precatórios de benefícios assistenciais,  não sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3869, 2, '2.2.1.4.0.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3870, 2, '2.2.1.4.1.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR - CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3871, 2, '2.2.1.4.2.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR - INTRA OFSS', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3872, 2, '2.2.1.4.3.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR - INTER OFSS - UNIÃO', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e a União.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3873, 2, '2.2.1.4.3.01.00.00.00.00', 'INSS A PAGAR', 'Compreende os valores relativos as contribuições a previdência social, incidentes sobre salários e remunerações pagos e sobre serviços de terceiros.', 2, false, 0, 'N');
