insert into contabilidade.planocontadetalhe values (3876, 2, '2.2.2.0.0.00.00.00.00.00', 'EMPRÉSTIMOS E FINANCIAMENTOS A LONGO PRAZO', 'Compreende as obrigações financeiras da entidade a titulo de empréstimos, bem como as aquisições efetuadas diretamente com o fornecedor, com vencimentos no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3877, 2, '2.2.2.1.0.00.00.00.00.00', 'EMPRÉSTIMOS A LONGO PRAZO - INTERNO', 'Compreende os empréstimos contratuais ou mobiliários assumidos dentro do país e transacionados, em regra, em moeda nacional, com vencimento no longo prazo.', 2, false, 0, 'N');
