insert into contabilidade.planocontadetalhe values (3879, 2, '2.2.2.1.1.01.00.00.00.00', 'EMPRÉSTIMOS INTERNOS - EM TÍTULOS', 'Compreende os valores das operações de créditos internas decorrentes da emissão de títulos.', 2, false, 0, 'N');
