insert into contabilidade.planocontadetalhe values (3943, 2, '2.2.7.0.0.00.00.00.00.00', 'PROVISÕES A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3944, 2, '2.2.7.1.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS TRABALHISTAS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a pagamento de reclamações trabalhistas, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3945, 2, '2.2.7.1.1.00.00.00.00.00', 'PROVISÃO PARA RISCOS TRABALHISTAS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a pagamento de reclamações trabalhistas, com probabilidade de ocorrerem no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3946, 2, '2.2.7.2.0.00.00.00.00.00', 'PROVISÕES MATEMÁTICAS PREVIDENCIÁRIAS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a futuros benefícios previdenciários a serem pagos aos contribuintes, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3987, 2, '2.2.8.8.1.03.00.00.00.00', 'DEPÓSITOS JUDICIAIS', 'Compreende os valores de obrigações, decorrentes de depósitos recebidos por determinação judicial e depósitos para recursos judiciais.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3947, 2, '2.2.7.2.1.00.00.00.00.00', 'PROVISÕES MATEMÁTICAS PREVIDENCIÁRIAS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a futuros benefícios previdenciários a serem pagos aos contribuintes, com probabilidade de ocorrerem no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3948, 2, '2.2.7.2.1.01.00.00.00.00', 'PLANO FINANCEIRO - PROVISÕES DE BENEFÍCIOS CONCEDIDOS   ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios já concedidos aos assistidos e beneficiários em gozo de beneficio de prestação continuada.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3949, 2, '2.2.7.2.1.02.00.00.00.00', 'PLANO FINANCEIRO - PROVISÕES DE BENEFÍCIOS A CONCEDER            ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios a conceder para a geração atual (servidores ativos).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3950, 2, '2.2.7.2.1.03.00.00.00.00', 'PLANO PREVIDENCIÁRIO - PROVISÕES DE BENEFICIOS CONCEDIDOS            ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios já concedidos aos assistidos e beneficiários em gozo de beneficio de prestação continuada.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3951, 2, '2.2.7.2.1.04.00.00.00.00', 'PLANO PREVIDENCIÁRIO - PROVISÕES DE BENEFÍCIOS A CONCEDER              ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios a conceder para a geração atual (servidores ativos).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3952, 2, '2.2.7.2.1.05.00.00.00.00', ' PLANO PREVIDENCIÁRIO - PLANO DE AMORTIZAÇÃO                            ', 'Compreende, de acordo com resultado da avaliação atuarial, o valor presente liquido dos direitos incorporados ao patrimônio do RPPS com base em legislação especifica, bem como registro tempestivo do plano de amortização implementado em lei do ente federativo, considerando que o ajuste contábil das reservas matemáticas somente ocorrera na apresentação de uma nova reavaliação atuarial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3953, 2, '2.2.7.2.1.06.00.00.00.00', ' PROVISÕES ATUARIAIS PARA AJUSTES DO PLANO FINANCEIRO      ', 'Compreende o somatório das provisões atuariais constituídas para ajustes do plano financeiro, conforme o resultado da avaliação atuarial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3954, 2, '2.2.7.2.1.07.00.00.00.00', ' PROVISÕES ATUARIAIS PARA AJUSTES DO PLANO PREVIDENCIÁRIO      ', 'Compreende o somatório das provisões atuariais constituídas para ajustes do plano previdenciário, conforme o resultado da avaliação atuarial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3955, 2, '2.2.7.3.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS FISCAIS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, relacionados ao pagamento de autuações fiscais, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3956, 2, '2.2.7.3.1.00.00.00.00.00', 'PROVISÃO PARA RISCOS FISCAIS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valor incertos, relacionados ao pagamento de autuações fiscais, com probabilidade de ocorrerem no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3957, 2, '2.2.7.3.1.01.00.00.00.00', 'PROVISÃO PARA PAGAMENTOS REFERENTES A AUTOS DE INFRAÇÃO', 'Compreende os valores das provisões para pagamento de autos de infração já lavrados e que se encontram na esfera administrativa.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3958, 2, '2.2.7.3.1.02.00.00.00.00', 'PROVISÃO PARA PAGAMENTOS DE AUTUAÇÕES FISCAIS - ESFERA JUDICIAL', 'Compreende os valores das provisões para pagamento de autuações fiscais que já se encontram na esfera judicial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3959, 2, '2.2.7.4.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS CÍVEIS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a pagamento de indenizações a fornecedores e clientes, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3960, 2, '2.3.3.3.0.00.00.00.00.00', 'ALIENAÇÃO DE BÔNUS DE SUBSCRIÇÃO', 'Compreende o produto da alienação de bônus de subscrição.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3961, 2, '2.2.7.5.0.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos com outros entes da federação, com probabilidade de ocorrerem no longo prazo. Na arrecadação, esta provisão será revertida em conta especifica de passivo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3962, 2, '2.2.7.5.3.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO - INTER OFSS - UNIÃO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos a outras entidades, conforme previsão legal. Na arrecadação, esta provisão será revertida em conta específica de passivo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3963, 2, '2.2.7.5.4.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO - INTER OFSS - ESTADO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos com outros entes da federação, com probabilidade de ocorrerem no longo prazo. Na arrecadação, esta provisão será revertida em conta especifica de passivo. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3964, 2, '2.2.7.5.5.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO - INTER OFSS - MUNICÍPIO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos com outros entes da federação, com probabilidade de ocorrerem no longo prazo. Na arrecadação, esta provisão será revertida em conta especifica de passivo. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3965, 2, '2.2.7.6.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS DECORRENTES DE CONTRATOS DE PPP A LONGO PRAZO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos riscos de demanda, construção, disponibilidade ou outros riscos decorrentes de contratos de PPP, de acordo com a Lei n.11.079/2004.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3966, 2, '2.2.7.6.1.00.00.00.00.00', 'PROVISÃO PARA RISCOS DECORRENTES DE CONTRATOS DE PPP A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos riscos de demanda, construção, disponibilidade ou outros riscos decorrentes de contratos de PPP, de acordo com a Lei n.11.079/2004. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3967, 2, '2.2.7.7.0.00.00.00.00.00', 'PROVISÃO PARA OBRIGAÇÕES DECORRENTES DA ATUAÇÃO GOVERNAMENTAL A LONGO PRAZO', 'Compreende os passivos de prazo ou de valores incertos relacionados a programas governamentais, como subsídios, auxílios, desonerações, assunção de obrigações ou outros.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3968, 2, '2.2.7.7.1.00.00.00.00.00', 'PROVISÃO PARA OBRIGAÇÕES DECORRENTES DA ATUAÇÃO GOVERNAMENTAL A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valores incertos relacionados a programas governamentais, como subsídios, auxílios, desonerações, assunção de obrigações ou outros. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3969, 2, '2.2.7.9.0.00.00.00.00.00', 'OUTRAS PROVISÕES A LONGO PRAZO', 'Compreende os demais passivos de prazo ou de valor incertos, com probabilidade de ocorrerem no longo prazo, não classificadas anteriormente neste plano de contas.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3970, 2, '2.2.7.9.1.00.00.00.00.00', 'OUTRAS PROVISÕES A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os demais passivos de prazo ou de valor incertos, com probabilidade de ocorrerem no longo prazo, não classificadas anteriormente neste plano de contas. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3971, 2, '2.2.7.9.1.03.00.00.00.00', 'PROVISÕES DO FCVS', 'Compreende, de acordo com a nota técnica atuarial, os valores atuariais dos passivos do fundo de compensação e variações salariais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3972, 2, '2.2.8.0.0.00.00.00.00.00', 'DEMAIS OBRIGAÇÕES A LONGO PRAZO', 'Compreende as obrigações da entidade junto a terceiros não inclusas nos subgrupos anteriores, inclusive os precatórios decorrentes dessas obrigações, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3973, 2, '2.2.8.1.0.00.00.00.00.00', 'ADIANTAMENTOS DE CLIENTES A LONGO PRAZO', 'Compreende as antecipações recebidas por operações de fornecimento de bens ou prestação de serviços e que ensejem a devolução da quantia recebida, caso a operação não ocorra.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3974, 2, '2.2.8.1.1.00.00.00.00.00', 'ADIANTAMENTOS DE CLIENTES A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende as antecipações recebidas por operações de fornecimento de bens ou prestação de serviços e que ensejem a devolução da quantia recebida, caso a operação não ocorra. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3976, 2, '2.2.8.2.1.00.00.00.00.00', 'OBRIGAÇÕES POR DANOS A TERCEIROS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os valores exigíveis no longo prazo, provenientes de danos a terceiros. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (3977, 2, '2.2.8.3.0.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A LONGO PRAZO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3978, 2, '2.2.8.3.1.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (3979, 2, '2.2.8.4.0.00.00.00.00.00', 'ADIANTAMENTO PARA FUTURO AUMENTO DE CAPITAL', 'Compreende os recursos recebidos pela entidade de seus acionistas ou quotistas destinados a serem utilizados para aumento de capital, quando haja a possibilidade de devolução destes recursos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3980, 2, '4.3.1.0.0.00.00.00.00.00', 'VENDA DE MERCADORIAS', 'Compreende as variações patrimoniais aumentativas auferidas com a venda de mercadorias, que resultem em aumento do patrimônio liquido, segregando-se a venda bruta das deduções como devoluções, abatimentos e descontos comerciais concedidos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3981, 2, '2.2.8.4.1.00.00.00.00.00', 'ADIANTAMENTO PARA FUTURO AUMENTO DE CAPITAL - CONSOLIDAÇÃO', 'Compreende os recursos recebidos pela entidade de seus acionistas ou quotistas destinados a serem utilizados para aumento de capital, quando haja a possibilidade de devolução destes recursos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3982, 2, '2.2.8.6.0.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP - LONGO PRAZO', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto à referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3983, 2, '2.2.8.6.1.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP - CONSOLIDAÇÃO', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto à referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço. Compreende os valores de operações efetuadas entre uma unidade pertencente ao orçamento fiscal e da seguridade social (OFSS) com pessoa ou unidade que não pertença ao OFSS de ente público, alem dos demais fatos que não compreendem transações entre partes.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3984, 2, '2.2.8.8.0.00.00.00.00.00', 'VALORES RESTITUÍVEIS', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositária, exigíveis no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3985, 2, '2.2.8.8.1.00.00.00.00.00', 'VALORES RESTITUÍVEIS - CONSOLIDAÇÃO', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositaria, exigíveis no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3986, 2, '2.2.8.8.1.01.00.00.00.00', 'CONSIGNAÇÕES ', 'Compreende os valores entregues em confiança ou em consignações, geralmente retidos em folha de pagamento de empregados ou servidores ou nos pagamentos referentes a compras de bens e serviços.', 2, false, 0, ' ');
insert into contabilidade.planocontadetalhe values (3988, 2, '2.2.8.8.1.04.00.00.00.00', 'DEPÓSITOS NÃO JUDICIAIS', 'Compreende os valores de obrigações, decorrentes de depósitos recebidos, exceto depósitos judiciais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3989, 2, '2.2.8.9.0.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A LONGO PRAZO', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3990, 2, '2.2.8.9.1.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (3991, 2, '2.2.9.0.0.00.00.00.00.00', 'RESULTADO DIFERIDO', 'Compreende o valor das variações patrimoniais aumentativas já recebidas que efetivamente devem ser reconhecidas em resultados em anos futuros e que não haja qualquer tipo de obrigação de devolução por parte da entidade.  Compreende também o saldo existente na antiga conta resultado de exercícios futuros em 31 de dezembro de 2008.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3992, 2, '2.2.9.1.0.00.00.00.00.00', 'VARIAÇÃO PATRIMONIAL AUMENTATIVA (VPA) DIFERIDA', 'Compreende o valor das variações patrimoniais aumentativas já recebidas que efetivamente devem ser reconhecidas em resultados em anos futuros e que não haja qualquer tipo de obrigação de devolução por parte da entidade.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (3993, 2, '2.2.9.1.1.00.00.00.00.00', 'VARIAÇÃO PATRIMONIAL AUMENTATIVA DIFERIDA - CONSOLIDAÇÃO', 'Compreende o valor das variações patrimoniais aumentativas já recebidas que efetivamente devem ser reconhecidas em resultados em anos futuros e que não haja qualquer tipo de obrigação de devolução por parte da entidade. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (3994, 2, '2.3.0.0.0.00.00.00.00.00', 'PATRIMÔNIO LÍQUIDO', 'Compreende o valor residual dos ativos depois de deduzidos todos os passivos.', 2, false, 0, 'N');
