insert into contabilidade.planocontadetalhe values (4246, 2, '4.4.5.0.0.00.00.00.00.00', 'REMUNERAÇÃO DE DEPÓSITOS BANCÁRIOS E APLICAÇÕES FINANCEIRAS', 'Compreende o valor total das variações patrimoniais aumentativas decorrentes da remuneração do saldo diário dos depósitos da União existentes no banco central, bem como aplicação de recursos da conta única de acordo com a rentabilidade media intrínseca dos títulos do tesouro.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4247, 2, '4.4.5.1.0.00.00.00.00.00', 'REMUNERAÇÃO DE DEPÓSITOS BANCÁRIOS', 'Compreende a variação patrimonial aumentativa decorrente da remuneração dos depósitos bancários do ente existentes no agente financeiro.', 2, false, 0, 'N');
