insert into contabilidade.planocontadetalhe values (4249, 2, '6.2.2.1.3.00.00.00.00.00', 'CREDITO UTILIZADO', 'Compreende o somatório dos valores referentes aos créditos orçamentários empenhados.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4250, 2, '4.4.5.2.0.00.00.00.00.00', 'REMUNERAÇÃO DE APLICAÇÕES FINANCEIRAS', 'Compreende a variação patrimonial aumentativa proveniente da remuneração de depósitos bancários, recursos oriundos de aplicações de entidades da administração pública no mercado financeiro, autorizadas por lei, em caderneta de poupança, fundo de investimento, contas remuneradas e outros.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4251, 2, '4.4.5.2.1.00.00.00.00.00', 'REMUNERAÇÃO DE APLICAÇÕES FINANCEIRAS - CONSOLIDAÇÃO', 'Compreende a variação patrimonial aumentativa proveniente da remuneração de depósitos bancários, recursos oriundos de aplicações de entidades da administração pública no mercado financeiro, autorizadas por lei, em caderneta de poupança, fundo de investimento, contas remuneradas e outros. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4254, 2, '4.4.8.1.1.00.00.00.00.00', 'RESULTADO POSITIVO DO BANCO CENTRAL - CONSOLIDAÇÃO', 'Compreende a incorporação do resultado positivo do Banco Central do Brasil, conforme art. 7º da Lei Complementar 101, de 4/5/2000. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4255, 2, '4.4.9.0.0.00.00.00.00.00', 'OUTRAS VARIAÇÕES PATRIMONIAIS AUMENTATIVAS - FINANCEIRAS', 'Compreende as variações patrimoniais aumentativas provenientes de operações financeiras não compreendidas nos subgrupos anteriores.', 2, false, 0, 'N');
