insert into contabilidade.planocontadetalhe values (4257, 2, '4.5.0.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS E DELEGAÇÕES RECEBIDAS', 'Compreende o somatório das variações patrimoniais aumentativas com transferências inter governamentais, transferências intragovernamentais, transferências de instituições multigovernamentais, transferências de instituições privadas com ou sem fins lucrativos, transferências de convênios, transferências do exterior e execuções orçamentárias delegadas.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4258, 2, '4.5.1.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS INTRAGOVERNAMENTAIS', 'Compreende as variações patrimoniais aumentativas decorrentes das transferências financeiras relativas à execução orçamentária, e de bens e valores, referentes às transações intragovernamentais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4259, 2, '4.5.5.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS DE CONSÓRCIOS PÚBLICOS', 'Compreende as variações patrimoniais aumentativas decorrentes das transferências de consórcios públicos, dos quais o ente recebedor participe.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4260, 2, '4.5.1.1.2.00.00.00.00.00', 'TRANSFERÊNCIAS RECEBIDAS PARA A EXECUÇÃO ORÇAMENTÁRIA - INTRA OFSS', 'Compreende o valor das transferências financeiras recebidas e correspondência de créditos em virtude da execução orçamentária (cota, repassse e sub-repasse). Não inclui o valor repassado para aportes no RPPS ou RGPS. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4261, 2, '4.5.1.2.0.00.00.00.00.00', 'TRANSFERÊNCIAS RECEBIDAS INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA', 'Compreende o valor das transferências, de bens ou valores, financeiras recebidas para restos a pagar e outras finalidades independentes da execução orçamentária.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4262, 2, '4.5.1.2.2.00.00.00.00.00', 'TRANSFERÊNCIAS RECEBIDAS INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA - INTRA OFSS', 'Compreende o valor das transferências financeiras, de bens ou valores, recebidas para restos a pagar e outras finalidades independentes da execução orçamentária. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'N');
