insert into contabilidade.planocontadetalhe values (4265, 2, '4.5.1.3.0.00.00.00.00.00', 'TRANSFERENCIAS RECEBIDAS PARA APORTES DE RECURSOS PARA O RPPS', 'Compreende o valor das transferências para aportes financeiros do ente para cobertura de insuficiências financeiras, formação de reserva, e cobertura de déficits financeiros ou atuariais do RPPS e outros aportes, exceto os decorrentes de alíquota de contribuição suplementar.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4266, 2, '4.5.1.3.2.00.00.00.00.00', 'TRANSFERENCIAS RECEBIDAS PARA APORTES DE RECURSOS PARA O RPPS - INTRA OFSS', 'Compreende o valor das transferências para aportes financeiros do ente para cobertura de insuficiências financeiras, formação de reserva, e cobertura de déficits financeiros ou atuariais do RPPS e outros aportes exceto os decorrentes de alíquota de contribuição suplementar. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4267, 2, '4.5.1.3.2.01.00.00.00.00', 'PLANO FINANCEIRO', 'Compreende o valor total dos valores recebidos pelo RPPS para cobertura de insuficiências financeiras, formação de reserva e outras necessidades financeiras, no âmbito do plano financeiro.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4268, 2, '4.5.1.3.2.02.00.00.00.00', 'PLANO PREVIDENCIÁRIO', 'Compreende o valor total recebido pelo RPPS para cobertura de déficit financeiro ou atuarial e outros aportes, no âmbito do plano previdenciário.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4269, 2, '4.5.1.4.0.00.00.00.00.00', 'TRANSFERÊNCIAS RECEBIDAS PARA APORTES DE RECURSOS PARA O RGPS', 'Compreende o valor das transferências recebidas para os aportes financeiros da União para a cobertura de déficits financeiros ou atuariais do RGPS.', 2, false, 0, 'N');
