insert into contabilidade.planocontadetalhe values (4289, 2, '4.5.2.4.4.00.00.00.00.00', 'OUTRAS TRANSFERÊNCIAS - INTER OFSS - ESTADO', 'Compreende as variações patrimoniais aumentativas decorrentes de demais transferências da união, estados, Distrito Federal, municípios, inclusive das entidades vinculadas, de bens e/ou valores. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (4274, 2, '4.5.2.1.1.00.00.00.00.00', 'TRANSFERÊNCIAS CONSTITUCIONAIS E LEGAIS DE RECEITAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais aumentativas decorrentes da transferência de outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS). Por exemplo, empresa independente de um ente recebendo transferência de outro ente (a empresa independente não pertence ao OFSS mas pertence ao conceito de governamental).', 2, false, 0, 'N');
